import React, { useEffect, useState, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../api/axiosInstance";
import { UserRemoveIcon, CogIcon, CalendarIcon, PencilIcon, CameraIcon } from "@heroicons/react/outline";

const BACKEND_BASE_URL = "http://localhost:5001";

function getToken() {
  const token = localStorage.getItem("token");
  return token;
}

const ProfilePage = () => {
  const { username } = useParams();
  const navigate = useNavigate();

  const [profileUser, setProfileUser] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);
  const [createdDebates, setCreatedDebates] = useState([]);
  const [createdBlogs, setCreatedBlogs] = useState([]);
  const [createdDiscussions, setCreatedDiscussions] = useState([]);
  const [profileError, setProfileError] = useState("");
  const [isFollowing, setIsFollowing] = useState(false);
  const [isFollower, setIsFollower] = useState(false);
  const [activeTab, setActiveTab] = useState("debate");
  const [showFollowers, setShowFollowers] = useState(false);
  const [showFollowing, setShowFollowing] = useState(false);
  const [showSettingsMenu, setShowSettingsMenu] = useState(false);
  const [showChangeBioModal, setShowChangeBioModal] = useState(false);
  const [showChangeProfilePictureModal, setShowChangeProfilePictureModal] = useState(false);
  const [newBio, setNewBio] = useState("");
  const [newProfilePicture, setNewProfilePicture] = useState(null);
  const [loading, setLoading] = useState(true);

  const token = getToken();

  const fetchCurrentUser = useCallback(async () => {
    if (!token) return null;
    const res = await API.get("/users/profile/me", { headers: { Authorization: `Bearer ${token}` } });
    return res.data;
  }, [token]);

  const fetchProfile = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}`, { headers: { Authorization: `Bearer ${token}` } });
    return res.data;
  }, [username, token]);

  const fetchCreatedDebates = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}/debates`, { headers: { Authorization: `Bearer ${token}` } });
    return res.data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  }, [username, token]);

  const fetchCreatedBlogs = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}/blogs`, { headers: { Authorization: `Bearer ${token}` } });
    return res.data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  }, [username, token]);

  const fetchCreatedDiscussions = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}/discussions`, { headers: { Authorization: `Bearer ${token}` } });
    return res.data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  }, [username, token]);

  const fetchAllData = useCallback(async () => {
    setLoading(true);
    setProfileUser(null);
    setCreatedDebates([]);
    setCreatedBlogs([]);
    setCreatedDiscussions([]);
    setProfileError("");

    try {
      const [currentUserData, profileData, debatesData, blogsData, discussionsData] = await Promise.all([
        fetchCurrentUser(),
        fetchProfile(),
        fetchCreatedDebates(),
        fetchCreatedBlogs(),
        fetchCreatedDiscussions(),
      ]);

      setCurrentUser(currentUserData);
      setProfileUser(profileData);
      setCreatedDebates(debatesData);
      setCreatedBlogs(blogsData);
      setCreatedDiscussions(discussionsData);

      setNewBio(profileData.bio || "");
      const followers = Array.isArray(profileData.followers) ? profileData.followers : [];
      const followings = Array.isArray(profileData.followings) ? profileData.followings : [];
      if (currentUserData) {
        setIsFollowing(followers.some((f) => String(f._id) === String(currentUserData._id)));
        setIsFollower(followings.some((f) => String(f._id) === String(currentUserData._id)));
      }
    } catch (err) {
      console.error("Error fetching data:", err);
      setProfileError(err.response?.data?.message || "Error fetching profile data");
    } finally {
      setLoading(false);
    }
  }, [fetchCurrentUser, fetchProfile, fetchCreatedDebates, fetchCreatedBlogs, fetchCreatedDiscussions]);

  useEffect(() => {
    fetchAllData();
  }, [username, fetchAllData]);

  const handleChangeBio = async () => {
    try {
      if (!token) throw new Error("No authentication token found");
      await API.put("/users/profile/update-bio", { bio: newBio }, { headers: { Authorization: `Bearer ${token}` } });
      setShowChangeBioModal(false);
      fetchAllData();
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error updating bio");
    }
  };

  const handleChangeProfilePicture = async () => {
    if (!newProfilePicture) {
      setProfileError("Please select a file to upload.");
      return;
    }
    const formData = new FormData();
    formData.append("profilePicture", newProfilePicture);
    try {
      await API.put("/users/profile/update-profile-picture", formData, { headers: { Authorization: `Bearer ${token}` } });
      setShowChangeProfilePictureModal(false);
      setNewProfilePicture(null);
      fetchAllData();
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error updating profile picture");
    }
  };

  const handleLogOut = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const handleFollow = async () => {
    if (!token) {
      alert("You must be logged in to follow or unfollow!");
      return navigate("/login");
    }
    try {
      if (isFollowing) {
        await API.post(`/users/profile/${username}/unfollow`, {}, { headers: { Authorization: `Bearer ${token}` } });
      } else {
        await API.post(`/users/profile/${username}/follow`, {}, { headers: { Authorization: `Bearer ${token}` } });
      }
      fetchAllData();
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error following/unfollowing");
    }
  };

  const handleChat = () => {
    if (!token) {
      alert("You must be logged in to chat!");
      return navigate("/login");
    }
    navigate(`/chat/${username}`);
  };

  const handleDebateClick = (debateId) => navigate(`/debatepage/${debateId}`);
  const handleDiscussionClick = (discussionId) => navigate(`/discussionpage/${discussionId}`);
  const handleBlogClick = (blogId) => navigate(`/blogpage/${blogId}`);
  const handleProfileClick = (username) => {
    setShowFollowers(false);
    setShowFollowing(false);
    navigate(`/profile/${username}`);
  };

  const isOwnProfile = currentUser && profileUser && currentUser._id === profileUser._id;
  const totalPosts = (createdDebates.length || 0) + (createdDiscussions.length || 0) + (createdBlogs.length || 0);
  const followButtonText = isFollowing ? "Unfollow" : isFollower ? "Follow Back" : "Follow";

  if (loading) {
    return (
      <div className="min-h-screen bg-white flex items-center justify-center">
        <span className="text-xl text-gray-800 animate-pulse">Loading profile...</span>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-white text-gray-800">
      {/* Error Message */}
      {profileError && (
        <div className="max-w-3xl mx-auto px-4 mt-4">
          <div className="bg-red-100 text-red-800 p-4 rounded-lg shadow-md">{profileError}</div>
        </div>
      )}

      {/* Header Section */}
      <header className="bg-white py-8">
        <div className="max-w-3xl mx-auto px-4">
          <div className="flex items-center justify-center flex-col md:flex-row">
            <div className="relative group mb-6 md:mb-0 md:mr-8">
              <img
                src={
                  profileUser?.profilePicture
                    ? `${BACKEND_BASE_URL}${profileUser.profilePicture}`
                    : `${BACKEND_BASE_URL}/images/default-avatar.png`
                }
                alt="profile"
                className="w-32 h-32 rounded-full object-cover border-4 border-transparent bg-gradient-to-r from-blue-400 to-purple-500 p-1 group-hover:scale-105 transition-transform duration-300 shadow-lg"
                onError={(e) => (e.target.src = `${BACKEND_BASE_URL}/images/default-avatar.png`)}
              />
              {isOwnProfile && (
                <button
                  onClick={() => setShowChangeProfilePictureModal(true)}
                  className="absolute bottom-0 right-0 p-2 bg-blue-500 text-white rounded-full hover:bg-blue-600 transition-colors duration-200 shadow-md"
                >
                  <CameraIcon className="h-5 w-5" />
                </button>
              )}
            </div>
            <div className="text-center md:text-left">
              <div className="flex items-center justify-center md:justify-start mb-2">
                <h1 className="text-3xl font-bold text-gray-900">{profileUser?.username || "Loading..."}</h1>
                {isOwnProfile ? (
                  <button
                    onClick={() => setShowSettingsMenu(!showSettingsMenu)}
                    className="ml-4 p-2 bg-gray-200 rounded-full hover:bg-gray-300 transition-colors duration-200"
                  >
                    <CogIcon className="h-5 w-5 text-gray-700" />
                  </button>
                ) : (
                  <div className="ml-4 flex space-x-2">
                    <button
                      onClick={handleFollow}
                      className={`px-4 py-2 rounded-full text-white font-medium ${
                        isFollowing ? "bg-gray-500 hover:bg-gray-600" : "bg-blue-500 hover:bg-blue-600"
                      } transition-colors duration-200 shadow-md`}
                    >
                      {followButtonText}
                    </button>
                    <button
                      onClick={handleChat}
                      className="px-4 py-2 rounded-full bg-green-500 text-white font-medium hover:bg-green-600 transition-colors duration-200 shadow-md"
                    >
                      Message
                    </button>
                  </div>
                )}
              </div>
              {showSettingsMenu && isOwnProfile && (
                <div className="absolute mt-2 w-48 bg-white rounded-lg shadow-xl z-10 border border-gray-200">
                  <ul className="py-2">
                    <li
                      className="px-4 py-2 hover:bg-gray-100 cursor-pointer flex items-center"
                      onClick={() => {
                        setShowChangeBioModal(true);
                        setShowSettingsMenu(false);
                      }}
                    >
                      <PencilIcon className="h-4 w-4 mr-2" />
                      Change Bio
                    </li>
                    <li
                      className="px-4 py-2 hover:bg-gray-100 cursor-pointer flex items-center"
                      onClick={handleLogOut}
                    >
                      <UserRemoveIcon className="h-4 w-4 mr-2" />
                      Log Out
                    </li>
                  </ul>
                </div>
              )}
              <div className="flex justify-center md:justify-start space-x-8 mb-4">
                <div className="flex items-center group cursor-pointer hover:text-blue-500 transition-colors duration-200">
                  <span className="font-semibold text-lg group-hover:scale-110 transition-transform duration-200">
                    {totalPosts}
                  </span>
                  <span className="text-gray-600 ml-1">Posts</span>
                </div>
                <div
                  onClick={() => setShowFollowers(true)}
                  className="flex items-center group cursor-pointer hover:text-blue-500 transition-colors duration-200"
                >
                  <span className="font-semibold text-lg group-hover:scale-110 transition-transform duration-200">
                    {profileUser?.followers?.length || 0}
                  </span>
                  <span className="text-gray-600 ml-1">Followers</span>
                </div>
                <div
                  onClick={() => setShowFollowing(true)}
                  className="flex items-center group cursor-pointer hover:text-blue-500 transition-colors duration-200"
                >
                  <span className="font-semibold text-lg group-hover:scale-110 transition-transform duration-200">
                    {profileUser?.followings?.length || 0}
                  </span>
                  <span className="text-gray-600 ml-1">Following</span>
                </div>
              </div>
              <p className="text-gray-700 italic">{profileUser?.bio || "No bio yet."}</p>
              <div className="flex items-center justify-center md:justify-start text-gray-500 text-sm mt-2">
                <CalendarIcon className="h-4 w-4 mr-1" />
                Joined: {profileUser?.createdAt ? new Date(profileUser.createdAt).toLocaleDateString() : "Loading..."}
              </div>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="max-w-3xl mx-auto px-4 py-6">
        {/* Tabs */}
        <div className="relative flex justify-center space-x-8 border-b border-gray-200 pb-2">
          {["debate", "discussion", "blogs"].map((tab) => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`relative px-4 py-2 text-sm font-medium uppercase ${
                activeTab === tab ? "text-blue-500" : "text-gray-500 hover:text-gray-700"
              } transition-colors duration-200`}
            >
              {tab}
              {activeTab === tab && (
                <span className="absolute bottom-0 left-0 w-full h-0.5 bg-blue-500 transform scale-x-100 transition-transform duration-300 origin-center"></span>
              )}
            </button>
          ))}
        </div>

        {/* Posts Grid */}
        <div className="mt-6 grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {activeTab === "debate" && (
            createdDebates.length > 0 ? (
              createdDebates.map((deb) => (
                <div
                  key={deb._id}
                  className="bg-gray-50 rounded-xl p-4 cursor-pointer hover:shadow-lg hover:scale-105 transition-all duration-300 shadow-md"
                  onClick={() => handleDebateClick(deb._id)}
                >
                  <h3 className="text-sm font-semibold text-gray-900 truncate">{deb.title || "Untitled Debate"}</h3>
                  <p className="text-gray-600 text-xs mt-1 line-clamp-2">
                    {deb.openingArgument || "No opening argument"}
                  </p>
                </div>
              ))
            ) : (
              <p className="col-span-3 text-center text-gray-500">No debates created yet.</p>
            )
          )}
          {activeTab === "discussion" && (
            createdDiscussions.length > 0 ? (
              createdDiscussions.map((disc) => (
                <div
                  key={disc._id}
                  className="bg-gray-50 rounded-xl p-4 cursor-pointer hover:shadow-lg hover:scale-105 transition-all duration-300 shadow-md"
                  onClick={() => handleDiscussionClick(disc._id)}
                >
                  <h3 className="text-sm font-semibold text-gray-900 truncate">{disc.title || "Untitled Discussion"}</h3>
                  <p className="text-gray-600 text-xs mt-1 line-clamp-2">{disc.description || "No description"}</p>
                </div>
              ))
            ) : (
              <p className="col-span-3 text-center text-gray-500">No discussions created yet.</p>
            )
          )}
          {activeTab === "blogs" && (
            createdBlogs.length > 0 ? (
              createdBlogs.map((blog) => (
                <div
                  key={blog._id}
                  className="bg-gray-50 rounded-xl p-4 cursor-pointer hover:shadow-lg hover:scale-105 transition-all duration-300 shadow-md"
                  onClick={() => handleBlogClick(blog._id)}
                >
                  <h3 className="text-sm font-semibold text-gray-900 truncate">{blog.title || "Untitled Blog"}</h3>
                  <p className="text-gray-600 text-xs mt-1 line-clamp-2">{blog.content || "No content"}</p>
                  {blog.fileUrl && blog.fileUrl.match(/\.(jpeg|jpg|png)$/i) && (
                    <img
                      src={`${BACKEND_BASE_URL}${blog.fileUrl}`}
                      alt="Blog media"
                      className="w-full h-32 object-cover rounded-lg mt-2 hover:opacity-90 transition-opacity duration-200"
                      onError={(e) => (e.target.src = `${BACKEND_BASE_URL}/images/default-image.png`)}
                    />
                  )}
                </div>
              ))
            ) : (
              <p className="col-span-3 text-center text-gray-500">No blogs yet.</p>
            )
          )}
        </div>
      </div>

      {/* Followers Modal */}
      {showFollowers && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-xl shadow-2xl max-w-md w-full max-h-[80vh] overflow-y-auto animate-fade-in">
            <h2 className="text-xl font-semibold text-gray-900 mb-4 text-center">Followers</h2>
            {profileUser?.followers?.length > 0 ? (
              <ul className="space-y-3">
                {profileUser.followers.map((follower) => (
                  <li
                    key={follower._id}
                    className="flex items-center space-x-3 p-2 bg-gray-50 rounded-lg cursor-pointer hover:bg-gray-100 transition-colors duration-200"
                    onClick={() => handleProfileClick(follower.username)}
                  >
                    <img
                      src={
                        follower.profilePicture
                          ? `${BACKEND_BASE_URL}${follower.profilePicture}`
                          : `${BACKEND_BASE_URL}/images/default-avatar.png`
                      }
                      alt={follower.username}
                      className="w-10 h-10 rounded-full border-2 border-gray-300"
                    />
                    <span className="text-sm font-medium text-gray-900">{follower.username}</span>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-center text-gray-500">No followers yet.</p>
            )}
            <button
              onClick={() => setShowFollowers(false)}
              className="mt-4 w-full bg-gray-200 text-gray-800 px-4 py-2 rounded-lg hover:bg-gray-300 transition-colors duration-200"
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Following Modal */}
      {showFollowing && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-xl shadow-2xl max-w-md w-full max-h-[80vh] overflow-y-auto animate-fade-in">
            <h2 className="text-xl font-semibold text-gray-900 mb-4 text-center">Following</h2>
            {profileUser?.followings?.length > 0 ? (
              <ul className="space-y-3">
                {profileUser.followings.map((following) => (
                  <li
                    key={following._id}
                    className="flex items-center space-x-3 p-2 bg-gray-50 rounded-lg cursor-pointer hover:bg-gray-100 transition-colors duration-200"
                    onClick={() => handleProfileClick(following.username)}
                  >
                    <img
                      src={
                        following.profilePicture
                          ? `${BACKEND_BASE_URL}${following.profilePicture}`
                          : `${BACKEND_BASE_URL}/images/default-avatar.png`
                      }
                      alt={following.username}
                      className="w-10 h-10 rounded-full border-2 border-gray-300"
                    />
                    <span className="text-sm font-medium text-gray-900">{following.username}</span>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-center text-gray-500">Not following anyone yet.</p>
            )}
            <button
              onClick={() => setShowFollowing(false)}
              className="mt-4 w-full bg-gray-200 text-gray-800 px-4 py-2 rounded-lg hover:bg-gray-300 transition-colors duration-200"
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Change Bio Modal */}
      {showChangeBioModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-xl shadow-2xl max-w-md w-full animate-fade-in">
            <h2 className="text-xl font-semibold text-gray-900 mb-4 text-center">Change Bio</h2>
            <textarea
              value={newBio}
              onChange={(e) => setNewBio(e.target.value)}
              className="w-full p-3 bg-gray-50 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-200"
              rows="4"
              placeholder="Enter your new bio..."
            />
            <div className="flex space-x-3 mt-4">
              <button
                onClick={handleChangeBio}
                className="flex-1 bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors duration-200 shadow-md"
              >
                Save
              </button>
              <button
                onClick={() => setShowChangeBioModal(false)}
                className="flex-1 bg-gray-200 text-gray-800 px-4 py-2 rounded-lg hover:bg-gray-300 transition-colors duration-200"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Change Profile Picture Modal */}
      {showChangeProfilePictureModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-xl shadow-2xl max-w-md w-full animate-fade-in">
            <h2 className="text-xl font-semibold text-gray-900 mb-4 text-center">Change Profile Picture</h2>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => setNewProfilePicture(e.target.files[0])}
              className="w-full p-2 bg-gray-50 rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <div className="flex space-x-3 mt-4">
              <button
                onClick={handleChangeProfilePicture}
                className="flex-1 bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-colors duration-200 shadow-md"
              >
                Upload
              </button>
              <button
                onClick={() => setShowChangeProfilePictureModal(false)}
                className="flex-1 bg-gray-200 text-gray-800 px-4 py-2 rounded-lg hover:bg-gray-300 transition-colors duration-200"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProfilePage;
