import React, { useState, useEffect, useCallback } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance";
import { LockClosedIcon, ChatIcon } from "@heroicons/react/solid";

// Compact Discussion Card Component
const DiscussionCard = ({ discussion }) => (
  <motion.div
    whileHover={{ scale: 1.03 }}
    transition={{ duration: 0.2 }}
    className="relative group bg-white rounded-xl shadow-lg overflow-hidden transition-all duration-300 hover:shadow-xl h-48"
