          See More {section.charAt(0).toUpperCase() + section.slice(1)}
        </h3>
      </div>
    </Link>
  </motion.div>
);

const Home = () => {
  const location = useLocation();
  const navigate = useNavigate();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    setActiveSection(params.get("section") || "discussions");
  }, [location.search]);

  const [activeSection, setActiveSection] = useState("discussions");
  const [discussions, setDiscussions] = useState([]);
  const [debates, setDebates] = useState([]);
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [disRes, debRes, blogRes] = await Promise.all([
          API.get("/discussions"),
          API.get("/debates"),
          API.get("/blogs"),
        ]);
        setDiscussions(Array.isArray(disRes.data) ? disRes.data : []);
        setDebates(Array.isArray(debRes.data) ? debRes.data : []);
        setBlogs(Array.isArray(blogRes.data) ? blogRes.data : []);
      } catch (err) {
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const sortByPopularity = useCallback((items) => {
    return [...items].sort((a, b) => {
      const popA = (a.comments?.length || 0) + (a.upvotes?.length || 0) + (a.downvotes?.length || 0);
      const popB = (b.comments?.length || 0) + (b.upvotes?.length || 0) + (b.downvotes?.length || 0);
      return popB - popA;
    });
  }, []);

  const renderSectionCards = (items, renderCard, section) => {
    const sorted = sortByPopularity(items);
    const cards = sorted.slice(0, 8).map((item, index) => (
      <motion.div
        key={item._id}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: index * 0.1 }}
      >
        {renderCard(item)}
      </motion.div>
    ));
    cards.push(
      <motion.div
        key="see-more"
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.8 }}
      >
        <SeeMoreCard section={section} />
      </motion.div>
    );
    return cards;
  };

  const renderSectionToggle = () => (
    <div className="flex flex-col md:flex-row gap-4 mb-6 md:ml-64 justify-center">
      {["discussions", "debates", "blogs"].map((sec) => (
        <button
          key={sec}
          onClick={() => {
            setActiveSection(sec);
            navigate(`/?section=${sec}`);
          }}
          className={`px-4 py-2 rounded-lg transition-all duration-300 text-sm md:text-base ${
            activeSection === sec ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-800 hover:bg-gray-300"
          }`}
        >
          {sec.charAt(0).toUpperCase() + sec.slice(1)}
        </button>
      ))}
    </div>
  );

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100">
      {/* Hero Section with Attractive Card */}
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="bg-gradient-to-r from-blue-600 to-purple-600 text-white py-8 sm:py-10 md:py-12 lg:py-16 px-4 sm:px-6 md:px-8 lg:px-10 text-center shadow-lg relative md:ml-64"
      >
        <motion.div
          animate={{ scale: [1, 1.05, 1], opacity: [0.8, 1, 0.8] }}
          transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
          className="inline-block"
        >
          <ChatIcon className="h-10 w-10 sm:h-12 sm:w-12 md:h-14 md:w-14 lg:h-16 lg:w-16 mx-auto mb-4 text-yellow-300" />
        </motion.div>
        <h1 className="text-3xl sm:text-4xl md:text-5xl lg:text-6xl font-extrabold tracking-tight mb-3 sm:mb-4">
          <span className="text-white">Welcome to </span>
          <span className="text-yellow-300">Debatify!</span>
        </h1>
        <p className="text-sm sm:text-base md:text-lg lg:text-xl max-w-sm sm:max-w-md md:max-w-lg lg:max-w-2xl mx-auto opacity-90 leading-relaxed">
          Join the conversation, share your opinions, and engage in meaningful discussions/debates on topics that matter to you.
        </p>
        <div className="mt-3 sm:mt-4 md:mt-5 lg:mt-6 flex justify-center gap-2 sm:gap-3 md:gap-4">
          <Link
            to="/createDiscussion"
            className="inline-block px-5 py-2 sm:px-6 sm:py-2 md:px-7 md:py-3 lg:px-8 lg:py-3 bg-yellow-400 text-blue-900 font-semibold rounded-full shadow-lg hover:bg-yellow-500 transition-all duration-300 transform hover:scale-105 text-sm sm:text-base md:text-lg"
          >
            Start a Discussion Now
          </Link>
        </div>
      </motion.div>

      {/* Main Content */}
      <div className="px-2 sm:px-4 md:px-8 pt-6 sm:pt-8 md:pt-10">
        {renderSectionToggle()}

        {loading ? (
          <div className="text-center md:ml-64">
            <motion.div
              animate={{ rotate: 360 }}
              transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
              className="inline-block h-6 w-6 sm:h-7 sm:w-7 md:h-8 md:w-8 border-4 border-blue-500 border-t-transparent rounded-full"
            />
            <p className="text-gray-600 mt-2 text-sm sm:text-base md:text-base">Loading...</p>
          </div>
        ) : (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
            className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 md:ml-64 max-w-6xl mx-auto"
          >
            {activeSection === "discussions" &&
              renderSectionCards(discussions, (d) => <DiscussionCard key={d._id} discussion={d} />, "discussions")}
            {activeSection === "debates" &&
              renderSectionCards(debates, (d) => <DebateCard key={d._id} debate={d} />, "debates")}
            {activeSection === "blogs" &&
              renderSectionCards(blogs, (b) => <BlogCard key={b._id} blog={b} />, "blogs")}
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default Home;
