import React, { useEffect, useState, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance";
import { PencilIcon, ArrowLeftIcon, ArrowRightIcon, SearchIcon, LockClosedIcon } from "@heroicons/react/outline";

const Blog = () => {
  const navigate = useNavigate();
  const [blogs, setBlogs] = useState([]);
  const [filteredBlogs, setFilteredBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sortBy, setSortBy] = useState("latest");
  const [currentPage, setCurrentPage] = useState(0);
  const [searchQuery, setSearchQuery] = useState("");
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const blogsPerPage = 9;

  // Initial fetch effect
  useEffect(() => {
    const fetchDiscussions = async () => {
      try {
        setLoading(true);
        const { data } = await API.get("/discussions");
        setDiscussions(Array.isArray(data) ? data : []);
      } catch (error) {
        console.error("Error fetching discussions:", error);
        setDiscussions([]);
      } finally {
        setLoading(false);
      }
    };
    fetchDiscussions();
  }, []);

   // Polling effect for new discussions
  useEffect(() => {
    const intervalId = setInterval(async () => {
      try {
        const { data } = await API.get("/discussions");
        const newDiscussions = Array.isArray(data) ? data : [];
        const existingDiscussionIds = new Set(discussions.map((d) => d._id));
        const discussionsToAdd = newDiscussions.filter((d) => !existingDiscussionIds.has(d._id));

        if (discussionsToAdd.length > 0) {
          setDiscussions((prevDiscussions) => [...prevDiscussions, ...discussionsToAdd]);
        }
      } catch (error) {
        console.error("Error polling discussions:", error);
      }
    }, 1000); // Poll every 1 seconds

    return () => clearInterval(intervalId);
  }, [discussions]); // Depend on discussions to compare existing IDs

  useEffect(() => {
    if (searchQuery.trim() === "") {
      setFilteredBlogs(blogs);
      setIsDropdownOpen(false);
      setCurrentPage(0);
      return;
    }

    const lowerQuery = searchQuery.toLowerCase();
    const results = blogs.filter((blog) => {
      const title = blog.title || "";
      const content = blog.content || "";
      const username = blog.author?.username || "";
      return (
        title.toLowerCase().includes(lowerQuery) ||
        content.toLowerCase().includes(lowerQuery) ||
        username.toLowerCase().includes(lowerQuery)
      );
    });

    setFilteredBlogs(results);
    setIsDropdownOpen(results.length > 0);
    setCurrentPage(0);
  }, [searchQuery, blogs]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const sortedBlogs = [...filteredBlogs].sort((a, b) => {
    if (sortBy === "latest") return new Date(b.createdAt) - new Date(a.createdAt);
    if (sortBy === "oldest") return new Date(a.createdAt) - new Date(b.createdAt);
    if (sortBy === "popularity") {
      const popularityA = (a.upvotes || 0) + (a.downvotes || 0);
      const popularityB = (b.upvotes || 0) + (b.downvotes || 0);
      return popularityB - popularityA;
    }
    return 0;
  });

  const totalPages = Math.ceil(sortedBlogs.length / blogsPerPage);
  const paginatedBlogs = sortedBlogs.slice(currentPage * blogsPerPage, (currentPage + 1) * blogsPerPage);

  const handlePrevPage = () => {
    if (currentPage > 0) setCurrentPage(currentPage - 1);
  };

  const handleNextPage = () => {
    if (currentPage < totalPages - 1) setCurrentPage(currentPage + 1);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (filteredBlogs.length > 0) {
      navigate(`/blogs/${filteredBlogs[0]._id}`);
      setSearchQuery("");
      setIsDropdownOpen(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 via-blue-100 to-green-100 ml-64">
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="bg-gradient-to-r from-blue-600 to-green-600 text-white py-16 px-5 text-center shadow-lg relative"
      >
        <div className="absolute top-4 right-5" ref={dropdownRef}>
          <form onSubmit={handleSearchSubmit} className="flex items-center">
            <motion.div whileHover={{ scale: 1.02 }} transition={{ duration: 0.3 }}>
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="Search blogs..."
                className="px-4 py-2 pr-10 w-64 bg-white/20 backdrop-blur-md text-white placeholder-gray-200 rounded-xl border border-white/30 focus:outline-none focus:ring-2 focus:ring-yellow-300 transition-all duration-300"
              />
              <button type="submit" className="absolute right-3 top-1/2 transform -translate-y-1/2 text-yellow-300 hover:text-yellow-200">
                <SearchIcon className="h-5 w-5" />
              </button>
            </motion.div>
          </form>
          {isDropdownOpen && (
            <motion.div
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
              className="absolute top-14 right-0 w-64 bg-white rounded-xl shadow-lg max-h-80 overflow-y-auto z-40"
            >
              {filteredBlogs.map((blog) => (
                <Link
                  key={blog._id}
                  to={`/blogs/${blog._id}`}
                  onClick={() => {
                    setSearchQuery("");
                    setIsDropdownOpen(false);
                  }}
                  className="block px-4 py-3 border-b border-gray-200 hover:bg-gradient-to-r hover:from-blue-500/20 hover:to-green-500/20 transition-colors duration-200"
                >
                  <h3 className="text-gray-800 font-semibold">{blog.title || "Untitled Blog"}</h3>
                  <p className="text-gray-600 text-sm truncate">
                    {blog.content ? `${blog.content.substring(0, 50)}...` : "No content available"}
                  </p>
                  <div className="text-sm mt-1 text-gray-500">
                    By: {blog.author?.username || "Unknown"} {blog.isPrivate && <LockClosedIcon className="h-4 w-4 inline text-red-500" />}
                  </div>
                </Link>
              ))}
            </motion.div>
          )}
        </div>
        <motion.div animate={{ scale: [1, 1.05, 1], opacity: [0.8, 1, 0.8] }} transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }} className="inline-block">
          <PencilIcon className="h-16 w-16 mx-auto mb-4 text-yellow-300" />
        </motion.div>
        <h1 className="text-5xl md:text-6xl font-extrabold tracking-tight mb-4">
          <span className="text-yellow-300">Blog</span> Hub
        </h1>
        <p className="text-lg md:text-xl max-w-2xl mx-auto opacity-90">
          Share your thoughts, inspire others, and explore a world of ideas through engaging blogs.
        </p>
        <div className="mt-6 space-x-4">
          <Link
            to="/createblog"
            className="inline-block px-8 py-3 bg-yellow-400 text-blue-900 font-semibold rounded-full shadow-lg hover:bg-yellow-500 transition-all duration-300 transform hover:scale-105"
          >
            Write a New Blog
          </Link>

        </div>
      </motion.div>
      <div className="p-5 md:p-10 max-w-6xl mx-auto">
        <motion.h2
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="text-4xl font-bold text-gray-800 mb-8 text-center"
        >
          Latest Blogs
        </motion.h2>
        <div className="flex flex-wrap justify-between items-center mb-8">
          <div className="space-x-4">
            <button
              onClick={() => setSortBy("latest")}
              className={`px-4 py-2 rounded-lg transition-all duration-300 ${
                sortBy === "latest" ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-800 hover:bg-gray-300"
              }`}
            >
              Latest
            </button>
            <button
              onClick={() => setSortBy("oldest")}
              className={`px-4 py-2 rounded-lg transition-all duration-300 ${
                sortBy === "oldest" ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-800 hover:bg-gray-300"
              }`}
            >
              Oldest
            </button>
            <button
              onClick={() => setSortBy("popularity")}
              className={`px-4 py-2 rounded-lg transition-all duration-300 ${
                sortBy === "popularity" ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-800 hover:bg-gray-300"
              }`}
            >
              Popularity
            </button>
          </div>
          <div className="flex space-x-4 items-center mt-4 sm:mt-0">
            <button
              onClick={handlePrevPage}
              disabled={currentPage === 0}
              className="px-3 py-1 rounded-full bg-gray-200 text-gray-800 hover:bg-gray-300 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <ArrowLeftIcon className="h-5 w-5" />
            </button>
            <span className="text-gray-700">Page {currentPage + 1} of {totalPages}</span>
            <button
              onClick={handleNextPage}
              disabled={currentPage === totalPages - 1}
              className="px-3 py-1 rounded-full bg-gray-200 text-gray-800 hover:bg-gray-300 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <ArrowRightIcon className="h-5 w-5" />
            </button>
          </div>
        </div>
        {loading ? (
          <div className="text-center">
            <motion.div
              animate={{ rotate: 360 }}
              transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
              className="inline-block h-8 w-8 border-4 border-blue-500 border-t-transparent rounded-full"
            />
            <p className="text-gray-600 mt-2">Loading blogs...</p>
          </div>
        ) : sortedBlogs.length === 0 ? (
          <motion.p initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5 }} className="text-center text-gray-500 text-lg">
            No blogs yet. Write one now!
          </motion.p>
        ) : (
          <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.5, delay: 0.4 }} className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
            {paginatedBlogs.map((blog, index) => (
              <motion.div
                key={blog._id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                whileHover={{ scale: 1.03, transition: { duration: 0.2 } }}
                className="relative group bg-white rounded-xl shadow-lg overflow-hidden transition-all duration-300 hover:shadow-xl"
              >
                <Link to={`/blogpage/${blog._id}`}>
                  <div className="absolute inset-0 bg-gradient-to-t from-blue-500/80 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
                  <div className="p-6 relative z-10">
                    <h3 className="text-xl font-semibold text-gray-800 group-hover:text-white transition-colors duration-300">
                      {blog.title || "Untitled Blog"}
                    </h3>
                    <p className="text-gray-600 group-hover:text-gray-200 mt-2 transition-colors duration-300">
                      {blog.content ? blog.content.substring(0, 100) + "..." : "No content available"}
                    </p>
                    {blog.fileUrl && (
                      <div className="mt-2">
                        {blog.fileUrl.endsWith(".pdf") ? (
                          <span className="text-sm text-blue-500 group-hover:text-yellow-300">PDF Attached</span>
                        ) : blog.fileUrl.match(/\.(mp4|webm|ogg)$/) ? (
                          <span className="text-sm text-blue-500 group-hover:text-yellow-300">Video Attached</span>
                        ) : blog.fileUrl.match(/\.(mp3|wav|ogg)$/) ? (
                          <span className="text-sm text-blue-500 group-hover:text-yellow-300">Audio Attached</span>
                        ) : (
                          <span className="text-sm text-blue-500 group-hover:text-yellow-300">Image Attached</span>
                        )}
                      </div>
                    )}
                    <div className="mt-3 flex justify-between items-center">
                      <span className="text-sm text-blue-500 group-hover:text-yellow-300 font-medium transition-colors duration-300">
                        Votes: {(blog.upvotes || 0) - (blog.downvotes || 0)}
                      </span>
                      <span className="text-sm text-gray-500 group-hover:text-gray-300 transition-colors duration-300">
                        By: <Link to={`/profile/${blog.author?.username || 'unknown'}`} className="hover:underline">{blog.author?.username || "Unknown"}</Link>
                        {blog.isPrivate && <LockClosedIcon className="h-4 w-4 inline ml-1 text-red-500" />}
                      </span>
                    </div>
                  </div>
                </Link>
              </motion.div>
            ))}
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default Blog;
