import { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import API from "../api/axiosInstance";
import { LockClosedIcon } from "@heroicons/react/solid";

const Home = () => {
  const [activeSection, setActiveSection] = useState("discussions");
  const [discussions, setDiscussions] = useState([]);
  const [debates, setDebates] = useState([]);
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [disData, debData, blogData] = await Promise.all([
          API.get("/discussions"),
          API.get("/debates"),
          API.get("/blogs"),
        ]);
        setDiscussions(disData.data || []);
        setDebates(debData.data || []);
        setBlogs(blogData.data || []);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const getPopular = (data) =>
    [...data]
      .sort((a, b) => {
        const popA = (a.comments?.length || 0) + (a.upvotes?.length || 0) + (a.downvotes?.length || 0);
        const popB = (b.comments?.length || 0) + (b.upvotes?.length || 0) + (b.downvotes?.length || 0);
        return popB - popA;
      })
      .slice(0, 9);

  return (
    <div className="md:ml-64 p-4">
      {/* Navigation */}
      <div className="flex flex-col md:flex-row gap-4 mb-8">
        <button
          onClick={() => setActiveSection("discussions")}
          className={`py-2 px-4 rounded ${activeSection === "discussions" ? "bg-blue-600 text-white" : "bg-gray-200"}`}
        >
          Discussions
        </button>
        <button
          onClick={() => setActiveSection("debates")}
          className={`py-2 px-4 rounded ${activeSection === "debates" ? "bg-teal-600 text-white" : "bg-gray-200"}`}
        >
          Debates
        </button>
        <button
          onClick={() => setActiveSection("blogs")}
          className={`py-2 px-4 rounded ${activeSection === "blogs" ? "bg-purple-600 text-white" : "bg-gray-200"}`}
        >
          Blogs
        </button>
      </div>

      {/* Content */}
      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          {activeSection === "discussions" && (
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
              {getPopular(discussions).map((discussion) => (
                <Link
                  key={discussion._id}
                  to={`/discussionpage/${discussion._id}`}
                  className="relative group rounded-lg overflow-hidden shadow-lg hover:scale-105 transition-transform"
                >
                  <div className="absolute inset-0 bg-gradient-to-t from-blue-500/80 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
                  <div className="p-4 md:p-6 relative z-10">
                    <h3 className="text-lg md:text-xl font-semibold text-gray-800 group-hover:text-white transition-colors duration-300">
                      {discussion.title || "Untitled Discussion"}
                    </h3>
                    <p className="text-gray-600 group-hover:text-gray-200 mt-2 text-sm md:text-base">
                      {discussion.description ? discussion.description.substring(0, 80) + "..." : "No description"}
                    </p>
                    <p className="text-xs md:text-sm text-blue-500 group-hover:text-yellow-300 mt-3 font-medium transition-colors duration-300">
                      {discussion.category || "Uncategorized"}
                    </p>
                    <p className="text-xs md:text-sm text-gray-500 group-hover:text-gray-300 mt-1 transition-colors duration-300">
                      By:{" "}
                      <Link to={`/profile/${discussion.author?.username || "unknown"}`} className="hover:underline">
                        {discussion.author?.username || "Unknown"}
                      </Link>
                      {discussion.isPrivate && <LockClosedIcon className="h-4 w-4 inline ml-1 text-red-500" />}
                    </p>
                  </div>
                </Link>
              ))}
              {discussions.length >= 9 && (
                <Link to="/discussions" className="flex items-center justify-center bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition">
                  See More
                </Link>
              )}
            </div>
          )}

          {activeSection === "debates" && (
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
              {getPopular(debates).map((debate) => (
                <Link
                  key={debate._id}
                  to={`/debatepage/${debate._id}`}
                  className="block p-4 border rounded-lg shadow hover:bg-gradient-to-r hover:from-teal-500/20 hover:to-blue-500/20 transition-colors duration-200"
                >
                  <h3 className="text-gray-800 font-semibold text-sm md:text-base">
                    {debate.title || "Untitled Debate"}
                  </h3>
                  <p className="text-gray-600 text-xs md:text-sm truncate">
                    {debate.openingArgument ? `${debate.openingArgument.substring(0, 30)}...` : "No argument"}
                  </p>
                  <div className="flex justify-between text-xs md:text-sm mt-1">
                    <span className="text-teal-500">{debate.category || "Uncategorized"}</span>
                    <span className="text-gray-500 group-hover:text-gray-300 transition-colors duration-300">
                      By:{" "}
                      <Link to={`/profile/${debate.author?.username || "unknown"}`} className="hover:underline">
                        {debate.author?.username || "Unknown"}
                      </Link>
                      {debate.isPrivate && <LockClosedIcon className="h-4 w-4 inline ml-1 text-red-500" />}
                    </span>
                  </div>
                </Link>
              ))}
              {debates.length >= 9 && (
                <Link to="/debates" className="flex items-center justify-center bg-teal-500 text-white rounded-lg hover:bg-teal-600 transition">
                  See More
                </Link>
              )}
            </div>
          )}

          {activeSection === "blogs" && (
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
              {getPopular(blogs).map((blog) => (
                <Link
                  key={blog._id}
                  to={`/blogpage/${blog._id}`}
                  className="relative group rounded-lg overflow-hidden shadow-lg hover:scale-105 transition-transform"
                >
                  <div className="absolute inset-0 bg-gradient-to-t from-blue-500/80 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
                  <div className="p-4 md:p-6 relative z-10">
                    <h3 className="text-lg md:text-xl font-semibold text-gray-800 group-hover:text-white transition-colors duration-300">
                      {blog.title || "Untitled Blog"}
                    </h3>
                    <p className="text-gray-600 group-hover:text-gray-200 mt-2 text-sm md:text-base">
                      {blog.content ? blog.content.substring(0, 80) + "..." : "No content available"}
                    </p>
                    {blog.fileUrl && (
                      <div className="mt-2">
                        {blog.fileUrl.endsWith(".pdf") ? (
                          <span className="text-xs md:text-sm text-blue-500 group-hover:text-yellow-300">PDF Attached</span>
                        ) : blog.fileUrl.match(/\.(mp4|webm|ogg)$/) ? (
                          <span className="text-xs md:text-sm text-blue-500 group-hover:text-yellow-300">Video Attached</span>
                        ) : blog.fileUrl.match(/\.(mp3|wav|ogg)$/) ? (
                          <span className="text-xs md:text-sm text-blue-500 group-hover:text-yellow-300">Audio Attached</span>
                        ) : (
                          <span className="text-xs md:text-sm text-blue-500 group-hover:text-yellow-300">Image Attached</span>
                        )}
                      </div>
                    )}
                    <div className="mt-3 flex justify-between items-center">
                      <span className="text-xs md:text-sm text-blue-500 group-hover:text-yellow-300 font-medium transition-colors duration-300">
                        Votes: {(blog.upvotes || 0) - (blog.downvotes || 0)}
                      </span>
                      <span className="text-xs md:text-sm text-gray-500 group-hover:text-gray-300 transition-colors duration-300">
                        By:{" "}
                        <Link to={`/profile/${blog.author?.username || "unknown"}`} className="hover:underline">
                          {blog.author?.username || "Unknown"}
                        </Link>
                        {blog.isPrivate && <LockClosedIcon className="h-4 w-4 inline ml-1 text-red-500" />}
                      </span>
                    </div>
                  </div>
                </Link>
              ))}
              {blogs.length >= 9 && (
                <Link to="/blogs" className="flex items-center justify-center bg-purple-500 text-white rounded-lg hover:bg-purple-600 transition">
                  See More
                </Link>
              )}
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default Home;
