import React, { useEffect, useState, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance";
import { ChatIcon, UserAddIcon, UserRemoveIcon, CogIcon } from "@heroicons/react/outline";

const BACKEND_BASE_URL = "http://localhost:5001";

function getToken() {
  const token = localStorage.getItem("token");
  console.log("Retrieved token:", token);
  return token;
}

const ProfilePage = () => {
  const { username } = useParams();
  const navigate = useNavigate();

  const [profileUser, setProfileUser] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);
  const [createdDebates, setCreatedDebates] = useState([]);
  const [createdBlogs, setCreatedBlogs] = useState([]);
  const [createdDiscussions, setCreatedDiscussions] = useState([]);
  const [profileError, setProfileError] = useState("");
  const [debatesError, setDebatesError] = useState("");
  const [discussionsError, setDiscussionsError] = useState("");
  const [isFollowing, setIsFollowing] = useState(false);
  const [isFollower, setIsFollower] = useState(false);
  const [activeTab, setActiveTab] = useState("debate");
  const [showFollowers, setShowFollowers] = useState(false);
  const [showFollowing, setShowFollowing] = useState(false);
  const [showSettingsMenu, setShowSettingsMenu] = useState(false);
  const [showChangeBioModal, setShowChangeBioModal] = useState(false);
  const [showChangeProfilePictureModal, setShowChangeProfilePictureModal] = useState(false);
  const [newBio, setNewBio] = useState("");
  const [newProfilePicture, setNewProfilePicture] = useState(null);
  const [loading, setLoading] = useState(true); // New loading state

  const token = getToken();

  const fetchCurrentUser = useCallback(async () => {
    if (!token) return null;
    const res = await API.get("/users/profile/me", {
      headers: { Authorization: `Bearer ${token}` },
    });
    return res.data;
  }, [token]);

  const fetchProfile = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    return res.data;
  }, [username, token]);

  const fetchCreatedDebates = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}/debates`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    return res.data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  }, [username, token]);

  const fetchCreatedBlogs = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}/blogs`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    return res.data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  }, [username, token]);

  const fetchCreatedDiscussions = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}/discussions`, {
      headers: { Authorization: `Bearer ${token}` },
    });
    return res.data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  }, [username, token]);

  const fetchAllData = useCallback(async () => {
    setLoading(true); // Start loading
    setProfileUser(null); // Reset state to avoid stale data
    setCreatedDebates([]);
    setCreatedBlogs([]);
    setCreatedDiscussions([]);
    setProfileError("");
    setDebatesError("");
    setDiscussionsError("");

    try {
      const [currentUserData, profileData, debatesData, blogsData, discussionsData] = await Promise.all([
        fetchCurrentUser(),
        fetchProfile(),
        fetchCreatedDebates(),
        fetchCreatedBlogs(),
        fetchCreatedDiscussions(),
      ]);

      setCurrentUser(currentUserData);
      setProfileUser(profileData);
      setCreatedDebates(debatesData);
      setCreatedBlogs(blogsData);
      setCreatedDiscussions(discussionsData);

      setNewBio(profileData.bio || "");
      const followers = Array.isArray(profileData.followers) ? profileData.followers : [];
      const followings = Array.isArray(profileData.followings) ? profileData.followings : [];
      if (currentUserData) {
        setIsFollowing(followers.some((f) => String(f._id) === String(currentUserData._id)));
        setIsFollower(followings.some((f) => String(f._id) === String(currentUserData._id)));
      }
    } catch (err) {
      console.error("Error fetching data:", err);
      setProfileError(err.response?.data?.message || "Error fetching profile data");
      setDebatesError(err.response?.data?.message || "Error fetching debates");
      setDiscussionsError(err.response?.data?.message || "Error fetching discussions");
    } finally {
      setLoading(false); // End loading
    }
  }, [
    fetchCurrentUser,
    fetchProfile,
    fetchCreatedDebates,
    fetchCreatedBlogs,
    fetchCreatedDiscussions,
  ]);

  useEffect(() => {
    fetchAllData();
  }, [username, fetchAllData]); // Depend on username directly

  const handleChangeBio = async () => {
    try {
      if (!token) throw new Error("No authentication token found");
      if (typeof newBio !== "string") throw new Error("Bio must be a string");
      await API.put("/users/profile/update-bio", { bio: newBio }, { headers: { Authorization: `Bearer ${token}` } });
      setShowChangeBioModal(false);
      fetchAllData();
    } catch (err) {
      console.error("Error updating bio:", err);
      setProfileError(err.response?.data?.message || err.message || "Error updating bio");
    }
  };

  const handleChangeProfilePicture = async () => {
    if (!newProfilePicture) {
      setProfileError("Please select a file to upload.");
      return;
    }
    const formData = new FormData();
    formData.append("profilePicture", newProfilePicture);
    try {
      if (!token) throw new Error("No authentication token found");
      await API.put("/users/profile/update-profile-picture", formData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setShowChangeProfilePictureModal(false);
      setNewProfilePicture(null);
      fetchAllData();
    } catch (err) {
      console.error("Error updating profile picture:", err);
      setProfileError(err.response?.data?.message || err.message || "Error updating profile picture");
    }
  };

  const handleLogOut = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const handleShowFollowers = () => {
    if (!token) {
      alert("Please log in to view followers!");
      navigate("/login");
      return;
    }
    setShowFollowers(true);
  };

  const handleShowFollowing = () => {
    if (!token) {
      alert("Please log in to view following!");
      navigate("/login");
      return;
    }
    setShowFollowing(true);
  };

  const handleFollow = async () => {
    if (!token) {
      alert("You must be logged in to follow or unfollow!");
      return navigate("/login");
    }
    try {
      if (isFollowing) {
        await API.post(`/users/profile/${username}/unfollow`, {}, { headers: { Authorization: `Bearer ${token}` } });
      } else {
        await API.post(`/users/profile/${username}/follow`, {}, { headers: { Authorization: `Bearer ${token}` } });
      }
      fetchAllData();
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error following/unfollowing");
      console.error(err);
    }
  };

  const handleChat = () => {
    if (!token) {
      alert("You must be logged in to chat!");
      return navigate("/login");
    }
    alert("Open chat window here!");
  };

  const handleDebateClick = (debateId) => navigate(`/debatepage/${debateId}`);
  const handleDiscussionClick = (discussionId) => navigate(`/discussionpage/${discussionId}`);
  const handleBlogClick = (blogId) => navigate(`/blogpage/${blogId}`);
  const handleProfileClick = (username) => {
    setShowFollowers(false);
    setShowFollowing(false);
    navigate(`/profile/${username}`);
  };

  const isOwnProfile = currentUser && profileUser && currentUser._id === profileUser._id;
  const totalPosts = (createdDebates.length || 0) + (createdDiscussions.length || 0) + (createdBlogs.length || 0);
  const followButtonText = isFollowing ? "Unfollow" : isFollower ? "Follow Back" : "Follow";
  const MAX_ITEMS_BEFORE_SCROLL = 5;

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-900 text-white ml-64 flex items-center justify-center">
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
          className="w-12 h-12 border-4 border-blue-500 border-t-transparent rounded-full"
        />
        <span className="ml-4 text-xl">Loading profile...</span>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white ml-64">
      {/* Error Messages */}
      {(profileError || debatesError || discussionsError) && (
        <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 mt-4">
          {profileError && <div className="bg-red-600 text-white p-4 rounded-lg mb-4">{profileError}</div>}
          {debatesError && <div className="bg-red-600 text-white p-4 rounded-lg mb-4">{debatesError}</div>}
          {discussionsError && <div className="bg-red-600 text-white p-4 rounded-lg mb-4">{discussionsError}</div>}
        </div>
      )}

      {/* Header Section */}
      <header className="relative">
        <div className="h-48 bg-gradient-to-r from-blue-600 to-indigo-700"></div>
        <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 -mt-20">
          <div className="flex items-end">
            <motion.img
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5 }}
              src={
                profileUser?.profilePicture
                  ? `${BACKEND_BASE_URL}${profileUser.profilePicture}`
                  : `${BACKEND_BASE_URL}/images/default-avatar.png`
              }
              alt="profile"
              className="w-40 h-40 rounded-full border-4 border-gray-900 shadow-md"
              onError={(e) => (e.target.src = `${BACKEND_BASE_URL}/images/default-avatar.png`)}
            />
            <div className="ml-6 flex-1">
              <div className="flex justify-between items-center">
                <div>
                  <h1 className="text-4xl font-bold">{profileUser?.username || "Loading..."}</h1>
                  <p className="text-xl text-gray-400">@{profileUser?.username || "Loading..."}</p>
                </div>
                {isOwnProfile ? (
                  <div className="relative">
                    <motion.button
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      onClick={() => setShowSettingsMenu(!showSettingsMenu)}
                      className="p-2 bg-gray-700 text-white rounded-full hover:bg-gray-600 transition-colors duration-200"
                    >
                      <CogIcon className="h-6 w-6" />
                    </motion.button>
                    {showSettingsMenu && (
                      <div className="absolute right-0 mt-2 w-48 bg-gray-800 rounded-lg shadow-lg z-10">
                        <ul className="py-2">
                          <li
                            className="px-4 py-2 hover:bg-gray-700 cursor-pointer"
                            onClick={() => {
                              setShowChangeBioModal(true);
                              setShowSettingsMenu(false);
                            }}
                          >
                            Change Bio
                          </li>
                          <li
                            className="px-4 py-2 hover:bg-gray-700 cursor-pointer"
                            onClick={() => {
                              setShowChangeProfilePictureModal(true);
                              setShowSettingsMenu(false);
                            }}
                          >
                            Change Profile Picture
                          </li>
                          <li className="px-4 py-2 hover:bg-gray-700 cursor-pointer" onClick={handleLogOut}>
                            Log Out
                          </li>
                          <li
                            className="px-4 py-2 hover:bg-gray-700 cursor-pointer"
                            onClick={() => {
                              setShowSettingsMenu(false);
                              fetchAllData();
                            }}
                          >
                            Cancel
                          </li>
                        </ul>
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="flex space-x-3">
                    <motion.button
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      onClick={handleFollow}
                      className={`flex items-center px-4 py-2 rounded-full text-white ${
                        isFollowing ? "bg-gray-600 hover:bg-gray-700" : "bg-blue-600 hover:bg-blue-700"
                      } transition-all duration-300`}
                    >
                      {isFollowing ? <UserRemoveIcon className="h-5 w-5 mr-2" /> : <UserAddIcon className="h-5 w-5 mr-2" />}
                      {followButtonText}
                    </motion.button>
                    <motion.button
                      whileHover={{ scale: 1.05 }}
                      whileTap={{ scale: 0.95 }}
                      onClick={handleChat}
                      className="flex items-center px-4 py-2 bg-green-600 text-white rounded-full hover:bg-green-700 transition-all duration-300"
                    >
                      <ChatIcon className="h-5 w-5 mr-2" />
                      Chat
                    </motion.button>
                  </div>
                )}
              </div>
              <div className="mt-4 flex space-x-8">
                <div>
                  <span className="text-xl font-bold">{totalPosts}</span>{" "}
                  <span className="text-gray-400">Posts</span>
                </div>
                <div className="cursor-pointer" onClick={handleShowFollowers}>
                  <span className="text-xl font-bold">{profileUser?.followers?.length || 0}</span>{" "}
                  <span className="text-gray-400">Followers</span>
                </div>
                <div className="cursor-pointer" onClick={handleShowFollowing}>
                  <span className="text-xl font-bold">{profileUser?.followings?.length || 0}</span>{" "}
                  <span className="text-gray-400">Following</span>
                </div>
              </div>
              <p className="mt-2 text-gray-300">{profileUser?.bio || "No bio yet."}</p>
              {profileUser?.name && <p className="text-gray-400">Name: {profileUser.name}</p>}
              <p className="text-gray-400">
                Joined: {profileUser?.createdAt ? new Date(profileUser.createdAt).toLocaleDateString() : "Loading..."}
              </p>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="max-w-5xl mx-auto px-4 sm:px-6 lg:px-8 mt-6">
        {/* Tabs */}
        <div className="border-b border-gray-700">
          <div className="flex justify-around">
            {["debate", "discussion", "blogs"].map((tab) => (
              <motion.button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`pb-4 px-6 text-base font-medium ${
                  activeTab === tab ? "border-b-2 border-blue-500 text-white" : "text-gray-400 hover:text-gray-200"
                } transition-colors duration-200`}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                {tab.toUpperCase()}
              </motion.button>
            ))}
          </div>
        </div>

        {/* Posts */}
        <div className="mt-6">
          {activeTab === "debate" && (
            <div
              className={`space-y-4 ${
                createdDebates.length > MAX_ITEMS_BEFORE_SCROLL
                  ? "max-h-[500px] overflow-y-auto scrollbar-thin scrollbar-thumb-gray-600 scrollbar-track-gray-800 pr-2"
                  : ""
              }`}
            >
              {createdDebates.length > 0 ? (
                createdDebates.map((deb, index) => (
                  <motion.div
                    key={deb._id || index}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.3, delay: index * 0.1 }}
                    className="bg-gray-800 rounded-lg p-4 cursor-pointer hover:bg-gray-700 transition-all duration-200 shadow-md"
                    onClick={() => handleDebateClick(deb._id)}
                  >
                    <div className="flex justify-between items-start">
                      <div>
                        <h3 className="text-lg font-bold">{deb.title || "Untitled Debate"}</h3>
                        <p className="text-gray-400">
                          {deb.openingArgument?.substring(0, 100) || "No opening argument available."}
                          {deb.openingArgument?.length > 100 ? "..." : ""}
                        </p>
                      </div>
                      <div className="text-gray-500 text-sm">
                        <span>{deb.comments?.length || 0} Comments</span>
                      </div>
                    </div>
                    <p className="text-gray-500 text-sm mt-2">
                      Posted on: {new Date(deb.createdAt).toLocaleString()}
                    </p>
                  </motion.div>
                ))
              ) : (
                <p className="text-center text-gray-400">No debates created yet.</p>
              )}
            </div>
          )}
          {activeTab === "discussion" && (
            <div
              className={`space-y-4 ${
                createdDiscussions.length > MAX_ITEMS_BEFORE_SCROLL
                  ? "max-h-[500px] overflow-y-auto scrollbar-thin scrollbar-thumb-gray-600 scrollbar-track-gray-800 pr-2"
                  : ""
              }`}
            >
              {createdDiscussions.length > 0 ? (
                createdDiscussions.map((disc, index) => (
                  <motion.div
                    key={disc._id || index}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.3, delay: index * 0.1 }}
                    className="bg-gray-800 rounded-lg p-4 cursor-pointer hover:bg-gray-700 transition-all duration-200 shadow-md"
                    onClick={() => handleDiscussionClick(disc._id)}
                  >
                    <div className="flex justify-between items-start">
                      <div>
                        <h3 className="text-lg font-bold">{disc.title || "Untitled Discussion"}</h3>
                        <p className="text-gray-400">
                          {disc.description?.substring(0, 100) || "No description available."}
                          {disc.description?.length > 100 ? "..." : ""}
                        </p>
                      </div>
                      <div className="text-gray-500 text-sm">
                        <span>{disc.comments?.length || 0} Comments</span>
                      </div>
                    </div>
                    <p className="text-gray-500 text-sm mt-2">
                      Posted on: {new Date(disc.createdAt).toLocaleString()}
                    </p>
                  </motion.div>
                ))
              ) : (
                <p className="text-center text-gray-400">No discussions created yet.</p>
              )}
            </div>
          )}
          {activeTab === "blogs" && (
            <div
              className={`space-y-4 ${
                createdBlogs.length > MAX_ITEMS_BEFORE_SCROLL
                  ? "max-h-[500px] overflow-y-auto scrollbar-thin scrollbar-thumb-gray-600 scrollbar-track-gray-800 pr-2"
                  : ""
              }`}
            >
              {createdBlogs.length > 0 ? (
                createdBlogs.map((blog, index) => (
                  <motion.div
                    key={blog._id || index}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.3, delay: index * 0.1 }}
                    className="bg-gray-800 rounded-lg p-4 cursor-pointer hover:bg-gray-700 transition-all duration-200 shadow-md"
                    onClick={() => handleBlogClick(blog._id)}
                  >
                    <div className="flex justify-between items-start">
                      <div>
                        <h3 className="text-lg font-bold">{blog.title || "Untitled Blog"}</h3>
                        <p className="text-gray-400">
                          {blog.content?.substring(0, 100) || "No content available."}
                          {blog.content?.length > 100 ? "..." : ""}
                        </p>
                      </div>
                      <div className="text-gray-500 text-sm">
                        <span>{blog.comments?.length || 0} Comments</span>
                      </div>
                    </div>
                    {blog.fileUrl && (
                      <div className="mt-2">
                        {blog.fileUrl.match(/\.(jpeg|jpg|png)$/i) && (
                          <img
                            src={`${BACKEND_BASE_URL}${blog.fileUrl}`}
                            alt="Blog media"
                            className="w-full h-32 object-cover rounded-lg"
                            onError={(e) => (e.target.src = `${BACKEND_BASE_URL}/images/default-image.png`)}
                          />
                        )}
                        {blog.fileUrl.match(/\.(mp4|webm|ogg)$/i) && (
                          <video
                            src={`${BACKEND_BASE_URL}${blog.fileUrl}`}
                            className="w-full h-32 rounded-lg"
                            controls
                          />
                        )}
                        {blog.fileUrl.match(/\.(mp3|wav|ogg)$/i) && (
                          <audio src={`${BACKEND_BASE_URL}${blog.fileUrl}`} className="w-full" controls />
                        )}
                        {blog.fileUrl.match(/\.pdf$/i) && (
                          <a
                            href={`${BACKEND_BASE_URL}${blog.fileUrl}`}
                            className="text-blue-400 hover:underline"
                            target="_blank"
                            rel="noopener noreferrer"
                          >
                            View PDF
                          </a>
                        )}
                      </div>
                    )}
                    <p className="text-gray-500 text-sm mt-2">
                      Posted on: {new Date(blog.createdAt).toLocaleString()}
                    </p>
                  </motion.div>
                ))
              ) : (
                <p className="text-center text-gray-400">No blogs yet.</p>
              )}
            </div>
          )}
        </div>
      </div>

      {/* Followers Modal */}
      {showFollowers && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center"
        >
          <motion.div
            initial={{ scale: 0.8 }}
            animate={{ scale: 1 }}
            exit={{ scale: 0.8 }}
            className="bg-gray-900 text-white p-6 rounded-xl shadow-lg max-w-md w-full max-h-[80vh] overflow-y-auto"
          >
            <h2 className="text-2xl font-bold mb-4 text-center">Followers</h2>
            {profileUser?.followers?.length > 0 ? (
              <ul className="space-y-4">
                {profileUser.followers.map((follower) => (
                  <li
                    key={follower._id}
                    className="flex items-center space-x-3 p-3 bg-gray-800 rounded-lg cursor-pointer hover:bg-gray-700 transition-all duration-200"
                    onClick={() => handleProfileClick(follower.username)}
                  >
                    <img
                      src={
                        follower.profilePicture
                          ? `${BACKEND_BASE_URL}${follower.profilePicture}`
                          : `${BACKEND_BASE_URL}/images/default-avatar.png`
                      }
                      alt={follower.username}
                      className="w-10 h-10 rounded-full border-2 border-gray-600"
                      onError={(e) => (e.target.src = `${BACKEND_BASE_URL}/images/default-avatar.png`)}
                    />
                    <span className="text-lg">{follower.username}</span>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-center text-gray-400">No followers yet.</p>
            )}
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => setShowFollowers(false)}
              className="mt-6 w-full bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-all duration-300"
            >
              Close
            </motion.button>
          </motion.div>
        </motion.div>
      )}

      {/* Following Modal */}
      {showFollowing && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center"
        >
          <motion.div
            initial={{ scale: 0.8 }}
            animate={{ scale: 1 }}
            exit={{ scale: 0.8 }}
            className="bg-gray-900 text-white p-6 rounded-xl shadow-lg max-w-md w-full max-h-[80vh] overflow-y-auto"
          >
            <h2 className="text-2xl font-bold mb-4 text-center">Following</h2>
            {profileUser?.followings?.length > 0 ? (
              <ul className="space-y-4">
                {profileUser.followings.map((following) => (
                  <li
                    key={following._id}
                    className="flex items-center space-x-3 p-3 bg-gray-800 rounded-lg cursor-pointer hover:bg-gray-700 transition-all duration-200"
                    onClick={() => handleProfileClick(following.username)}
                  >
                    <img
                      src={
                        following.profilePicture
                          ? `${BACKEND_BASE_URL}${following.profilePicture}`
                          : `${BACKEND_BASE_URL}/images/default-avatar.png`
                      }
                      alt={following.username}
                      className="w-10 h-10 rounded-full border-2 border-gray-600"
                      onError={(e) => (e.target.src = `${BACKEND_BASE_URL}/images/default-avatar.png`)}
                    />
                    <span className="text-lg">{following.username}</span>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-center text-gray-400">Not following anyone yet.</p>
            )}
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => setShowFollowing(false)}
              className="mt-6 w-full bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-all duration-300"
            >
              Close
            </motion.button>
          </motion.div>
        </motion.div>
      )}

      {/* Change Bio Modal */}
      {showChangeBioModal && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center"
        >
          <motion.div
            initial={{ scale: 0.8 }}
            animate={{ scale: 1 }}
            exit={{ scale: 0.8 }}
            className="bg-gray-900 text-white p-6 rounded-xl shadow-lg max-w-md w-full"
          >
            <h2 className="text-2xl font-bold mb-4 text-center">Change Bio</h2>
            <textarea
              value={newBio}
              onChange={(e) => setNewBio(e.target.value)}
              className="w-full p-2 bg-gray-800 text-white rounded-lg border border-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
              rows="4"
              placeholder="Enter your new bio..."
            />
            <div className="flex space-x-3 mt-4">
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={handleChangeBio}
                className="flex-1 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-all duration-300"
              >
                Save
              </motion.button>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => setShowChangeBioModal(false)}
                className="flex-1 bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-all duration-300"
              >
                Cancel
              </motion.button>
            </div>
          </motion.div>
        </motion.div>
      )}

      {/* Change Profile Picture Modal */}
      {showChangeProfilePictureModal && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          exit={{ opacity: 0 }}
          className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center"
        >
          <motion.div
            initial={{ scale: 0.8 }}
            animate={{ scale: 1 }}
            exit={{ scale: 0.8 }}
            className="bg-gray-900 text-white p-6 rounded-xl shadow-lg max-w-md w-full"
          >
            <h2 className="text-2xl font-bold mb-4 text-center">Change Profile Picture</h2>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => setNewProfilePicture(e.target.files[0])}
              className="w-full p-2 bg-gray-800 text-white rounded-lg border border-gray-700 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
            <div className="flex space-x-3 mt-4">
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={handleChangeProfilePicture}
                className="flex-1 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-all duration-300"
              >
                Upload
              </motion.button>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => setShowChangeProfilePictureModal(false)}
                className="flex-1 bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-all duration-300"
              >
                Cancel
              </motion.button>
            </div>
          </motion.div>
        </motion.div>
      )}
    </div>
  );
};

export default ProfilePage;
