import React, { useEffect, useState, useRef, useCallback } from "react";
import { NavLink, Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import {
  HomeIcon,
  ChatBubbleLeftRightIcon,
  PencilIcon,
  UserIcon,
  MagnifyingGlassIcon,
  BellIcon,
  Bars3Icon,
  XMarkIcon,
} from "@heroicons/react/outline";
import API from "../api/axiosInstance";

const Navbar = () => {
  const navigate = useNavigate();
  const [isOpen, setIsOpen] = useState(false);
  const [username, setUsername] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [isNotificationOpen, setIsNotificationOpen] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const [token, setToken] = useState(localStorage.getItem("token"));
  const searchRef = useRef(null);
  const notificationRef = useRef(null);
  const lastNotificationIdRef = useRef(null);

  // Toggle navbar on mobile
  const toggleNavbar = () => {
    setIsOpen(!isOpen);
  };

  // Format notification message
  const formatNotificationMessage = useCallback((notif) => {
    const actor = notif.user?.username || "Someone";
    const targetTitle = notif.target?.title || "Untitled";
    const targetType = notif.target?.type || "post";

    switch (notif.type) {
      case "follow": return `${actor} followed you`;
      case "unfollow": return `${actor} unfollowed you`;
      case "upvote": return `${actor} upvoted your ${targetType} "${targetTitle}"`;
      case "downvote": return `${actor} downvoted your ${targetType} "${targetTitle}"`;
      case "comment": return `${actor} commented "${notif.comment?.text || "something"}" on your ${targetType} "${targetTitle}"`;
      case "comment_like": return `${actor} liked your comment at ${targetType} "${targetTitle}"`;
      default: return notif.message || "New notification";
    }
  }, []);

  // Fetch notifications
  const fetchNotifications = useCallback(async () => {
    if (!token) return;

    try {
      const res = await API.get("/notifications", {
        headers: { Authorization: `Bearer ${token}` },
      });

      const formattedNotifications = res.data.map((notif) => ({
        ...notif,
        message: formatNotificationMessage(notif),
      }));

      formattedNotifications.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
      const latestId = formattedNotifications[0]?._id;
      if (lastNotificationIdRef.current !== latestId) {
        setNotifications(formattedNotifications);
        setUnreadCount(formattedNotifications.filter((n) => !n.read).length);
        lastNotificationIdRef.current = latestId;
      }
    } catch (err) {
      console.error("Error fetching notifications:", err);
    }
  }, [token, formatNotificationMessage]);

  // Fetch user profile
  const fetchUserProfile = useCallback(async () => {
    if (token) {
      try {
        const res = await API.get("/users/profile/me", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUsername(res.data.username);
        fetchNotifications();
      } catch (err) {
        console.error("Error fetching user profile:", err);
        setUsername("");
        setNotifications([]);
        setUnreadCount(0);
      }
    } else {
      setUsername("");
      setNotifications([]);
      setUnreadCount(0);
    }
  }, [token, fetchNotifications]);

  // Monitor token changes and fetch profile/notifications
  useEffect(() => {
    fetchUserProfile();

    const pollingId = token ? setInterval(fetchNotifications, 5000) : null;

    const handleTokenChange = () => {
      const newToken = localStorage.getItem("token");
      if (newToken !== token) {
        setToken(newToken);
      }
    };

    window.addEventListener("storageUpdate", handleTokenChange);

    return () => {
      if (pollingId) clearInterval(pollingId);
      window.removeEventListener("storageUpdate", handleTokenChange);
    };
  }, [token, fetchUserProfile, fetchNotifications]);

  // Close dropdowns on outside click
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setIsSearchOpen(false);
      }
      if (notificationRef.current && !notificationRef.current.contains(event.target)) {
        setIsNotificationOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  // Handle user search
  const handleSearch = async (query) => {
    setSearchQuery(query);
    if (query.trim() === "") {
      setSearchResults([]);
      setIsSearchOpen(false);
      return;
    }
    try {
      const { data } = await API.get(`/users/search?query=${query}`);
      setSearchResults(data);
      setIsSearchOpen(data.length > 0);
    } catch (error) {
      console.error("Error searching users:", error);
      setSearchResults([]);
    }
  };

  // Mark notifications as read
  const markNotificationsAsRead = async () => {
    try {
      await API.put("/notifications/mark-read", {}, { headers: { Authorization: `Bearer ${token}` } });
      setNotifications((prev) => prev.map((notif) => ({ ...notif, read: true })));
      setUnreadCount(0);
    } catch (error) {
      console.error("Error marking notifications as read:", error);
    }
  };

  // Toggle notification dropdown
  const toggleNotifications = () => {
    setIsNotificationOpen((prev) => !prev);
    if (!isNotificationOpen && unreadCount > 0) {
      markNotificationsAsRead();
    }
  };

  // Logout functionality
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    setToken(null);
    setUsername("");
    navigate("/login");
    setIsOpen(false);
  };

  // Group notifications by time period
  const getNotificationGroup = (date) => {
    const today = new Date();
    const notificationDate = new Date(date);
    const diffTime = today - notificationDate;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays <= 1) return "Today";
    if (diffDays <= 2) return "Yesterday";
    if (diffDays <= 7) return "This week";
    if (diffDays <= 30) return "This month";
    return "Older";
  };

  const groupedNotifications = notifications.reduce((acc, notif) => {
    const group = getNotificationGroup(notif.createdAt);
    if (!acc[group]) acc[group] = [];
    acc[group].push(notif);
    return acc;
  }, {});

  const groupOrder = ["Today", "Yesterday", "This week", "This month", "Older"];

  return (
    <>
      {/* Toggle Button for Mobile */}
      <motion.button
        onClick={toggleNavbar}
        className="md:hidden fixed top-4 left-4 z-50 p-2 bg-white rounded-full shadow-md"
        whileHover={{ scale: 1.1 }}
        whileTap={{ scale: 0.9 }}
        transition={{ duration: 0.3, ease: "easeInOut" }}
      >
        {isOpen ? (
          <XMarkIcon className="h-6 w-6 text-gray-800" />
        ) : (
          <Bars3Icon className="h-6 w-6 text-gray-800" />
        )}
      </motion.button>

      {/* Navbar */}
      <motion.nav
        initial={{ x: -256 }}
        animate={{ x: isOpen ? 0 : -256 }}
        transition={{ duration: 0.5, ease: "easeInOut" }}
        className={`fixed top-0 left-0 h-full w-64 bg-white text-gray-800 flex flex-col shadow-lg z-40 md:translate-x-0 md:w-64 md:flex md:flex-col md:justify-between`}
      >
        {/* Navbar Header */}
        <motion.div
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, ease: "easeOut", delay: 0.2 }}
          className="p-4"
        >
          <Link to="/" className="text-2xl font-bold text-blue-600">
            Debatify
          </Link>
        </motion.div>

        {/* Navigation Items */}
        <div className="flex-1 space-y-2 p-4">
          <NavLink
            to="/"
            onClick={() => setIsOpen(false)}
            className={({ isActive }) =>
              `flex items-center space-x-3 p-3 rounded-lg ${
                isActive ? "bg-gray-100 text-blue-600" : "text-gray-800 hover:bg-gray-100"
              } transition-colors duration-300`
            }
          >
            <HomeIcon className="h-6 w-6" />
            <span className="text-lg font-medium">Home</span>
          </NavLink>

          <NavLink
            to="/debate"
            onClick={() => setIsOpen(false)}
            className={({ isActive }) =>
              `flex items-center space-x-3 p-3 rounded-lg ${
                isActive ? "bg-gray-100 text-blue-600" : "text-gray-800 hover:bg-gray-100"
              } transition-colors duration-300`
            }
          >
            <ChatBubbleLeftRightIcon className="h-6 w-6" />
            <span className="text-lg font-medium">Debates</span>
          </NavLink>

          <NavLink
            to="/blog"
            onClick={() => setIsOpen(false)}
            className={({ isActive }) =>
              `flex items-center space-x-3 p-3 rounded-lg ${
                isActive ? "bg-gray-100 text-blue-600" : "text-gray-800 hover:bg-gray-100"
              } transition-colors duration-300`
            }
          >
            <PencilIcon className="h-6 w-6" />
            <span className="text-lg font-medium">Blogs</span>
          </NavLink>

          <motion.div
            initial={{ opacity: 0, y: 10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, ease: "easeOut", delay: 0.3 }}
            className="relative"
            ref={searchRef}
          >
            <motion.div
              whileHover={{ scale: 1.02 }}
              transition={{ duration: 0.3, ease: "easeInOut" }}
              className="flex items-center space-x-3 p-3 rounded-lg bg-gray-100 text-gray-800 transition-colors duration-300"
            >
              <MagnifyingGlassIcon className="h-6 w-6 text-blue-600" />
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => handleSearch(e.target.value)}
                placeholder="Search users..."
                className="bg-transparent focus:outline-none w-full text-gray-800 placeholder-gray-500 text-lg font-medium"
                aria-label="Search users by username"
              />
            </motion.div>
            {isSearchOpen && searchResults.length > 0 && (
              <motion.div
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -10 }}
                transition={{ duration: 0.3, ease: "easeOut" }}
                className="absolute top-14 left-0 w-full bg-white rounded-lg shadow-lg max-h-60 overflow-y-auto z-50 border border-gray-200"
              >
                {searchResults.map((user) => (
                  <Link
                    key={user._id}
                    to={`/profile/${user.username}`}
                    onClick={() => {
                      setSearchQuery("");
                      setIsSearchOpen(false);
                      setIsOpen(false);
                    }}
                    className="block px-4 py-2 hover:bg-gradient-to-r hover:from-blue-500/20 hover:to-purple-500/20 transition-colors duration-300 text-gray-800 border-b border-gray-200 last:border-b-0"
                  >
                    <h3 className="text-gray-800 font-semibold">{user.username}</h3>
                    <p className="text-gray-600 text-sm truncate">
                      {user.bio ? user.bio.substring(0, 30) + "..." : "No bio available"}
                    </p>
                    <div className="flex justify-between text-sm mt-1">
                      <span className="text-blue-500">Role: {user.role || "User"}</span>
                      <span className="text-gray-500">
                        Joined: {user.createdAt ? new Date(user.createdAt).toLocaleDateString() : "Unknown"}
                      </span>
                    </div>
                  </Link>
                ))}
              </motion.div>
            )}
          </motion.div>

          {username && (
            <motion.div
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, ease: "easeOut", delay: 0.4 }}
              className="relative"
              ref={notificationRef}
            >
              <motion.button
                onClick={toggleNotifications}
                className="flex items-center space-x-3 p-3 rounded-lg text-gray-800 hover:bg-gray-100 transition-colors duration-300 w-full"
                animate={unreadCount > 0 ? { x: [0, 5, -5, 0] } : {}}
                transition={
                  unreadCount > 0
                    ? { duration: 0.5, repeat: Infinity, ease: "easeInOut" }
                    : {}
                }
              >
                <BellIcon className="h-6 w-6" />
                <span className="text-lg font-medium">Notifications</span>
                {unreadCount > 0 && (
                  <motion.span
                    initial={{ scale: 0 }}
                    animate={{ scale: 1 }}
                    transition={{ duration: 0.3, ease: "easeOut" }}
                    className="ml-2 bg-red-500 text-white text-xs rounded-full px-2 py-1"
                  >
                    {unreadCount}
                  </motion.span>
                )}
              </motion.button>
              {isNotificationOpen && (
                <motion.div
                  initial={{ opacity: 0, x: -10 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: -10 }}
                  transition={{ duration: 0.3, ease: "easeOut" }}
                  className="absolute left-8 bottom-[-20vh] transform -translate-y-1/2 ml-2 w-96 bg-white rounded-lg shadow-lg max-h-[50vh] overflow-y-auto z-50 border border-gray-200"
                >
                  {notifications.length > 0 ? (
                    groupOrder.map((group) =>
                      groupedNotifications[group] ? (
                        <div key={group}>
                          <h3 className="px-4 py-2 text-sm font-semibold text-gray-600 sticky top-0 bg-white border-b border-gray-200">
                            {group}
                          </h3>
                          {groupedNotifications[group].map((notif, index) => (
                            <motion.div
                              key={notif._id}
                              initial={{ opacity: 0, y: 10 }}
                              animate={{ opacity: 1, y: 0 }}
                              transition={{ duration: 0.3, ease: "easeOut", delay: index * 0.1 }}
                              className={`px-4 py-3 border-b border-gray-200 ${
                                !notif.read ? "bg-blue-50" : ""
                              } hover:bg-gray-100 transition-colors duration-300`}
                            >
                              <p className="text-sm text-gray-800">{notif.message}</p>
                              <p className="text-xs text-gray-500">
                                {new Date(notif.createdAt).toLocaleString()}
                              </p>
                            </motion.div>
                          ))}
                        </div>
                      ) : null
                    )
                  ) : (
                    <motion.p
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                      transition={{ duration: 0.5, ease: "easeOut" }}
                      className="px-4 py-3 text-gray-500 text-sm"
                    >
                      No notifications yet.
                    </motion.p>
                  )}
                </motion.div>
              )}
            </motion.div>
          )}

          {username && (
            <motion.div
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, ease: "easeOut", delay: 0.5 }}
            >
              <NavLink
                to={`/profile/${username}`}
                onClick={() => setIsOpen(false)}
                className={({ isActive }) =>
                  `flex items-center space-x-3 p-3 rounded-lg ${
                    isActive ? "bg-gray-100 text-blue-600" : "text-gray-800 hover:bg-gray-100"
                  } transition-colors duration-300`
                }
              >
                <UserIcon className="h-6 w-6" />
                <span className="text-lg font-medium">Profile</span>
              </NavLink>
            </motion.div>
          )}
        </div>

        {/* Logout/Login Button */}
        <motion.div
          initial={{ opacity: 0, y: 10 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, ease: "easeOut", delay: 0.6 }}
          className="p-4"
        >
          {token ? (
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              transition={{ duration: 0.3, ease: "easeInOut" }}
              onClick={handleLogout}
              className="w-full text-left p-3 rounded-lg text-gray-800 hover:bg-gray-100 transition-colors duration-300"
            >
              Logout
            </motion.button>
          ) : (
            <Link
              to="/login"
              onClick={() => setIsOpen(false)}
              className="w-full block text-left p-3 rounded-lg text-gray-800 hover:bg-gray-100 transition-colors duration-300"
            >
              Login / Register
            </Link>
          )}
        </motion.div>
      </motion.nav>

      {/* Overlay for Mobile */}
      {isOpen && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 0.5 }}
          exit={{ opacity: 0 }}
          transition={{ duration: 0.3, ease: "easeInOut" }}
          className="md:hidden fixed inset-0 bg-black z-30"
          onClick={toggleNavbar}
        />
      )}
    </>
  );
};

export default Navbar;
