import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import API from "../api/axiosInstance";
import { motion } from "framer-motion";
import { ThumbUpIcon, ThumbDownIcon, DownloadIcon } from "@heroicons/react/solid";

// Backend base URL (adjust based on your environment)
const BACKEND_BASE_URL = "http://localhost:5000"; // Change to your backend URL in production

// CSS to ensure video controls are visible and hide seek bar for audio
const mediaStyles = `
  video {
    width: 100%;
    max-height: 24rem; /* max-h-96 */
    border-radius: 0.5rem; /* rounded-lg */
  }
  video::-webkit-media-controls {
    display: flex !important;
    justify-content: center;
  }
  video::-webkit-media-controls-play-button {
    display: block !important;
  }
  audio::-webkit-media-controls-timeline,
  audio::-webkit-media-controls-current-time-display,
  audio::-webkit-media-controls-time-remaining-display {
    display: none !important;
  }
  audio::-webkit-media-controls {
    justify-content: center !important;
  }
  audio {
    width: 100%;
  }
`;

const BlogPage = () => {
  const { id } = useParams();
  const [blog, setBlog] = useState(null);
  const [loading, setLoading] = useState(true);
  const [voting, setVoting] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBlog = async () => {
      try {
        setLoading(true);
        console.log("Fetching blog with ID:", id);
        const { data } = await API.get(`/blogs/${id}`);
        console.log("API response:", data);
        console.log("File URL:", data.fileUrl);
        setBlog(data);
      } catch (error) {
        console.error("Error fetching blog:", error);
        setError("Failed to load blog.");
      } finally {
        setLoading(false);
      }
    };
    fetchBlog();
  }, [id]);

  const handleVote = async (voteType) => {
    try {
      setVoting(true);
      setError(null);
      const token = localStorage.getItem("token");
      if (!token) {
        setError("You must be logged in to vote.");
        return;
      }
      const endpoint = voteType === "upvote" ? `/blogs/${id}/upvote` : `/blogs/${id}/downvote`;
      const { data } = await API.post(endpoint, {}, { headers: { Authorization: `Bearer ${token}` } });

      setBlog((prevBlog) => ({
        ...prevBlog,
        upvotes: data.upvotes || 0,
        downvotes: data.downvotes || 0,
        upvotedBy: data.upvotedBy || prevBlog.upvotedBy,
        downvotedBy: data.downvotedBy || prevBlog.downvotedBy,
      }));
    } catch (error) {
      console.error("Error voting:", error);
      setError(error.response?.data?.message || "Failed to vote.");
    } finally {
      setVoting(false);
    }
  };

  const getFileNameFromUrl = (url) => {
    if (!url) return "";
    const parts = url.split("/");
    return parts[parts.length - 1];
  };

  const renderMedia = (fileUrl) => {
    if (!fileUrl) {
      console.log("No fileUrl provided to renderMedia");
      return null;
    }

    const fullFileUrl = fileUrl.startsWith("http") ? fileUrl : `${BACKEND_BASE_URL}${fileUrl}`;
    console.log("Rendering media for fullFileUrl:", fullFileUrl);

    if (fileUrl.match(/\.(jpeg|jpg|png)$/i)) {
      return (
        <div className="mt-4">
          <img
            src={fullFileUrl}
            alt="Blog media"
            className="w-full max-h-96 object-cover rounded-lg"
            onError={(e) => console.error("Image failed to load:", fullFileUrl)}
          />
        </div>
      );
    } else if (fileUrl.match(/\.(mp4|webm|ogg)$/i)) {
      return (
        <div className="mt-4">
          <video
            controls
            src={fullFileUrl}
            type={fileUrl.endsWith(".mp4") ? "video/mp4" : fileUrl.endsWith(".webm") ? "video/webm" : "video/ogg"}
            className="w-full max-h-96 rounded-lg"
            onError={(e) => console.error("Video failed to load:", fullFileUrl)}
          >
            Your browser does not support the video tag.
          </video>
        </div>
      );
    } else if (fileUrl.match(/\.(mp3|wav|ogg)$/i)) {
      return (
        <div className="mt-4">
          <p className="text-gray-700 font-semibold mb-2">{getFileNameFromUrl(fullFileUrl)}</p>
          <audio
            controls
            controlsList="nodownload noremoteplayback"
            className="w-full"
            onError={(e) => console.error("Audio failed to load:", fullFileUrl)}
          >
            <source
              src={fullFileUrl}
              type={fileUrl.endsWith(".mp3") ? "audio/mpeg" : fileUrl.endsWith(".wav") ? "audio/wav" : "audio/ogg"}
            />
            Your browser does not support the audio element.
          </audio>
        </div>
      );
    } else if (fileUrl.match(/\.pdf$/i)) {
      return (
        <div className="mt-4">
          <a
            href={fullFileUrl}
            download={`blog_${id}.pdf`}
            className="inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors duration-200"
          >
            <DownloadIcon className="h-5 w-5 mr-2" />
            Download PDF
          </a>
        </div>
      );
    }
    console.log("File type not supported for rendering:", fullFileUrl);
    return null;
  };

  if (loading) return <p className="text-center mt-5 text-gray-600">Loading blog...</p>;
  if (!blog) return <p className="text-center mt-5 text-red-500">Blog not found.</p>;

  return (
    <div className="min-h-screen bg-gradient-to-r from-gray-50 to-blue-50 p-5">
      <style>{mediaStyles}</style>

      {error && <div className="mb-4 p-3 bg-red-100 text-red-700 rounded-md text-center">{error}</div>}

      <div className="max-w-3xl mx-auto bg-white shadow-lg rounded-lg p-5">
        <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.5 }}>
          <h2 className="text-4xl font-bold text-gray-800 mb-2">{blog.title}</h2>
          <p className="text-gray-600 mb-4 whitespace-pre-wrap">{blog.content}</p>

          {blog.fileUrl ? (
            renderMedia(blog.fileUrl)
          ) : (
            <p className="text-gray-500 italic">No media available for this blog.</p>
          )}

          <p className="text-sm text-blue-600 mb-4">
            By:{" "}
            <Link to={`/profile/${blog.author?.username || 'unknown'}`} className="font-semibold hover:underline">
              {blog.author?.username || "Unknown"}
            </Link>{" "}
            | Posted:{" "}
            <span className="font-semibold">
              {blog.createdAt ? new Date(blog.createdAt).toLocaleString() : "Unknown"}
            </span>
          </p>

          <div className="flex space-x-4 mb-6">
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => handleVote("upvote")}
              disabled={voting}
              className={`flex items-center px-4 py-2 rounded-lg transition-colors duration-200 ${
                voting ? "bg-green-300 cursor-not-allowed" : "bg-green-500 hover:bg-green-600"
              } text-white`}
            >
              <ThumbUpIcon className="h-5 w-5 mr-2" />
              Upvote ({blog.upvotes || 0})
            </motion.button>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => handleVote("downvote")}
              disabled={voting}
              className={`flex items-center px-4 py-2 rounded-lg transition-colors duration-200 ${
                voting ? "bg-red-300 cursor-not-allowed" : "bg-red-500 hover:bg-red-600"
              } text-white`}
            >
              <ThumbDownIcon className="h-5 w-5 mr-2" />
              Downvote ({blog.downvotes || 0})
            </motion.button>
          </div>
        </motion.div>
      </div>

      <div className="max-w-3xl mx-auto text-center mt-8">
        <Link to="/blog" className="text-blue-600 hover:underline font-semibold">
          ← Back to Blog Hub
        </Link>
      </div>
    </div>
  );
};

export default BlogPage;
