import React, { useEffect, useState, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../api/axiosInstance";

// Backend base URL (adjust based on your environment)
const BACKEND_BASE_URL = "http://localhost:5001"; // Change to your backend URL in production

function getToken() {
  const token = localStorage.getItem("token");
  console.log("Retrieved token:", token);
  return token;
}

const ProfilePage = () => {
  const { username } = useParams();
  const to = useNavigate();

  const [profileUser, setProfileUser] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);
  const [profileError, setProfileError] = useState("");
  const [newBio, setNewBio] = useState("");
  const [newProfilePicture, setNewProfilePicture] = useState(null);
  const [isEditingBio, setIsEditingBio] = useState(false);
  const [isEditingProfilePicture, setIsEditingProfilePicture] = useState(false);

  const token = getToken();

  // Fetch the current user's profile (same logic as Navbar)
  const fetchCurrentUser = useCallback(async () => {
    if (!token) return;
    try {
      const res = await API.get("/users/profile/me", {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log("Fetched current user:", res.data);
      setCurrentUser(res.data);
    } catch (err) {
      console.error("Error fetching current user profile:", err);
      setCurrentUser(null);
      setProfileError(err.response?.data?.message || "Error fetching current user");
    }
  }, [token]);

  // Fetch the profile of the user being viewed
  const fetchProfile = useCallback(async () => {
    try {
      const res = await API.get(`/users/profile/${username}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log("Fetched profile data:", res.data);
      setProfileUser(res.data);
      setNewBio(res.data.bio || "");
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error fetching profile");
      console.error("Full error:", err.response || err);
    }
  }, [username, token]);

  // Update bio
  const handleChangeBio = async () => {
    try {
      if (!token) {
        throw new Error("No authentication token found");
      }
      if (typeof newBio !== "string") {
        throw new Error("Bio must be a string");
      }
      const response = await API.put(
        "/users/profile/update-bio",
        { bio: newBio },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      console.log("Bio update response:", response.data);
      setIsEditingBio(false);
      fetchProfile();
    } catch (err) {
      console.error("Error updating bio:", err);
      setProfileError(err.response?.data?.message || err.message || "Error updating bio");
    }
  };

  // Update profile picture
  const handleChangeProfilePicture = async () => {
    if (!newProfilePicture) {
      setProfileError("Please select a file to upload.");
      return;
    }

    const formData = new FormData();
    formData.append("profilePicture", newProfilePicture);

    try {
      if (!token) {
        throw new Error("No authentication token found");
      }
      const response = await API.put("/users/profile/update-profile-picture", formData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      console.log("Profile picture update response:", response.data);
      setIsEditingProfilePicture(false);
      setNewProfilePicture(null);
      fetchProfile();
    } catch (err) {
      console.error("Error updating profile picture:", err);
      setProfileError(err.response?.data?.message || err.message || "Error updating profile picture");
    }
  };

  useEffect(() => {
    fetchCurrentUser();
    fetchProfile();
  }, [fetchCurrentUser, fetchProfile]);

  const isOwnProfile = currentUser && profileUser && currentUser._id === profileUser._id;
  console.log("isOwnProfile:", isOwnProfile);
  console.log("currentUser:", currentUser);
  console.log("profileUser:", profileUser);

  return (
    <div className="min-h-screen bg-gray-900 text-white flex justify-center items-center">
      <div className="w-full max-w-md p-6 bg-gray-800 rounded-lg shadow-lg">
        {/* Error Message */}
        {profileError && (
          <div className="bg-red-600 text-white p-3 rounded mb-4">
            {profileError}
          </div>
        )}

        {/* Profile Information */}
        {profileUser ? (
          <div className="text-center">
            <img
              src={
                profileUser.profilePicture
                  ? `${BACKEND_BASE_URL}${profileUser.profilePicture}`
                  : `${BACKEND_BASE_URL}/images/default-avatar.png`
              }
              alt="Profile"
              className="w-24 h-24 rounded-full mx-auto mb-4 border-2 border-gray-600"
              onError={(e) => {
                console.error("Profile picture failed to load:", profileUser.profilePicture);
                e.target.src = `${BACKEND_BASE_URL}/images/default-avatar.png`;
              }}
            />
            <h1 className="text-2xl font-bold mb-2">{profileUser.username}</h1>
            <p className="text-gray-400 mb-4">{profileUser.bio || "No bio yet."}</p>

            {/* Actions for Own Profile */}
            {isOwnProfile ? (
              <div className="space-y-4">
                {/* Change Bio */}
                {isEditingBio ? (
                  <div className="space-y-2">
                    <textarea
                      value={newBio}
                      onChange={(e) => setNewBio(e.target.value)}
                      className="w-full p-2 bg-gray-700 text-white rounded border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
                      rows="3"
                      placeholder="Enter your new bio..."
                    />
                    <div className="flex space-x-2">
                      <button
                        onClick={handleChangeBio}
                        className="flex-1 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
                      >
                        Save
                      </button>
                      <button
                        onClick={() => setIsEditingBio(false)}
                        className="flex-1 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                ) : (
                  <button
                    onClick={() => setIsEditingBio(true)}
                    className="w-full bg-gray-700 text-white px-4 py-2 rounded hover:bg-gray-600"
                  >
                    Change Bio
                  </button>
                )}

                {/* Change Profile Picture */}
                {isEditingProfilePicture ? (
                  <div className="space-y-2">
                    <input
                      type="file"
                      accept="image/*"
                      onChange={(e) => setNewProfilePicture(e.target.files[0])}
                      className="w-full p-2 bg-gray-700 text-white rounded border border-gray-600"
                    />
                    <div className="flex space-x-2">
                      <button
                        onClick={handleChangeProfilePicture}
                        className="flex-1 bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
                      >
                        Upload
                      </button>
                      <button
                        onClick={() => setIsEditingProfilePicture(false)}
                        className="flex-1 bg-red-600 text-white px-4 py-2 rounded hover:bg-red-700"
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                ) : (
                  <button
                    onClick={() => setIsEditingProfilePicture(true)}
                    className="w-full bg-gray-700 text-white px-4 py-2 rounded hover:bg-gray-600"
                  >
                    Change Profile Picture
                  </button>
                )}
              </div>
            ) : (
              <p className="text-gray-400">This is not your profile.</p>
            )}
          </div>
        ) : (
          <p className="text-center text-gray-400">Loading...</p>
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
