import { useEffect, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import API from "../api";
import { LockClosedIcon } from "@heroicons/react/solid";

const Home = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const urlParams = new URLSearchParams(location.search);
  const defaultSection = urlParams.get("section") || "discussions";

  const [activeSection, setActiveSection] = useState(defaultSection);
  const [discussions, setDiscussions] = useState([]);
  const [debates, setDebates] = useState([]);
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setActiveSection(defaultSection);
  }, [location.search]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [discussionsData, debatesData, blogsData] = await Promise.all([
          API.get("/discussions"),
          API.get("/debates"),
          API.get("/blogs"),
        ]);
        setDiscussions(Array.isArray(discussionsData.data) ? discussionsData.data : []);
        setDebates(Array.isArray(debatesData.data) ? debatesData.data : []);
        setBlogs(Array.isArray(blogsData.data) ? blogsData.data : []);
      } catch (err) {
        console.error("Error fetching data", err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleSectionChange = (section) => {
    navigate(`/?section=${section}`);
  };

  const sortAndSlice = (items, type) => {
    const sorted = [...items].sort((a, b) => {
      const popA = (a.comments?.length || 0) + (a.upvotes?.length || 0) + (a.downvotes?.length || 0);
      const popB = (b.comments?.length || 0) + (b.upvotes?.length || 0) + (b.downvotes?.length || 0);
      return popB - popA;
    });
    return sorted.slice(0, 9);
  };

  const renderDiscussionCard = (discussion) => (
    <Link to={`/discussionpage/${discussion._id}?section=discussions`} key={discussion._id} className="relative group rounded-lg bg-white shadow hover:shadow-lg overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-t from-blue-500/80 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
      <div className="p-4 md:p-6 relative z-10">
        <h3 className="text-lg md:text-xl font-semibold text-gray-800 group-hover:text-white">{discussion.title || "Untitled Discussion"}</h3>
        <p className="text-gray-600 group-hover:text-gray-200 mt-2 text-sm md:text-base">
          {discussion.description ? discussion.description.substring(0, 80) + "..." : "No description"}
        </p>
        <p className="text-xs md:text-sm text-blue-500 group-hover:text-yellow-300 mt-3 font-medium">{discussion.category || "Uncategorized"}</p>
        <p className="text-xs md:text-sm text-gray-500 group-hover:text-gray-300 mt-1">By: <Link to={`/profile/${discussion.author?.username || "unknown"}`} className="hover:underline">{discussion.author?.username || "Unknown"}</Link> {discussion.isPrivate && <LockClosedIcon className="h-3 w-3 md:h-4 md:w-4 inline ml-1 text-red-500" />}</p>
      </div>
    </Link>
  );

  const renderDebateCard = (debate) => (
    <Link to={`/debatepage/${debate._id}?section=debates`} key={debate._id} className="block px-4 py-3 border border-gray-200 rounded-lg shadow hover:shadow-lg hover:bg-gradient-to-r hover:from-teal-500/20 hover:to-blue-500/20 transition-colors duration-200">
      <h3 className="text-gray-800 font-semibold text-sm md:text-base">{debate.title || "Untitled Debate"}</h3>
      <p className="text-gray-600 text-xs md:text-sm truncate">{debate.openingArgument ? `${debate.openingArgument.substring(0, 30)}...` : "No argument"}</p>
      <div className="flex justify-between text-xs md:text-sm mt-1">
        <span className="text-teal-500">{debate.category || "Uncategorized"}</span>
        <span className="text-gray-500">By: <Link to={`/profile/${debate.author?.username || "unknown"}`} className="hover:underline">{debate.author?.username || "Unknown"}</Link> {debate.isPrivate && <LockClosedIcon className="h-3 w-3 md:h-4 md:w-4 inline ml-1 text-red-500" />}</span>
      </div>
    </Link>
  );

  const renderBlogCard = (blog) => (
    <Link to={`/blogpage/${blog._id}?section=blogs`} key={blog._id} className="relative group rounded-lg bg-white shadow hover:shadow-lg overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-t from-blue-500/80 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
      <div className="p-4 md:p-6 relative z-10">
        <h3 className="text-lg md:text-xl font-semibold text-gray-800 group-hover:text-white">{blog.title || "Untitled Blog"}</h3>
        <p className="text-gray-600 group-hover:text-gray-200 mt-2 text-sm md:text-base">{blog.content ? blog.content.substring(0, 80) + "..." : "No content available"}</p>
        {blog.fileUrl && <p className="mt-2 text-xs md:text-sm text-blue-500 group-hover:text-yellow-300">{blog.fileUrl.endsWith(".pdf") ? "PDF Attached" : "Media Attached"}</p>}
        <div className="mt-3 flex justify-between items-center">
          <span className="text-xs md:text-sm text-blue-500 group-hover:text-yellow-300 font-medium">Votes: {(blog.upvotes || 0) - (blog.downvotes || 0)}</span>
          <span className="text-xs md:text-sm text-gray-500 group-hover:text-gray-300">By: <Link to={`/profile/${blog.author?.username || "unknown"}`} className="hover:underline">{blog.author?.username || "Unknown"}</Link> {blog.isPrivate && <LockClosedIcon className="h-3 w-3 md:h-4 md:w-4 inline ml-1 text-red-500" />}</span>
        </div>
      </div>
    </Link>
  );

  return (
    <div className="p-4 md:ml-64">
      <div className="flex flex-col md:flex-row gap-4 mb-6">
        <button onClick={() => handleSectionChange("discussions")} className={`py-2 px-4 rounded ${activeSection === "discussions" ? "bg-blue-600 text-white" : "bg-gray-200"}`}>Discussions</button>
        <button onClick={() => handleSectionChange("debates")} className={`py-2 px-4 rounded ${activeSection === "debates" ? "bg-teal-600 text-white" : "bg-gray-200"}`}>Debates</button>
        <button onClick={() => handleSectionChange("blogs")} className={`py-2 px-4 rounded ${activeSection === "blogs" ? "bg-purple-600 text-white" : "bg-gray-200"}`}>Blogs</button>
      </div>

      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {activeSection === "discussions" &&
            sortAndSlice(discussions, "discussion").map((discussion) => renderDiscussionCard(discussion))}
          {activeSection === "debates" &&
            sortAndSlice(debates, "debate").map((debate) => renderDebateCard(debate))}
          {activeSection === "blogs" &&
            sortAndSlice(blogs, "blog").map((blog) => renderBlogCard(blog))}
        </div>
      )}

      {/* Show More Button */}
      <div className="mt-8 text-center">
        {activeSection === "discussions" && <Link to="/discussions" className="text-blue-600 hover:underline">See More Discussions</Link>}
        {activeSection === "debates" && <Link to="/debates" className="text-teal-600 hover:underline">See More Debates</Link>}
        {activeSection === "blogs" && <Link to="/blogs" className="text-purple-600 hover:underline">See More Blogs</Link>}
      </div>
    </div>
  );
};

export default Home;
