import React, { useEffect, useState, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance";
import { ChatIcon, ArrowLeftIcon, ArrowRightIcon, SearchIcon } from "@heroicons/react/outline";

const Home = () => {
  const navigate = useNavigate();
  const [discussions, setDiscussions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sortBy, setSortBy] = useState("latest"); // Options: "latest", "oldest", "popularity"
  const [currentPage, setCurrentPage] = useState(0); // Page index for pagination
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredDiscussions, setFilteredDiscussions] = useState([]);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const discussionsPerPage = 9;

  useEffect(() => {
    const fetchDiscussions = async () => {
      try {
        setLoading(true);
        const { data } = await API.get("/discussions");
        setDiscussions(Array.isArray(data) ? data : []);
      } catch (error) {
        console.error("Error fetching discussions:", error);
        setDiscussions([]);
      } finally {
        setLoading(false);
      }
    };
    fetchDiscussions();
  }, []);

  useEffect(() => {
    if (searchQuery.trim() === "") {
      setFilteredDiscussions([]);
      setIsDropdownOpen(false);
      return;
    }

    const lowerQuery = searchQuery.toLowerCase();
    const results = discussions.filter((discussion) => {
      const title = discussion.title || "";
      const description = discussion.description || "";
      const category = discussion.category || "";
      const username = discussion.author?.username || "";

      return (
        title.toLowerCase().includes(lowerQuery) ||
        description.toLowerCase().includes(lowerQuery) ||
        category.toLowerCase().includes(lowerQuery) ||
        username.toLowerCase().includes(lowerQuery)
      );
    });

    setFilteredDiscussions(results);
    setIsDropdownOpen(results.length > 0);
  }, [searchQuery, discussions]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  // Sort discussions based on selected criteria
  const sortedDiscussions = [...discussions].sort((a, b) => {
    if (sortBy === "latest") return new Date(b.createdAt) - new Date(a.createdAt);
    if (sortBy === "oldest") return new Date(a.createdAt) - new Date(b.createdAt);
    if (sortBy === "popularity") {
      const popularityA =
        (a.comments?.length || 0) + (a.upvotes?.length || 0) + (a.downvotes?.length || 0);
      const popularityB =
        (b.comments?.length || 0) + (b.upvotes?.length || 0) + (b.downvotes?.length || 0);
      return popularityB - popularityA;
    }
    return 0;
  });

  // Paginate discussions for grid layout
  const totalPages = Math.ceil(sortedDiscussions.length / discussionsPerPage);
  const paginatedDiscussions = sortedDiscussions.slice(
    currentPage * discussionsPerPage,
    (currentPage + 1) * discussionsPerPage
  );

  // Handle page navigation
  const handlePrevPage = () => {
    if (currentPage > 0) setCurrentPage(currentPage - 1);
  };

  const handleNextPage = () => {
    if (currentPage < totalPages - 1) setCurrentPage(currentPage + 1);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (filteredDiscussions.length > 0) {
      navigate(`/discussionpage/${filteredDiscussions[0]._id}`);
      setSearchQuery("");
      setIsDropdownOpen(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100">
      {/* Hero Section */}
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="bg-gradient-to-r from-blue-600 to-purple-600 text-white py-16 px-5 text-center shadow-lg relative"
      >
        {/* Search Bar - Positioned in Top Right */}
        <div className="absolute top-4 right-5" ref={dropdownRef}>
          <form onSubmit={handleSearchSubmit} className="flex items-center">
            <motion.div whileHover={{ scale: 1.02 }} transition={{ duration: 0.3 }}>
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="Search discussions..."
                className="px-4 py-2 pr-10 w-64 bg-white/20 backdrop-blur-md text-white placeholder-gray-200 rounded-xl border border-white/30 focus:outline-none focus:ring-2 focus:ring-yellow-300 transition-all duration-300"
              />
              <button
                type="submit"
                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-yellow-300 hover:text-yellow-200"
              >
                <SearchIcon className="h-5 w-5" />
              </button>
            </motion.div>
          </form>

          {/* Dropdown for Search Results */}
          {isDropdownOpen && (
            <motion.div
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
              className="absolute top-14 right-0 w-64 bg-white rounded-xl shadow-lg max-h-80 overflow-y-auto z-40"
            >
              {filteredDiscussions.map((discussion) => (
                <Link
                  key={discussion._id}
                  to={`/discussionpage/${discussion._id}`}
                  onClick={() => {
                    setSearchQuery("");
                    setIsDropdownOpen(false);
                  }}
                  className="block px-4 py-3 border-b border-gray-200 hover:bg-gradient-to-r hover:from-blue-500/20 hover:to-purple-500/20 transition-colors duration-200"
                >
                  <h3 className="text-gray-800 font-semibold">
                    {discussion.title || "Untitled Discussion"}
                  </h3>
                  <p className="text-gray-600 text-sm truncate">
                    {discussion.description
                      ? `${discussion.description.substring(0, 50)}...`
                      : "No description available"}
                  </p>
                  <div className="flex justify-between text-sm mt-1">
                    <span className="text-blue-500">
                      Category: {discussion.category || "Uncategorized"}
                    </span>
                    <span className="text-gray-500">
                      By: {discussion.author?.username || "Unknown"}
                    </span>
                  </div>
                </Link>
              ))}
            </motion.div>
          )}
        </div>

        <motion.div
          animate={{ scale: [1, 1.05, 1], opacity: [0.8, 1, 0.8] }}
          transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
          className="inline-block"
        >
          <ChatIcon className="h-16 w-16 mx-auto mb-4 text-yellow-300" />
        </motion.div>
        <h1 className="text-5xl md:text-6xl font-extrabold tracking-tight mb-4">
          Welcome to <span className="text-yellow-300">Debatify!</span>
        </h1>
        <p className="text-lg md:text-xl max-w-2xl mx-auto opacity-90">
          Join the conversation, share your opinions, and engage in meaningful discussions/debates on topics that matter to you.
        </p>
        <div className="mt-6 flex justify-center gap-4">
          <Link
            to="/createDiscussion"
            className="inline-block px-8 py-3 bg-yellow-400 text-blue-900 font-semibold rounded-full shadow-lg hover:bg-yellow-500 transition-all duration-300 transform hover:scale-105"
          >
            Start a Discussion Now
          </Link>
          <Link
            to="/debate" // Link to DebateHub page
            className="inline-block px-8 py-3 bg-blue-500 text-white font-semibold rounded-full shadow-lg hover:bg-blue-600 transition-all duration-300 transform hover:scale-105"
          >
            Go to Debate Hub
          </Link>
          <Link
            to="/blog"
            className="inline-block px-8 py-3 bg-green-500 text-white font-semibold rounded-full shadow-lg hover:bg-blue-600 transition-all duration-300 transform hover:scale-105"
          >
            Blog Hub
          </Link>
        </div>
      </motion.div>

      {/* Filters and Discussions Section */}
      <div className="p-5 md:p-10 max-w-6xl mx-auto">
        <motion.h2
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="text-4xl font-bold text-gray-800 mb-8 text-center"
        >
          Ongoing Discussions
        </motion.h2>

        {/* Filter Buttons */}
        <div className="flex flex-wrap justify-between items-center mb-8">
          <div className="space-x-4">
            <button
              onClick={() => setSortBy("latest")}
              className={`px-4 py-2 rounded-lg transition-all duration-300 ${
                sortBy === "latest"
                  ? "bg-blue-600 text-white"
                  : "bg-gray-200 text-gray-800 hover:bg-gray-300"
              }`}
            >
              Latest
            </button>
            <button
              onClick={() => setSortBy("oldest")}
              className={`px-4 py-2 rounded-lg transition-all duration-300 ${
                sortBy === "oldest"
                  ? "bg-blue-600 text-white"
                  : "bg-gray-200 text-gray-800 hover:bg-gray-300"
              }`}
            >
              Oldest
            </button>
            <button
              onClick={() => setSortBy("popularity")}
              className={`px-4 py-2 rounded-lg transition-all duration-300 ${
                sortBy === "popularity"
                  ? "bg-blue-600 text-white"
                  : "bg-gray-200 text-gray-800 hover:bg-gray-300"
              }`}
            >
              Popularity
            </button>
          </div>

          {/* Pagination Controls */}
          <div className="flex space-x-4 items-center mt-4 sm:mt-0">
            <button
              onClick={handlePrevPage}
              disabled={currentPage === 0}
              className="px-3 py-1 rounded-full bg-gray-200 text-gray-800 hover:bg-gray-300 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <ArrowLeftIcon className="h-5 w-5" />
            </button>
            <span className="text-gray-700">
              Page {currentPage + 1} of {totalPages}
            </span>
            <button
              onClick={handleNextPage}
              disabled={currentPage === totalPages - 1}
              className="px-3 py-1 rounded-full bg-gray-200 text-gray-800 hover:bg-gray-300 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed"
            >
              <ArrowRightIcon className="h-5 w-5" />
            </button>
          </div>
        </div>

        {/* Discussions Grid */}
        {loading ? (
          <div className="text-center">
            <motion.div
              animate={{ rotate: 360 }}
              transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
              className="inline-block h-8 w-8 border-4 border-blue-500 border-t-transparent rounded-full"
            />
            <p className="text-gray-600 mt-2">Loading discussions...</p>
          </div>
        ) : discussions.length === 0 ? (
          <motion.p
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
            className="text-center text-gray-500 text-lg"
          >
            No discussions yet. Be the first to start one!
          </motion.p>
        ) : (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5, delay: 0.4 }}
            className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3"
          >
            {paginatedDiscussions.map((discussion, index) => (
              <motion.div
                key={discussion._id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                whileHover={{ scale: 1.03, transition: { duration: 0.2 } }}
                className="relative group bg-white rounded-xl shadow-lg overflow-hidden transform transition-all duration-300 hover:shadow-xl"
              >
                <Link to={`/discussionpage/${discussion._id}`}>
                  {/* Gradient Overlay on Hover */}
                  <div className="absolute inset-0 bg-gradient-to-t from-blue-500/80 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
                  <div className="p-6 relative z-10">
                    <h3 className="text-xl font-semibold text-gray-800 group-hover:text-white transition-colors duration-300">
                      {discussion.title}
                    </h3>
                    <p className="text-gray-600 group-hover:text-gray-200 mt-2 transition-colors duration-300">
                      {discussion.description.substring(0, 100)}...
                    </p>
                    <p className="text-sm text-blue-500 group-hover:text-yellow-300 mt-3 font-medium transition-colors duration-300">
                      Category: {discussion.category}
                    </p>
                    <p className="text-sm text-gray-500 group-hover:text-gray-300 mt-1 transition-colors duration-300">
                      By: {discussion.author?.username || "Unknown"}
                    </p>
                  </div>
                </Link>
              </motion.div>
            ))}
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default Home;
