import React, { useEffect, useState, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance";
import { PencilIcon, ArrowLeftIcon, ArrowRightIcon, SearchIcon, LockClosedIcon } from "@heroicons/react/outline";

const Blog = () => {
  const navigate = useNavigate();
  const [blogs, setBlogs] = useState([]);
  const [filteredBlogs, setFilteredBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sortBy, setSortBy] = useState("latest");
  const [currentPage, setCurrentPage] = useState(0);
  const [searchQuery, setSearchQuery] = useState("");
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const blogsPerPage = 9;

  // [Previous useEffect hooks remain unchanged...]

  const sortedBlogs = [...filteredBlogs].sort((a, b) => {
    if (sortBy === "latest") return new Date(b.createdAt) - new Date(a.createdAt);
    if (sortBy === "oldest") return new Date(a.createdAt) - new Date(b.createdAt);
    if (sortBy === "popularity") {
      const popularityA = (a.upvotes || 0) + (a.downvotes || 0);
      const popularityB = (b.upvotes || 0) + (b.downvotes || 0);
      return popularityB - popularityA;
    }
    return 0;
  });

  const totalPages = Math.ceil(sortedBlogs.length / blogsPerPage);
  const paginatedBlogs = sortedBlogs.slice(currentPage * blogsPerPage, (currentPage + 1) * blogsPerPage);

  const handlePrevPage = () => {
    if (currentPage > 0) setCurrentPage(currentPage - 1);
  };

  const handleNextPage = () => {
    if (currentPage < totalPages - 1) setCurrentPage(currentPage + 1);
  };

  const handleSearchSubmit = (e) => {
    e.preventDefault();
    if (filteredBlogs.length > 0) {
      navigate(`/blogs/${filteredBlogs[0]._id}`);
      setSearchQuery("");
      setIsDropdownOpen(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="bg-blue-500 text-white py-12 px-5">
        <div className="max-w-6xl mx-auto relative">
          <div className="absolute top-0 right-0" ref={dropdownRef}>
            <form onSubmit={handleSearchSubmit} className="flex items-center">
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="Search blogs..."
                className="px-4 py-2 w-64 bg-white text-gray-800 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-300"
              />
              <button type="submit" className="ml-2 text-white">
                <SearchIcon className="h-5 w-5" />
              </button>
            </form>
            {isDropdownOpen && (
              <div className="absolute top-12 right-0 w-64 bg-white rounded-lg shadow-lg max-h-80 overflow-y-auto">
                {filteredBlogs.map((blog) => (
                  <Link
                    key={blog._id}
                    to={`/blogs/${blog._id}`}
                    onClick={() => {
                      setSearchQuery("");
                      setIsDropdownOpen(false);
                    }}
                    className="block px-4 py-3 border-b border-gray-200 hover:bg-gray-100"
                  >
                    <h3 className="text-gray-800 font-medium">{blog.title || "Untitled Blog"}</h3>
                    <p className="text-gray-600 text-sm truncate">
                      {blog.content ? `${blog.content.substring(0, 50)}...` : "No content available"}
                    </p>
                  </Link>
                ))}
              </div>
            )}
          </div>
          <PencilIcon className="h-12 w-12 mx-auto mb-4" />
          <h1 className="text-4xl font-bold text-center">Blog Hub</h1>
          <p className="text-center mt-2">Explore and share ideas through blogs</p>
          <div className="mt-6 flex justify-center gap-4">
            <Link to="/createblog" className="px-6 py-2 bg-white text-blue-500 rounded-lg hover:bg-gray-100">
              Write a Blog
            </Link>
            <Link to="/" className="px-6 py-2 bg-white text-blue-500 rounded-lg hover:bg-gray-100">
              Home
            </Link>
          </div>
        </div>
      </div>

      <div className="max-w-6xl mx-auto p-5">
        <h2 className="text-3xl font-bold text-gray-800 mb-6 text-center">Latest Blogs</h2>
        <div className="flex justify-between items-center mb-6">
          <div className="space-x-2">
            <button
              onClick={() => setSortBy("latest")}
              className={`px-3 py-1 rounded-lg ${sortBy === "latest" ? "bg-blue-500 text-white" : "bg-gray-200"}`}
            >
              Latest
            </button>
            <button
              onClick={() => setSortBy("oldest")}
              className={`px-3 py-1 rounded-lg ${sortBy === "oldest" ? "bg-blue-500 text-white" : "bg-gray-200"}`}
            >
              Oldest
            </button>
            <button
              onClick={() => setSortBy("popularity")}
              className={`px-3 py-1 rounded-lg ${sortBy === "popularity" ? "bg-blue-500 text-white" : "bg-gray-200"}`}
            >
              Popularity
            </button>
          </div>
          <div className="flex items-center gap-2">
            <button onClick={handlePrevPage} disabled={currentPage === 0} className="p-1 bg-gray-200 rounded-full">
              <ArrowLeftIcon className="h-5 w-5" />
            </button>
            <span>Page {currentPage + 1} of {totalPages}</span>
            <button onClick={handleNextPage} disabled={currentPage === totalPages - 1} className="p-1 bg-gray-200 rounded-full">
              <ArrowRightIcon className="h-5 w-5" />
            </button>
          </div>
        </div>

        {loading ? (
          <p className="text-center text-gray-600">Loading...</p>
        ) : sortedBlogs.length === 0 ? (
          <p className="text-center text-gray-600">No blogs available</p>
        ) : (
          <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
            {paginatedBlogs.map((blog) => (
              <Link
                key={blog._id}
                to={`/blogpage/${blog._id}`}
                className="bg-white p-5 rounded-lg shadow hover:shadow-md"
              >
                <h3 className="text-xl font-medium text-gray-800">{blog.title || "Untitled Blog"}</h3>
                <p className="text-gray-600 mt-2">
                  {blog.content ? blog.content.substring(0, 100) + "..." : "No content"}
                </p>
                <div className="mt-3 text-sm text-gray-500">
                  By: {blog.author?.username || "Unknown"}
                  {blog.isPrivate && <LockClosedIcon className="h-4 w-4 inline ml-1" />}
                </div>
              </Link>
            ))}
          </div>
        )}
      </div>

      <div className="bg-blue-500 py-4" />
    </div>
  );
};

export default Blog;
