import { Link } from "react-router-dom";
import {
  HomeIcon,
  ChatAlt2Icon,
  UsersIcon,
  BookOpenIcon,
  UserCircleIcon,
} from "@heroicons/react/outline";

const user = JSON.parse(localStorage.getItem("user"));


const MobileFooter = () => {
  return (
    <div className="fixed bottom-0 w-full bg-white border-t shadow-lg md:hidden z-50">
      <div className="flex justify-between items-center px-6 py-3">
        <Link to="/" className="flex flex-col items-center text-gray-600 hover:text-blue-600">
          <HomeIcon className="h-6 w-6" />
          <span className="text-xs mt-1">Home</span>
        </Link>

        <Link to="/discussion" className="flex flex-col items-center text-gray-600 hover:text-blue-600">
          <ChatAlt2Icon className="h-6 w-6" />
          <span className="text-xs mt-1">Discussions</span>
        </Link>

        <Link to="/debate" className="flex flex-col items-center text-gray-600 hover:text-blue-600">
          <UsersIcon className="h-6 w-6" />
          <span className="text-xs mt-1">Debates</span>
        </Link>

        <Link to="/blog" className="flex flex-col items-center text-gray-600 hover:text-blue-600">
          <BookOpenIcon className="h-6 w-6" />
          <span className="text-xs mt-1">Blogs</span>
        </Link>

        <Link to={`/profile/${user?.username}`} className="flex flex-col items-center text-gray-600 hover:text-blue-600">
          <UserCircleIcon className="h-6 w-6" />
          <span className="text-xs mt-1">Profile</span>
        </Link>
      </div>
    </div>
  );
};

export default MobileFooter;
