// src/components/Chat.js
import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../api/axiosInstance";
import io from "socket.io-client";

const socket = io("http://localhost:5001", {
  reconnection: true,
  reconnectionAttempts: 5,
});

const Chat = () => {
  const { userId } = useParams(); // The ID of the user to chat with
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const currentUserId = localStorage.getItem("userId");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token || !currentUserId) {
      navigate("/login");
      return;
    }

    // Fetch initial chat history
    const fetchChatHistory = async () => {
      try {
        const res = await API.get(`/history/${userId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        console.log("Chat history:", res.data); // Debug
        setMessages(res.data);
        setLoading(false);

        // Mark messages as seen
        await API.put(
          "/mark-seen",
          { sender: userId },
          { headers: { Authorization: `Bearer ${token}` } }
        );
      } catch (err) {
        console.error("Error fetching chat history:", err.response?.data || err.message);
        setLoading(false);
      }
    };

    fetchChatHistory();

    // Join chat room
    const room = [currentUserId, userId].sort().join("-");
    socket.emit("joinChat", { senderId: currentUserId, receiverId: userId });

    // Listen for new messages
    socket.on("message", (newMsg) => {
      setMessages((prev) => {
        // Avoid duplicates
        if (!prev.some((msg) => msg._id === newMsg._id)) {
          return [...prev, newMsg];
        }
        return prev;
      });
    });

    // Cleanup on unmount
    return () => {
      socket.off("message");
    };
  }, [userId, navigate, currentUserId]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim()) return;

    const token = localStorage.getItem("token");
    try {
      const messageData = {
        receiver: userId,
        text: newMessage,
        sender: currentUserId,
      };
      const res = await API.post("/send", messageData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessages((prev) => [...prev, res.data]);
      socket.emit("sendMessage", res.data); // Emit to Socket.IO
      setNewMessage("");
    } catch (err) {
      console.error("Error sending message:", err.response?.data || err.message);
    }
  };

  if (loading) return <div className="p-4">Loading...</div>;

  return (
    <div className="max-w-4xl mx-auto p-4 mt-16">
      <h1 className="text-2xl font-bold mb-4">Chat</h1>
      <div className="bg-white p-4 rounded-lg shadow max-h-[60vh] overflow-y-auto">
        {messages.length === 0 ? (
          <p className="text-gray-500">No messages yet.</p>
        ) : (
          messages.map((msg) => (
            <div
              key={msg._id}
              className={`mb-4 flex ${
                msg.sender._id === currentUserId ? "justify-end" : "justify-start"
              }`}
            >
              <div
                className={`max-w-xs p-3 rounded-lg ${
                  msg.sender._id === currentUserId ? "bg-blue-500 text-white" : "bg-gray-200 text-black"
                }`}
              >
                <p className="font-semibold">{msg.sender.username}</p>
                {msg.text && <p>{msg.text}</p>}
                {msg.image && (
                  <img
                    src={msg.image}
                    alt="Chat attachment"
                    className="max-w-full h-auto mt-2 rounded"
                  />
                )}
                <p className="text-xs mt-1 opacity-75">
                  {new Date(msg.timestamp).toLocaleString()}
                </p>
              </div>
            </div>
          ))
        )}
      </div>
      <form onSubmit={handleSendMessage} className="mt-4 flex space-x-2">
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Type a message..."
          className="flex-1 p-2 border rounded-lg focus:outline-none"
        />
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
        >
          Send
        </button>
      </form>
    </div>
  );
};

export default Chat;
