import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance";
import { useAuth } from ".context/AuthContext";
import { LockClosedIcon, UserIcon } from "@heroicons/react/outline";

const Auth = () => {
  const [form, setForm] = useState({ username: "", email: "", password: "" });
  const [isLogin, setIsLogin] = useState(true);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const navigate = useNavigate();
  const login = useAuth();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const endpoint = isLogin ? "/auth/login" : "/auth/signup";
      const { data } = await API.post(endpoint, form);
      login(data.token, data.name || "User"); 
      setSuccess(true);
      setTimeout(() => navigate("/"), 1500);
    } catch (error) {
      console.error("Error in authentication:", error.response?.data || error.message);
      alert(error.response?.data?.message || "Authentication failed!");
      setLoading(false);
    }
  };

  const toggleMode = () => {
    setIsLogin(!isLogin);
    setForm({ username: "", email: "", password: "" });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100 flex items-center justify-center p-4">
      <div className="ml-64 w-full flex justify-center">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, ease: "easeOut" }}
          className="bg-white rounded-2xl shadow-2xl p-6 w-full max-w-md transform transition-all duration-300 hover:shadow-3xl"
        >
          <motion.div initial={{ scale: 0 }} animate={{ scale: 1 }} transition={{ duration: 0.5 }} className="text-center">
            <h2 className="text-3xl font-extrabold text-gray-800 mb-6">
              {isLogin ? "Welcome Back" : "Create Account"}
              <span className="text-yellow-400">!</span>
            </h2>
            {isLogin ? (
              <LockClosedIcon className="h-12 w-12 text-blue-500 mx-auto mb-4 animate-pulse" />
            ) : (
              <UserIcon className="h-12 w-12 text-green-500 mx-auto mb-4 animate-pulse" />
            )}
          </motion.div>

          {success ? (
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5 }}
              className="text-center"
            >
              <LockClosedIcon className="h-20 w-20 text-green-500 mx-auto mb-4 animate-pulse" />
              <p className="text-xl font-semibold text-green-600">
                {isLogin ? "Logged In!" : "Account Created!"}
              </p>
              <p className="text-gray-500 mt-2">Redirecting to home...</p>
            </motion.div>
          ) : (
            <form onSubmit={handleSubmit} className="space-y-6">
              {!isLogin && (
                <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: 0.1, duration: 0.5 }}>
                  <label htmlFor="username" className="block text-sm font-medium text-gray-700 mb-1">
                    Username
                  </label>
                  <input
                    type="text"
                    id="username"
                    name="username"
                    placeholder="Choose a username"
                    value={form.username}
                    onChange={handleChange}
                    className="w-full px-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all duration-300"
                    required
                  />
                </motion.div>
              )}
              <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: isLogin ? 0.1 : 0.2, duration: 0.5 }}>
                <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
                  Email
                </label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  placeholder="Enter your email"
                  value={form.email}
                  onChange={handleChange}
                  className="w-full px-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all duration-300"
                  required
                />
              </motion.div>
              <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: isLogin ? 0.2 : 0.3, duration: 0.5 }}>
                <label htmlFor="password" className="block text-sm font-medium text-gray-700 mb-1">
                  Password
                </label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  placeholder="Enter your password"
                  value={form.password}
                  onChange={handleChange}
                  className="w-full px-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all duration-300"
                  required
                />
              </motion.div>
              <motion.button
                type="submit"
                disabled={loading}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                transition={{ duration: 0.3 }}
                className={`w-full bg-gradient-to-r from-${isLogin ? "blue-500" : "green-500"} to-${
                  isLogin ? "purple-600" : "emerald-600"
                } text-white p-2 rounded-xl shadow-md hover:from-${
                  isLogin ? "blue-600" : "green-600"
                } hover:to-${isLogin ? "purple-700" : "emerald-700"} transition-all duration-300 ${
                  loading ? "opacity-70 cursor-not-allowed" : ""
                }`}
              >
                {loading ? (
                  <span className="flex items-center justify-center">
                    <svg className="animate-spin h-5 w-5 mr-2 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path
                        className="opacity-75"
                        fill="currentColor"
                        d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                      ></path>
                    </svg>
                    {isLogin ? "Logging In..." : "Registering..."}
                  </span>
                ) : isLogin ? "Login" : "Register"}
              </motion.button>
              <motion.p
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.4, duration: 0.5 }}
                className="text-center text-yellow-500 cursor-pointer hover:text-yellow-400 transition-colors duration-300"
                onClick={toggleMode}
              >
                {isLogin ? "Don't have an account? Register" : "Already have an account? Login"}
              </motion.p>
            </form>
          )}
        </motion.div>
      </div>
    </div>
  );
};

export default Auth;
