import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import axios from 'axios';

const Navbar = () => {
  const [search, setSearch] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const user = useSelector((state) => state.user);
  const navigate = useNavigate();

  const handleSearch = async (e) => {
    const query = e.target.value;
    setSearch(query);
    if (query.trim() === '') {
      setSuggestions([]);
      return;
    }
    try {
      const res = await axios.get(`/api/users/search?query=${query}`);
      setSuggestions(res.data);
    } catch (err) {
      console.log(err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  return (
    <nav style={styles.navbar}>
      <div style={styles.left}>
        <Link to="/" style={styles.logo}>Debatify</Link>
      </div>

      <div style={styles.center}>
        <input
          type="text"
          placeholder="Search users..."
          value={search}
          onChange={handleSearch}
          style={styles.searchInput}
        />
        {suggestions.length > 0 && (
          <ul style={styles.suggestions}>
            {suggestions.map((user) => (
              <li
                key={user._id}
                style={styles.suggestionItem}
                onClick={() => navigate(`/profile/${user._id}`)}
              >
                {user.username}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div style={styles.right}>
        <Link to={`/profile/${user._id}`} style={styles.navItem}>Profile</Link>
        <Link to="/inbox" style={styles.navItem}>Inbox</Link>

        {/* Notification Bell */}
        <div style={styles.notificationBell}>
          <i className="fas fa-bell" style={styles.bellIcon}></i>
          <span style={styles.notificationCount}>3</span>
        </div>

        <button onClick={handleLogout} style={styles.logoutBtn}>Logout</button>
      </div>
    </nav>
  );
};

const styles = {
  navbar: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    backgroundColor: '#242526',
    padding: '10px 20px',
    color: '#fff',
    position: 'relative',
    zIndex: 1000,
  },
  left: {
    flex: 1,
  },
  logo: {
    color: '#fff',
    textDecoration: 'none',
    fontSize: '26px',
    fontWeight: 'bold',
  },
  center: {
    flex: 2,
    position: 'relative',
  },
  searchInput: {
    width: '100%',
    padding: '8px',
    borderRadius: '4px',
    border: 'none',
    outline: 'none',
  },
  suggestions: {
    position: 'absolute',
    top: '40px',
    left: 0,
    backgroundColor: '#fff',
    color: '#000',
    listStyle: 'none',
    width: '100%',
    padding: 0,
    margin: 0,
    borderRadius: '4px',
    boxShadow: '0 2px 6px rgba(0,0,0,0.2)',
    maxHeight: '200px',
    overflowY: 'auto',
  },
  suggestionItem: {
    padding: '10px',
    cursor: 'pointer',
  },
  right: {
    flex: 1,
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
  },
  navItem: {
    marginLeft: '20px',
    color: '#fff',
    textDecoration: 'none',
    fontSize: '16px',
  },
  notificationBell: {
    position: 'relative',
    marginLeft: '20px',
    cursor: 'pointer',
  },
  bellIcon: {
    fontSize: '22px',
    color: '#fff',
  },
  notificationCount: {
    position: 'absolute',
    top: '-5px',
    right: '-10px',
    backgroundColor: 'red',
    color: '#fff',
    padding: '2px 6px',
    borderRadius: '50%',
    fontSize: '12px',
  },
  logoutBtn: {
    marginLeft: '20px',
    padding: '8px 16px',
    backgroundColor: '#ff4d4f',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};

export default Navbar;
