// src/components/Chat.js
import React, { useEffect, useState, useRef } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance";
import { ChatIcon } from "@heroicons/react/outline";

const Chat = () => {
  const { username } = useParams(); // Now using username from URL
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [targetUserId, setTargetUserId] = useState(null); // Store resolved userId
  const navigate = useNavigate();
  const currentUserId = localStorage.getItem("userId");
  const messagesEndRef = useRef(null);

  // Fetch target userId and chat history
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token || !currentUserId) {
      navigate("/login");
      return;
    }

    const fetchUserIdAndChatHistory = async () => {
      try {
        setLoading(true);

        // Fetch userId from username
        const { data: user } = await API.get(`/users/username/${username}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!user || !user._id) throw new Error("User not found");
        setTargetUserId(user._id);

        // Fetch chat history with resolved userId
        const { data: chatData } = await API.get(`/history/${user._id}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setMessages(Array.isArray(chatData) ? chatData : []);

        // Mark messages as seen
        await API.put(
          "/mark-seen",
          { sender: user._id },
          { headers: { Authorization: `Bearer ${token}` } }
        );
      } catch (err) {
        console.error("Error fetching data:", err.response?.data || err.message);
        setError(err.message === "User not found" ? "User not found" : "Failed to load chat");
      } finally {
        setLoading(false);
      }
    };

    fetchUserIdAndChatHistory();
  }, [username, navigate, currentUserId]);

  // Auto-scroll to latest message
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSendMessage = async (e) => {
    e.preventDefault();
    if (!newMessage.trim() || !targetUserId) return;

    const token = localStorage.getItem("token");
    try {
      const messageData = {
        receiver: targetUserId, // Use resolved userId
        text: newMessage,
        sender: currentUserId,
      };
      const { data } = await API.post("/send", messageData, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMessages((prev) => [...prev, data]);
      setNewMessage("");
    } catch (err) {
      console.error("Error sending message:", err.response?.data || err.message);
      setError("Failed to send message");
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex justify-center items-center">
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
          className="h-8 w-8 border-4 border-blue-500 border-t-transparent rounded-full"
        />
      </div>
    );
  }

  if (error) {
    return <div className="p-4 text-red-500 text-center">{error}</div>;
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100 p-5 md:p-10 max-w-4xl mx-auto mt-16">
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="text-center mb-8"
      >
        <ChatIcon className="h-12 w-12 mx-auto text-blue-600 mb-4" />
        <h1 className="text-4xl font-bold text-gray-800">Chat with {username}</h1>
      </motion.div>

      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 0.5, delay: 0.2 }}
        className="bg-white rounded-lg shadow-lg p-4 max-h-[60vh] overflow-y-auto"
      >
        {messages.length === 0 ? (
          <p className="text-gray-500 text-center">No messages yet. Start chatting!</p>
        ) : (
          messages.map((msg, index) => (
            <motion.div
              key={msg._id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.05 }}
              className={`mb-4 flex ${
                msg.sender._id === currentUserId ? "justify-end" : "justify-start"
              }`}
            >
              <div
                className={`max-w-xs p-3 rounded-lg ${
                  msg.sender._id === currentUserId
                    ? "bg-blue-500 text-white"
                    : "bg-gray-200 text-black"
                }`}
              >
                <p className="font-semibold">{msg.sender.username}</p>
                {msg.text && <p>{msg.text}</p>}
                {msg.image && (
                  <img
                    src={msg.image}
                    alt="Chat attachment"
                    className="max-w-full h-auto mt-2 rounded"
                  />
                )}
                <p className="text-xs mt-1 opacity-75">
                  {new Date(msg.timestamp).toLocaleString()}
                </p>
              </div>
            </motion.div>
          ))
        )}
        <div ref={messagesEndRef} />
      </motion.div>

      <motion.form
        onSubmit={handleSendMessage}
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5, delay: 0.4 }}
        className="mt-4 flex space-x-2"
      >
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          placeholder="Type a message..."
          className="flex-1 p-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all duration-300"
        />
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600 transition-all duration-300"
        >
          Send
        </button>
      </motion.form>
    </div>
  );
};

export default Chat;
