import React from "react";
import { Routes, Route } from "react-router-dom";
import Home from "./pages/Home";
import Auth from "./pages/Auth";
import Discussion from "./components/Discussion";
import Layout from "./components/Layout";
import CreateDiscussion from "./pages/CreateDiscussion";
import Navbar from "./components/Navbar";
import DiscussionPage from "./pages/DiscussionPage";
import Support from "./components/Support";
import DebateHub from "./pages/Debate";
import CreateDebate from "./pages/CreateDebate";
import DebatePage from "./pages/DebatePage";
import ProfilePage from "./pages/ProfilePage";
import Blog from "./pages/Blog";
import CreateBlog from "./pages/CreateBlog";
import BlogPage from "./pages/BlogPage";
import ChatPage from "./pages/ChatPage";
import Inbox from "./components/Inbox";

const App = () => {
  return (
    <div>
      <Navbar />
      <Routes>
        <Route path="/" element={<Layout />}>
          <Route index element={<Home />} />
          <Route path="login" element={<Auth />} />
          <Route path="discussion/:id" element={<Discussion />} />
          <Route path="create-discussion" element={<CreateDiscussion />} />
          <Route path="discussionpage/:id" element={<DiscussionPage />} />
          <Route path="support" element={<Support />} />
          <Route path="debate" element={<DebateHub />} />
          <Route path="create-debate" element={<CreateDebate />} />
          <Route path="debatepage/:id" element={<DebatePage />} />
          <Route path="profile/:username" element={<ProfilePage />} />
          <Route path="blog" element={<Blog />} />
          <Route path="create-blog" element={<CreateBlog />} />
          <Route path="blogpage/:id" element={<BlogPage />} />
          <Route path="chat/:userId" element={<ChatPage />} />  {/* âœ… Chat route added */}

        </Route>
      </Routes>
    </div>
  );
};

export default App;
