import React, { useEffect, useState } from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import API from "../api/axiosInstance"; // Your Axios instance
import { LockClosedIcon } from "@heroicons/react/solid";

const Home = () => {
  const [discussions, setDiscussions] = useState([]);
  const [debates, setDebates] = useState([]);
  const [blogs, setBlogs] = useState([]);
  const [activeSection, setActiveSection] = useState("discussions");
  const [loading, setLoading] = useState(false);

  const location = useLocation();
  const navigate = useNavigate();

  // Update active section based on URL query
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    setActiveSection(params.get("section") || "discussions");
  }, [location.search]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [discussionRes, debateRes, blogRes] = await Promise.all([
          API.get("/discussions"),
          API.get("/debates"),
          API.get("/blogs"),
        ]);
        setDiscussions(Array.isArray(discussionRes.data) ? discussionRes.data : []);
        setDebates(Array.isArray(debateRes.data) ? debateRes.data : []);
        setBlogs(Array.isArray(blogRes.data) ? blogRes.data : []);
      } catch (err) {
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const handleSectionChange = (section) => {
    navigate(`/?section=${section}`);
  };

  const getPopular = (data) =>
    [...data]
      .sort((a, b) => {
        const popularityA = (a.comments?.length || 0) + (a.upvotes?.length || 0) + (a.downvotes?.length || 0);
        const popularityB = (b.comments?.length || 0) + (b.upvotes?.length || 0) + (b.downvotes?.length || 0);
        return popularityB - popularityA;
      })
      .slice(0, 9);

  return (
    <div className="w-full px-4 md:ml-64 mt-4 md:mt-8">
      {/* Section Selector */}
      <div className="flex flex-col md:flex-row gap-4 mb-6">
        <button
          onClick={() => handleSectionChange("discussions")}
          className={`px-4 py-2 rounded ${activeSection === "discussions" ? "bg-blue-500 text-white" : "bg-gray-200"}`}
        >
          Discussions
        </button>
        <button
          onClick={() => handleSectionChange("debates")}
          className={`px-4 py-2 rounded ${activeSection === "debates" ? "bg-blue-500 text-white" : "bg-gray-200"}`}
        >
          Debates
        </button>
        <button
          onClick={() => handleSectionChange("blogs")}
          className={`px-4 py-2 rounded ${activeSection === "blogs" ? "bg-blue-500 text-white" : "bg-gray-200"}`}
        >
          Blogs
        </button>
      </div>

      {/* Content */}
      {loading ? (
        <p className="text-center text-gray-500">Loading...</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {/* Discussions */}
          {activeSection === "discussions" &&
            getPopular(discussions).map((discussion) => (
              <Link
                key={discussion._id}
                to={`/discussionpage/${discussion._id}`}
                className="relative bg-white rounded-lg shadow group hover:shadow-lg transition"
              >
                <div className="absolute inset-0 bg-gradient-to-t from-blue-500/80 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
                <div className="p-4 md:p-6 relative z-10">
                  <h3 className="text-lg md:text-xl font-semibold text-gray-800 group-hover:text-white transition-colors duration-300">
                    {discussion.title || "Untitled Discussion"}
                  </h3>
                  <p className="text-gray-600 group-hover:text-gray-200 mt-2 text-sm md:text-base">
                    {discussion.description
                      ? discussion.description.substring(0, 80) + "..."
                      : "No description"}
                  </p>
                  <p className="text-xs md:text-sm text-blue-500 group-hover:text-yellow-300 mt-3 font-medium transition-colors duration-300">
                    {discussion.category || "Uncategorized"}
                  </p>
                  <p className="text-xs md:text-sm text-gray-500 group-hover:text-gray-300 mt-1 transition-colors duration-300">
                    By:{" "}
                    <Link
                      to={`/profile/${discussion.author?.username || "unknown"}`}
                      className="hover:underline"
                    >
                      {discussion.author?.username || "Unknown"}
                    </Link>
                    {discussion.isPrivate && (
                      <LockClosedIcon className="h-3 w-3 md:h-4 md:w-4 inline ml-1 text-red-500" />
                    )}
                  </p>
                </div>
              </Link>
            ))}

          {/* Debates */}
          {activeSection === "debates" &&
            getPopular(debates).map((debate) => (
              <Link
                key={debate._id}
                to={`/debatepage/${debate._id}?section=debates`}
                onClick={() => navigate(`/?section=debates`)}
                className="block px-4 py-4 bg-white rounded-lg shadow hover:shadow-lg border border-gray-200 transition"
              >
                <h3 className="text-gray-800 font-semibold text-sm md:text-base">
                  {debate.title || "Untitled Debate"}
                </h3>
                <p className="text-gray-600 text-xs md:text-sm truncate mt-2">
                  {debate.openingArgument
                    ? `${debate.openingArgument.substring(0, 30)}...`
                    : "No argument"}
                </p>
                <div className="flex justify-between text-xs md:text-sm mt-3">
                  <span className="text-teal-500">{debate.category || "Uncategorized"}</span>
                  <span className="text-gray-500">
                    By:{" "}
                    <Link
                      to={`/profile/${debate.author?.username || "unknown"}`}
                      className="hover:underline"
                    >
                      {debate.author?.username || "Unknown"}
                    </Link>
                    {debate.isPrivate && (
                      <LockClosedIcon className="h-3 w-3 md:h-4 md:w-4 inline ml-1 text-red-500" />
                    )}
                  </span>
                </div>
              </Link>
            ))}

          {/* Blogs */}
          {activeSection === "blogs" &&
            getPopular(blogs).map((blog) => (
              <Link
                key={blog._id}
                to={`/blogpage/${blog._id}?section=blogs`}
                onClick={() => navigate(`/?section=blogs`)}
                className="relative bg-white rounded-lg shadow group hover:shadow-lg transition"
              >
                <div className="absolute inset-0 bg-gradient-to-t from-blue-500/80 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
                <div className="p-4 md:p-6 relative z-10">
                  <h3 className="text-lg md:text-xl font-semibold text-gray-800 group-hover:text-white transition-colors duration-300">
                    {blog.title || "Untitled Blog"}
                  </h3>
                  <p className="text-gray-600 group-hover:text-gray-200 mt-2 text-sm md:text-base">
                    {blog.content ? blog.content.substring(0, 80) + "..." : "No content available"}
                  </p>
                  {blog.fileUrl && (
                    <div className="mt-2">
                      <span className="text-xs md:text-sm text-blue-500 group-hover:text-yellow-300">
                        {blog.fileUrl.endsWith(".pdf")
                          ? "PDF Attached"
                          : blog.fileUrl.match(/\.(mp4|webm|ogg)$/)
                          ? "Video Attached"
                          : blog.fileUrl.match(/\.(mp3|wav|ogg)$/)
                          ? "Audio Attached"
                          : "Image Attached"}
                      </span>
                    </div>
                  )}
                  <div className="mt-3 flex justify-between items-center">
                    <span className="text-xs md:text-sm text-blue-500 group-hover:text-yellow-300 font-medium transition-colors duration-300">
                      Votes: {(blog.upvotes || 0) - (blog.downvotes || 0)}
                    </span>
                    <span className="text-xs md:text-sm text-gray-500 group-hover:text-gray-300 transition-colors duration-300">
                      By:{" "}
                      <Link
                        to={`/profile/${blog.author?.username || "unknown"}`}
                        className="hover:underline"
                      >
                        {blog.author?.username || "Unknown"}
                      </Link>
                      {blog.isPrivate && (
                        <LockClosedIcon className="h-3 w-3 md:h-4 md:w-4 inline ml-1 text-red-500" />
                      )}
                    </span>
                  </div>
                </div>
              </Link>
            ))}
        </div>
      )}

      {/* See More */}
      {!loading && (activeSection === "discussions" ? discussions.length > 9 : activeSection === "debates" ? debates.length > 9 : blogs.length > 9) && (
        <div className="mt-6 text-center">
          <Link
            to={`/${activeSection}`}
            className="inline-block px-6 py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition"
          >
            See More
          </Link>
        </div>
      )}
    </div>
  );
};

export default Home;
