// CreateBlog.jsx
import React, { useState, useRef } from "react";
import API from "../api/axiosInstance";
import { useNavigate } from "react-router-dom";
import { FaPlus, FaTrashAlt } from "react-icons/fa";

const CreateBlog = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [files, setFiles] = useState([]);
  const [isPrivate, setIsPrivate] = useState(false);
  const [passcode, setPasscode] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const fileInputRef = useRef(null);

  const handleAddFile = () => {
    fileInputRef.current.click();
  };

  const handleFileChange = (e) => {
    const newFiles = Array.from(e.target.files);
    setFiles((prevFiles) => [...prevFiles, ...newFiles]);
    e.target.value = null; // reset input to allow same file re-selection
  };

  const handleRemoveFile = (indexToRemove) => {
    setFiles((prevFiles) => prevFiles.filter((_, index) => index !== indexToRemove));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    formData.append("isPrivate", isPrivate.toString());
    if (isPrivate) formData.append("passcode", passcode);
    files.forEach((file) => formData.append("files", file));

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("Please log in to create a blog post");
      }

      const controller = new AbortController();
      const timeoutId = setTimeout(() => {
        controller.abort();
        console.log("Request timed out after 15s");
      }, 15000);

      const response = await API.post("/blogs", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: `Bearer ${token}`,
        },
        signal: controller.signal,
      });

      clearTimeout(timeoutId);
      console.log("Blog created successfully:", response.data);
      // Reset form
      setTitle("");
      setContent("");
      setFiles([]);
      setIsPrivate(false);
      setPasscode("");
      navigate("/blog");
    } catch (err) {
      console.error("Error in blog creation:", err);
      if (err.name === "AbortError") {
        setError("Request timed out. Try smaller files or check your connection.");
      } else if (err.response) {
        setError(err.response.data.message || "Failed to create blog post");
      } else {
        setError(err.message || "An unexpected error occurred");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h2 className="text-3xl font-extrabold mb-6 text-center text-gray-800">Create New Blog</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data" className="space-y-6 bg-white shadow-md rounded-lg p-6">
        {/* Blog Title */}
        <div>
          <label htmlFor="title" className="block text-lg font-medium text-gray-700 mb-1">
            Blog Title
          </label>
          <input
            id="title"
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter your blog title..."
            className="w-full border border-gray-300 rounded-md p-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        {/* Blog Content */}
        <div>
          <label htmlFor="content" className="block text-lg font-medium text-gray-700 mb-1">
            Content
          </label>
          <textarea
            id="content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="Write your blog content here..."
            className="w-full border border-gray-300 rounded-md p-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
            rows="8"
            required
          ></textarea>
        </div>
        {/* File Upload */}
        <div>
          <label className="block text-lg font-medium text-gray-700 mb-1">Files</label>
          <div className="flex flex-wrap gap-4">
            {files.map((file, index) => (
              <div key={index} className="flex items-center space-x-2 bg-gray-100 p-2 rounded-md">
                <span className="text-sm text-gray-700 truncate max-w-xs">{file.name}</span>
                <button
                  type="button"
                  onClick={() => handleRemoveFile(index)}
                  className="text-red-500 hover:text-red-700"
                  title="Remove file"
                >
                  <FaTrashAlt />
                </button>
              </div>
            ))}
            <button
              type="button"
              onClick={handleAddFile}
              className="flex items-center space-x-2 border border-dashed border-gray-400 rounded-md p-2 hover:bg-gray-50 transition"
            >
              <FaPlus className="text-blue-500" />
              <span className="text-blue-500 font-medium">Add File</span>
            </button>
          </div>
          <input
            type="file"
            ref={fileInputRef}
            onChange={handleFileChange}
            className="hidden"
            accept="image/*,video/*,audio/*,.pdf"
            multiple
          />
        </div>
        {/* Privacy Options */}
        <div>
          <label className="inline-flex items-center">
            <input
              type="checkbox"
              checked={isPrivate}
              onChange={(e) => setIsPrivate(e.target.checked)}
              className="rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="ml-2 text-gray-700 font-medium">Private Blog</span>
          </label>
          {isPrivate && (
            <div className="mt-4">
              <label htmlFor="passcode" className="block text-lg font-medium text-gray-700 mb-1">
                Passcode
              </label>
              <input
                id="passcode"
                type="text"
                value={passcode}
                onChange={(e) => setPasscode(e.target.value)}
                placeholder="Enter passcode..."
                className="w-full border border-gray-300 rounded-md p-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
                required={isPrivate}
              />
            </div>
          )}
        </div>
        {/* Error Message */}
        {error && (
          <div className="p-4 bg-red-100 text-red-700 border border-red-200 rounded-md">
            {error}
          </div>
        )}
        {/* Submit Button */}
        <div>
          <button
            type="submit"
            disabled={loading}
            className={`w-full py-3 rounded-md text-white font-semibold transition ${
              loading ? "bg-gray-400 cursor-not-allowed" : "bg-blue-600 hover:bg-blue-700"
            }`}
          >
            {loading ? "Posting..." : "Publish Blog"}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreateBlog;
