import React, { useEffect, useState, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../api/axiosInstance";
import {
  UserRemoveIcon,
  CogIcon,
  CalendarIcon,
  PencilIcon,
  CameraIcon,
  ChatAlt2Icon,
  AnnotationIcon,
  PencilAltIcon,
  DocumentTextIcon,
  UserGroupIcon,
  UsersIcon,
  XIcon,
  TrashIcon,
} from "@heroicons/react/outline";

const BACKEND_BASE_URL = "http://localhost:5001";

function getToken() {
  const token = localStorage.getItem("token");
  return token;
}

const ProfilePage = () => {
  const { username } = useParams();
  const navigate = useNavigate();

  const [profileUser, setProfileUser] = useState(null);
  const [currentUser, setCurrentUser] = useState(null);
  const [createdDebates, setCreatedDebates] = useState([]);
  const [createdBlogs, setCreatedBlogs] = useState([]);
  const [createdDiscussions, setCreatedDiscussions] = useState([]);
  const [profileError, setProfileError] = useState("");
  const [isFollowing, setIsFollowing] = useState(false);
  const [isFollower, setIsFollower] = useState(false);
  const [activeTab, setActiveTab] = useState("debate");
  const [showFollowers, setShowFollowers] = useState(false);
  const [showFollowing, setShowFollowing] = useState(false);
  const [showSettingsMenu, setShowSettingsMenu] = useState(false);
  const [showChangeBioModal, setShowChangeBioModal] = useState(false);
  const [showChangeProfilePictureModal, setShowChangeProfilePictureModal] = useState(false);
  const [showChangeAddressModal, setShowChangeAddressModal] = useState(false);
  const [newBio, setNewBio] = useState("");
  const [newProfilePicture, setNewProfilePicture] = useState(null);
  const [newAddress, setNewAddress] = useState("");
  const [loading, setLoading] = useState(true);

  const token = getToken();

  const fetchCurrentUser = useCallback(async () => {
    if (!token) return null;
    const res = await API.get("/users/profile/me", { headers: { Authorization: `Bearer ${token}` } });
    return res.data;
  }, [token]);

  const fetchProfile = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}`, { headers: { Authorization: `Bearer ${token}` } });
    return res.data;
  }, [username, token]);

  const fetchCreatedDebates = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}/debates`, { headers: { Authorization: `Bearer ${token}` } });
    return res.data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  }, [username, token]);

  const fetchCreatedBlogs = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}/blogs`, { headers: { Authorization: `Bearer ${token}` } });
    return res.data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  }, [username, token]);

  const fetchCreatedDiscussions = useCallback(async () => {
    const res = await API.get(`/users/profile/${username}/discussions`, { headers: { Authorization: `Bearer ${token}` } });
    return res.data.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
  }, [username, token]);

  const fetchAllData = useCallback(async () => {
    setLoading(true);
    setProfileUser(null);
    setCreatedDebates([]);
    setCreatedBlogs([]);
    setCreatedDiscussions([]);
    setProfileError("");

    try {
      const [currentUserData, profileData, debatesData, blogsData, discussionsData] = await Promise.all([
        fetchCurrentUser(),
        fetchProfile(),
        fetchCreatedDebates(),
        fetchCreatedBlogs(),
        fetchCreatedDiscussions(),
      ]);

      setCurrentUser(currentUserData);
      setProfileUser(profileData);
      setCreatedDebates(debatesData);
      setCreatedBlogs(blogsData);
      setCreatedDiscussions(discussionsData);

      setNewBio(profileData.bio || "");
      setNewAddress(profileData.address || "");
      const followers = Array.isArray(profileData.followers) ? profileData.followers : [];
      const followings = Array.isArray(profileData.followings) ? profileData.followings : [];
      if (currentUserData) {
        setIsFollowing(followers.some((f) => String(f._id) === String(currentUserData._id)));
        setIsFollower(followings.some((f) => String(f._id) === String(currentUserData._id)));
      }
    } catch (err) {
      console.error("Error fetching data:", err);
      setProfileError(err.response?.data?.message || "Error fetching profile data");
    } finally {
      setLoading(false);
    }
  }, [fetchCurrentUser, fetchProfile, fetchCreatedDebates, fetchCreatedBlogs, fetchCreatedDiscussions]);

  useEffect(() => {
    fetchAllData();
  }, [username, fetchAllData]);

  const handleDeleteDebate = async (debateId) => {
    try {
      await API.delete(`/debates/${debateId}`, { headers: { Authorization: `Bearer ${token}` } });
      setCreatedDebates(createdDebates.filter((deb) => deb._id !== debateId));
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error deleting debate");
    }
  };

  const handleDeleteDiscussion = async (discussionId) => {
    try {
      await API.delete(`/discussions/${discussionId}`, { headers: { Authorization: `Bearer ${token}` } });
      setCreatedDiscussions(createdDiscussions.filter((disc) => disc._id !== discussionId));
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error deleting discussion");
    }
  };

  const handleDeleteBlog = async (blogId) => {
    try {
      await API.delete(`/blogs/${blogId}`, { headers: { Authorization: `Bearer ${token}` } });
      setCreatedBlogs(createdBlogs.filter((blog) => blog._id !== blogId));
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error deleting blog");
    }
  };

  const handleChangeBio = async () => {
    try {
      if (!token) throw new Error("No authentication token found");
      await API.put("/users/profile/update-bio", { bio: newBio }, { headers: { Authorization: `Bearer ${token}` } });
      setShowChangeBioModal(false);
      fetchAllData();
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error updating bio");
    }
  };

  const handleChangeProfilePicture = async () => {
    if (!newProfilePicture) {
      setProfileError("Please select a file to upload.");
      return;
    }
    const formData = new FormData();
    formData.append("profilePicture", newProfilePicture);
    try {
      await API.put("/users/profile/update-profile-picture", formData, { headers: { Authorization: `Bearer ${token}` } });
      setShowChangeProfilePictureModal(false);
      setNewProfilePicture(null);
      fetchAllData();
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error updating profile picture");
    }
  };

  const handleChangeAddress = async () => {
    try {
      if (!token) throw new Error("No authentication token found");
      await API.put("/users/profile/update-address", { address: newAddress }, { headers: { Authorization: `Bearer ${token}` } });
      setShowChangeAddressModal(false);
      fetchAllData();
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error updating address");
    }
  };

  const handleLogOut = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const handleFollow = async () => {
    if (!token) {
      alert("You must be logged in to follow or unfollow!");
      return navigate("/login");
    }
    try {
      if (isFollowing) {
        await API.post(`/users/profile/${username}/unfollow`, {}, { headers: { Authorization: `Bearer ${token}` } });
      } else {
        await API.post(`/users/profile/${username}/follow`, {}, { headers: { Authorization: `Bearer ${token}` } });
      }
      fetchAllData();
    } catch (err) {
      setProfileError(err.response?.data?.message || "Error following/unfollowing");
    }
  };

  const handleChat = () => {
    if (!token) {
      alert("You must be logged in to chat!");
      return navigate("/login");
    }
    navigate(`/chat/${username}`);
  };

  const handleDebateClick = (debateId) => navigate(`/debatepage/${debateId}`);
  const handleDiscussionClick = (discussionId) => navigate(`/discussionpage/${discussionId}`);
  const handleBlogClick = (blogId) => navigate(`/blogpage/${blogId}`);
  const handleProfileClick = (username) => {
    setShowFollowers(false);
    setShowFollowing(false);
    navigate(`/profile/${username}`);
  };

  const isOwnProfile = currentUser && profileUser && currentUser._id === profileUser._id;
  const totalPosts = (createdDebates.length || 0) + (createdDiscussions.length || 0) + (createdBlogs.length || 0);
  const followButtonText = isFollowing ? "Unfollow" : isFollower ? "Follow Back" : "Follow";

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-teal-800 to-gray-900 flex items-center justify-center">
        <span className="text-2xl text-white font-light animate-pulse tracking-wide">Crafting Your Profile...</span>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-900 via-teal-800 to-gray-900 text-white">
      {/* Error Message */}
      {profileError && (
        <div className="max-w-5xl mx-auto px-8 mt-8">
          <div className="bg-red-500 bg-opacity-20 backdrop-blur-md p-4 rounded-xl shadow-lg text-red-200">
            {profileError}
          </div>
        </div>
      )}

      {/* Header Section */}
      <header className="py-12 px-8 relative overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-r from-indigo-600 to-teal-500 opacity-80 transform -skew-y-6"></div>
        <div className="max-w-5xl mx-auto relative z-10">
          <div className="flex items-center justify-between flex-col md:flex-row gap-8">
            <div className="relative group">
              <div className="w-48 h-48 rounded-full bg-gradient-to-br from-gold-400 to-yellow-600 p-1 shadow-2xl transform group-hover:scale-105 transition-all duration-500">
                <img
                  src={
                    profileUser?.profilePicture
                      ? `${BACKEND_BASE_URL}${profileUser.profilePicture}`
                      : `${BACKEND_BASE_URL}/images/default-avatar.png`
                  }
                  alt="profile"
                  className="w-full h-full rounded-full object-cover border-4 border-gray-900"
                  onError={(e) => (e.target.src = `${BACKEND_BASE_URL}/images/default-avatar.png`)}
                />
              </div>
              {isOwnProfile && (
                <button
                  onClick={() => setShowChangeProfilePictureModal(true)}
                  className="absolute bottom-2 right-2 p-2 bg-gold-500 text-gray-900 rounded-full hover:bg-gold-600 transition-all duration-300 shadow-lg"
                >
                  <CameraIcon className="h-6 w-6" />
                </button>
              )}
            </div>
            <div className="text-center md:text-left">
              <div className="flex items-center justify-center md:justify-start mb-4">
                <h1 className="text-5xl font-extrabold tracking-tight text-white drop-shadow-lg">
                  {profileUser?.username || "Loading..."}
                </h1>
                {isOwnProfile ? (
                  <button
                    onClick={() => setShowSettingsMenu(!showSettingsMenu)}
                    className="ml-6 p-2 bg-white bg-opacity-10 backdrop-blur-md rounded-full hover:bg-opacity-20 transition-all duration-300"
                  >
                    <CogIcon className="h-7 w-7 text-gold-400" />
                  </button>
                ) : (
                  <div className="ml-6 flex space-x-4">
                    <button
                      onClick={handleFollow}
                      className={`px-6 py-2 rounded-full font-medium text-gray-900 shadow-lg transform hover:scale-105 transition-all duration-300 ${
                        isFollowing ? "bg-gray-400 hover:bg-gray-500" : "bg-gold-500 hover:bg-gold-600"
                      }`}
                    >
                      {followButtonText}
                    </button>
                    <button
                      onClick={handleChat}
                      className="px-6 py-2 rounded-full bg-teal-500 text-white font-medium shadow-lg hover:bg-teal-600 transform hover:scale-105 transition-all duration-300"
                    >
                      Message
                    </button>
                  </div>
                )}
              </div>
              {showSettingsMenu && isOwnProfile && (
                <div className="absolute mt-2 w-64 bg-gray-900 bg-opacity-80 backdrop-blur-md rounded-xl shadow-2xl z-20 border border-gold-500 border-opacity-20">
                  <ul className="py-2 text-white">
                    <li
                      className="px-4 py-3 hover:bg-gold-500 hover:bg-opacity-20 cursor-pointer flex items-center transition-all duration-200"
                      onClick={() => {
                        setShowChangeBioModal(true);
                        setShowSettingsMenu(false);
                      }}
                    >
                      <PencilIcon className="h-5 w-5 mr-3 text-gold-400" />
                      Change Bio
                    </li>
                    <li
                      className="px-4 py-3 hover:bg-gold-500 hover:bg-opacity-20 cursor-pointer flex items-center transition-all duration-200"
                      onClick={() => {
                        setShowChangeAddressModal(true);
                        setShowSettingsMenu(false);
                      }}
                    >
                      <PencilIcon className="h-5 w-5 mr-3 text-gold-400" />
                      Change Address
                    </li>
                    <li
                      className="px-4 py-3 hover:bg-gold-500 hover:bg-opacity-20 cursor-pointer flex items-center transition-all duration-200"
                      onClick={handleLogOut}
                    >
                      <UserRemoveIcon className="h-5 w-5 mr-3 text-gold-400" />
                      Log Out
                    </li>
                  </ul>
                </div>
              )}
              <div className="flex justify-center md:justify-start space-x-12 mb-6">
                <div className="flex items-center group cursor-pointer">
                  <DocumentTextIcon className="h-6 w-6 mr-2 text-gold-400 group-hover:animate-pulse" />
                  <span className="font-semibold text-xl text-white group-hover:text-gold-400 transition-colors duration-300">
                    {totalPosts}
                  </span>
                  <span className="text-gray-300 ml-1">Posts</span>
                </div>
                <div
                  onClick={() => setShowFollowers(true)}
                  className="flex items-center group cursor-pointer"
                >
                  <UserGroupIcon className="h-6 w-6 mr-2 text-gold-400 group-hover:animate-pulse" />
                  <span className="font-semibold text-xl text-white group-hover:text-gold-400 transition-colors duration-300">
                    {profileUser?.followers?.length || 0}
                  </span>
                  <span className="text-gray-300 ml-1">Followers</span>
                </div>
                <div
                  onClick={() => setShowFollowing(true)}
                  className="flex items-center group cursor-pointer"
                >
                  <UsersIcon className="h-6 w-6 mr-2 text-gold-400 group-hover:animate-pulse" />
                  <span className="font-semibold text-xl text-white group-hover:text-gold-400 transition-colors duration-300">
                    {profileUser?.followings?.length || 0}
                  </span>
                  <span className="text-gray-300 ml-1">Following</span>
                </div>
              </div>
              <p className="text-gray-200 italic text-lg font-light">{profileUser?.bio || "No bio yet."}</p>
              <div className="flex items-center justify-center md:justify-start text-gray-300 text-sm mt-3">
                <CalendarIcon className="h-5 w-5 mr-2 text-gold-400" />
                Joined: {profileUser?.createdAt ? new Date(profileUser.createdAt).toLocaleDateString() : "N/A"}
              </div>
              {profileUser?.address && (
                <p className="text-gray-300 text-sm mt-2">Address: {profileUser.address}</p>
              )}
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="max-w-5xl mx-auto px-8 py-12">
        {/* Tabs */}
        <div className="flex justify-center space-x-8 mb-10">
          {["debate", "discussion", "blogs"].map((tab) => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`flex items-center space-x-2 px-6 py-3 rounded-full font-medium text-lg uppercase tracking-wide shadow-lg transform hover:scale-105 transition-all duration-300 ${
                activeTab === tab
                  ? "bg-gold-500 text-gray-900"
                  : "bg-gray-900 bg-opacity-50 backdrop-blur-md text-white hover:bg-opacity-70"
              }`}
            >
              {tab === "debate" && <ChatAlt2Icon className="h-6 w-6" />}
              {tab === "discussion" && <AnnotationIcon className="h-6 w-6" />}
              {tab === "blogs" && <PencilAltIcon className="h-6 w-6" />}
              <span>{tab}</span>
            </button>
          ))}
        </div>

        {/* Posts Grid */}
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
          {activeTab === "debate" && (
            createdDebates.length > 0 ? (
              createdDebates.map((deb) => (
                <div
                  key={deb._id}
                  className="bg-gray-900 bg-opacity-40 backdrop-blur-lg rounded-2xl p-6 cursor-pointer hover:shadow-2xl hover:scale-105 transition-all duration-500 shadow-md border border-gold-500 border-opacity-20"
                  onClick={() => handleDebateClick(deb._id)}
                >
                  <h3 className="text-lg font-semibold text-white truncate">{deb.title || "Untitled Debate"}</h3>
                  <p className="text-gray-300 text-sm mt-2 line-clamp-2">{deb.openingArgument || "No opening argument"}</p>
                  {isOwnProfile && (
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteDebate(deb._id);
                      }}
                      className="mt-4 flex items-center text-red-400 hover:text-red-300 text-sm transition-colors duration-200"
                    >
                      <TrashIcon className="h-5 w-5 mr-2" />
                      Delete
                    </button>
                  )}
                </div>
              ))
            ) : (
              <p className="col-span-3 text-center text-gray-400 text-lg">No debates created yet.</p>
            )
          )}
          {activeTab === "discussion" && (
            createdDiscussions.length > 0 ? (
              createdDiscussions.map((disc) => (
                <div
                  key={disc._id}
                  className="bg-gray-900 bg-opacity-40 backdrop-blur-lg rounded-2xl p-6 cursor-pointer hover:shadow-2xl hover:scale-105 transition-all duration-500 shadow-md border border-gold-500 border-opacity-20"
                  onClick={() => handleDiscussionClick(disc._id)}
                >
                  <h3 className="text-lg font-semibold text-white truncate">{disc.title || "Untitled Discussion"}</h3>
                  <p className="text-gray-300 text-sm mt-2 line-clamp-2">{disc.description || "No description"}</p>
                  {isOwnProfile && (
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteDiscussion(disc._id);
                      }}
                      className="mt-4 flex items-center text-red-400 hover:text-red-300 text-sm transition-colors duration-200"
                    >
                      <TrashIcon className="h-5 w-5 mr-2" />
                      Delete
                    </button>
                  )}
                </div>
              ))
            ) : (
              <p className="col-span-3 text-center text-gray-400 text-lg">No discussions created yet.</p>
            )
          )}
          {activeTab === "blogs" && (
            createdBlogs.length > 0 ? (
              createdBlogs.map((blog) => (
                <div
                  key={blog._id}
                  className="bg-gray-900 bg-opacity-40 backdrop-blur-lg rounded-2xl p-6 cursor-pointer hover:shadow-2xl hover:scale-105 transition-all duration-500 shadow-md border border-gold-500 border-opacity-20"
                  onClick={() => handleBlogClick(blog._id)}
                >
                  <h3 className="text-lg font-semibold text-white truncate">{blog.title || "Untitled Blog"}</h3>
                  <p className="text-gray-300 text-sm mt-2 line-clamp-2">{blog.content || "No content"}</p>
                  {blog.fileUrl && blog.fileUrl.match(/\.(jpeg|jpg|png)$/i) && (
                    <img
                      src={`${BACKEND_BASE_URL}${blog.fileUrl}`}
                      alt="Blog media"
                      className="w-full h-40 object-cover rounded-lg mt-4 hover:opacity-90 transition-opacity duration-300"
                      onError={(e) => (e.target.src = `${BACKEND_BASE_URL}/images/default-image.png`)}
                    />
                  )}
                  {isOwnProfile && (
                    <button
                      onClick={(e) => {
                        e.stopPropagation();
                        handleDeleteBlog(blog._id);
                      }}
                      className="mt-4 flex items-center text-red-400 hover:text-red-300 text-sm transition-colors duration-200"
                    >
                      <TrashIcon className="h-5 w-5 mr-2" />
                      Delete
                    </button>
                  )}
                </div>
              ))
            ) : (
              <p className="col-span-3 text-center text-gray-400 text-lg">No blogs yet.</p>
            )
          )}
        </div>
      </div>

      {/* Followers Modal */}
      {showFollowers && (
        <div className="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center">
          <div className="bg-gray-900 bg-opacity-80 backdrop-blur-lg p-8 rounded-2xl shadow-2xl max-w-lg w-full max-h-[80vh] overflow-y-auto relative transform scale-95 animate-pop-in">
            <button
              onClick={() => setShowFollowers(false)}
              className="absolute top-4 right-4 text-gold-400 hover:text-gold-300 transition-colors duration-200"
            >
              <XIcon className="h-6 w-6" />
            </button>
            <h2 className="text-2xl font-bold text-white mb-6 text-center">Followers</h2>
            {profileUser?.followers?.length > 0 ? (
              <ul className="space-y-4">
                {profileUser.followers.map((follower) => (
                  <li
                    key={follower._id}
                    className="flex items-center space-x-4 p-3 bg-gray-800 bg-opacity-50 rounded-lg cursor-pointer hover:bg-opacity-70 transition-all duration-200"
                    onClick={() => handleProfileClick(follower.username)}
                  >
                    <img
                      src={
                        follower.profilePicture
                          ? `${BACKEND_BASE_URL}${follower.profilePicture}`
                          : `${BACKEND_BASE_URL}/images/default-avatar.png`
                      }
                      alt={follower.username}
                      className="w-12 h-12 rounded-full border-2 border-gold-500 border-opacity-50"
                    />
                    <span className="text-lg font-medium text-white">{follower.username}</span>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-center text-gray-400 text-lg">No followers yet.</p>
            )}
          </div>
        </div>
      )}

      {/* Following Modal */}
      {showFollowing && (
        <div className="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center">
          <div className="bg-gray-900 bg-opacity-80 backdrop-blur-lg p-8 rounded-2xl shadow-2xl max-w-lg w-full max-h-[80vh] overflow-y-auto relative transform scale-95 animate-pop-in">
            <button
              onClick={() => setShowFollowing(false)}
              className="absolute top-4 right-4 text-gold-400 hover:text-gold-300 transition-colors duration-200"
            >
              <XIcon className="h-6 w-6" />
            </button>
            <h2 className="text-2xl font-bold text-white mb-6 text-center">Following</h2>
            {profileUser?.followings?.length > 0 ? (
              <ul className="space-y-4">
                {profileUser.followings.map((following) => (
                  <li
                    key={following._id}
                    className="flex items-center space-x-4 p-3 bg-gray-800 bg-opacity-50 rounded-lg cursor-pointer hover:bg-opacity-70 transition-all duration-200"
                    onClick={() => handleProfileClick(following.username)}
                  >
                    <img
                      src={
                        following.profilePicture
                          ? `${BACKEND_BASE_URL}${following.profilePicture}`
                          : `${BACKEND_BASE_URL}/images/default-avatar.png`
                      }
                      alt={following.username}
                      className="w-12 h-12 rounded-full border-2 border-gold-500 border-opacity-50"
                    />
                    <span className="text-lg font-medium text-white">{following.username}</span>
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-center text-gray-400 text-lg">Not following anyone yet.</p>
            )}
          </div>
        </div>
      )}

      {/* Change Bio Modal */}
      {showChangeBioModal && (
        <div className="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center">
          <div className="bg-gray-900 bg-opacity-80 backdrop-blur-lg p-8 rounded-2xl shadow-2xl max-w-lg w-full transform scale-95 animate-pop-in relative">
            <button
              onClick={() => setShowChangeBioModal(false)}
              className="absolute top-4 right-4 text-gold-400 hover:text-gold-300 transition-colors duration-200"
            >
              <XIcon className="h-6 w-6" />
            </button>
            <h2 className="text-2xl font-bold text-white mb-6 text-center">Change Bio</h2>
            <textarea
              value={newBio}
              onChange={(e) => setNewBio(e.target.value)}
              className="w-full p-4 bg-gray-800 bg-opacity-50 rounded-lg border border-gold-500 border-opacity-20 text-white focus:outline-none focus:ring-2 focus:ring-gold-400 transition-all duration-200"
              rows="4"
              placeholder="Enter your new bio..."
            />
            <div className="flex space-x-4 mt-6">
              <button
                onClick={handleChangeBio}
                className="flex-1 bg-gold-500 text-gray-900 px-6 py-3 rounded-full font-medium hover:bg-gold-600 transform hover:scale-105 transition-all duration-300 shadow-lg"
              >
                Save
              </button>
              <button
                onClick={() => setShowChangeBioModal(false)}
                className="flex-1 bg-gray-800 bg-opacity-50 text-white px-6 py-3 rounded-full font-medium hover:bg-opacity-70 transition-all duration-300"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Change Profile Picture Modal */}
      {showChangeProfilePictureModal && (
        <div className="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center">
          <div className="bg-gray-900 bg-opacity-80 backdrop-blur-lg p-8 rounded-2xl shadow-2xl max-w-lg w-full transform scale-95 animate-pop-in relative">
            <button
              onClick={() => setShowChangeProfilePictureModal(false)}
              className="absolute top-4 right-4 text-gold-400 hover:text-gold-300 transition-colors duration-200"
            >
              <XIcon className="h-6 w-6" />
            </button>
            <h2 className="text-2xl font-bold text-white mb-6 text-center">Change Profile Picture</h2>
            <input
              type="file"
              accept="image/*"
              onChange={(e) => setNewProfilePicture(e.target.files[0])}
              className="w-full p-3 bg-gray-800 bg-opacity-50 rounded-lg border border-gold-500 border-opacity-20 text-white focus:outline-none focus:ring-2 focus:ring-gold-400"
            />
            <div className="flex space-x-4 mt-6">
              <button
                onClick={handleChangeProfilePicture}
                className="flex-1 bg-gold-500 text-gray-900 px-6 py-3 rounded-full font-medium hover:bg-gold-600 transform hover:scale-105 transition-all duration-300 shadow-lg"
              >
                Upload
              </button>
              <button
                onClick={() => setShowChangeProfilePictureModal(false)}
                className="flex-1 bg-gray-800 bg-opacity-50 text-white px-6 py-3 rounded-full font-medium hover:bg-opacity-70 transition-all duration-300"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Change Address Modal */}
      {showChangeAddressModal && (
        <div className="fixed inset-0 bg-black bg-opacity-70 flex justify-center items-center">
          <div className="bg-gray-900 bg-opacity-80 backdrop-blur-lg p-8 rounded-2xl shadow-2xl max-w-lg w-full transform scale-95 animate-pop-in relative">
            <button
              onClick={() => setShowChangeAddressModal(false)}
              className="absolute top-4 right-4 text-gold-400 hover:text-gold-300 transition-colors duration-200"
            >
              <XIcon className="h-6 w-6" />
            </button>
            <h2 className="text-2xl font-bold text-white mb-6 text-center">Change Address</h2>
            <input
              type="text"
              value={newAddress}
              onChange={(e) => setNewAddress(e.target.value)}
              className="w-full p-4 bg-gray-800 bg-opacity-50 rounded-lg border border-gold-500 border-opacity-20 text-white focus:outline-none focus:ring-2 focus:ring-gold-400 transition-all duration-200"
              placeholder="Enter your new address..."
            />
            <div className="flex space-x-4 mt-6">
              <button
                onClick={handleChangeAddress}
                className="flex-1 bg-gold-500 text-gray-900 px-6 py-3 rounded-full font-medium hover:bg-gold-600 transform hover:scale-105 transition-all duration-300 shadow-lg"
              >
                Save
              </button>
              <button
                onClick={() => setShowChangeAddressModal(false)}
                className="flex-1 bg-gray-800 bg-opacity-50 text-white px-6 py-3 rounded-full font-medium hover:bg-opacity-70 transition-all duration-300"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProfilePage;
