import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import API from "../api/axiosInstance"; // Adjust your API import path

const Home = () => {
  const [discussions, setDiscussions] = useState([]);
  const [debates, setDebates] = useState([]);
  const [blogs, setBlogs] = useState([]);
  const [activeSection, setActiveSection] = useState("discussions");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [discussionsRes, debatesRes, blogsRes] = await Promise.all([
          API.get("/discussions"),
          API.get("/debates"),
          API.get("/blogs"),
        ]);
        setDiscussions(Array.isArray(discussionsRes.data) ? discussionsRes.data : []);
        setDebates(Array.isArray(debatesRes.data) ? debatesRes.data : []);
        setBlogs(Array.isArray(blogsRes.data) ? blogsRes.data : []);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const sortByPopularity = (arr) => {
    return [...arr].sort((a, b) => {
      const popularityA = (a.comments?.length || 0) + (a.upvotes?.length || 0) + (a.downvotes?.length || 0);
      const popularityB = (b.comments?.length || 0) + (b.upvotes?.length || 0) + (b.downvotes?.length || 0);
      return popularityB - popularityA;
    });
  };

  const renderSection = (items, title, link, pagePath, contentKey = "description", titleKey = "title") => {
    const sorted = sortByPopularity(items).slice(0, 8); // Top 8 content
    if (sorted.length === 0) return <div className="text-gray-500">No {title} Found</div>;

    return (
      <section>
        <h2 className="text-3xl font-bold mb-6">{`Top ${title}`}</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-3 gap-6">
          {sorted.map((item) => (
            <Link
              to={`/${pagePath}/${item._id}`}
              key={item._id}
              className="relative rounded-2xl overflow-hidden shadow-lg group bg-white hover:scale-[1.02] transition-all duration-300"
            >
              <div className="absolute inset-0 bg-gradient-to-t from-blue-500/80 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300" />
              <div className="p-4 md:p-6 relative z-10">
                <h3 className="text-lg md:text-xl font-semibold text-gray-800 group-hover:text-white transition-colors duration-300">
                  {item[titleKey] || `Untitled ${title}`}
                </h3>
                <p className="text-gray-600 group-hover:text-gray-200 mt-2 text-sm md:text-base">
                  {item[contentKey] ? item[contentKey].substring(0, 80) + "..." : "No description"}
                </p>
                <p className="text-xs md:text-sm text-blue-500 group-hover:text-yellow-300 mt-3 font-medium transition-colors duration-300">
                  {item.category || "Uncategorized"}
                </p>
                <p className="text-xs md:text-sm text-gray-500 group-hover:text-gray-300 mt-1 transition-colors duration-300">
                  By: {item.author?.username || "Unknown"}
                </p>
              </div>
            </Link>
          ))}

          {/* See More */}
          <Link
            to={link}
            className="flex items-center justify-center bg-blue-100 hover:bg-blue-200 text-blue-700 font-semibold rounded-2xl p-6 transition-all"
          >
            See More â†’
          </Link>
        </div>
      </section>
    );
  };

  return (
    <div className="p-6 md:ml-64">
      <div className="flex flex-col md:flex-row gap-4 mb-8">
        <button
          onClick={() => setActiveSection("discussions")}
          className={`px-6 py-3 rounded-xl font-semibold ${
            activeSection === "discussions" ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-700"
          }`}
        >
          Discussions
        </button>
        <button
          onClick={() => setActiveSection("debates")}
          className={`px-6 py-3 rounded-xl font-semibold ${
            activeSection === "debates" ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-700"
          }`}
        >
          Debates
        </button>
        <button
          onClick={() => setActiveSection("blogs")}
          className={`px-6 py-3 rounded-xl font-semibold ${
            activeSection === "blogs" ? "bg-blue-600 text-white" : "bg-gray-200 text-gray-700"
          }`}
        >
          Blogs
        </button>
      </div>

      {loading && <p className="text-center text-gray-500">Loading...</p>}

      {!loading && activeSection === "discussions" &&
        renderSection(discussions, "Discussions", "/discussions", "discussionpage")
      }

      {!loading && activeSection === "debates" &&
        renderSection(debates, "Debates", "/debates", "debatepage")
      }

      {!loading && activeSection === "blogs" &&
        renderSection(blogs, "Blogs", "/blogs", "blogpage", "content", "title")
      }
    </div>
  );
};

export default Home;
