import React, { useState, useEffect } from "react";
import { Link, useLocation } from "react-router-dom";
import API from "../api";
import { LockClosedIcon } from "@heroicons/react/solid";

const Home = () => {
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const defaultSection = queryParams.get("section") || "discussions";

  const [section, setSection] = useState(defaultSection);
  const [discussions, setDiscussions] = useState([]);
  const [debates, setDebates] = useState([]);
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const [discussionRes, debateRes, blogRes] = await Promise.all([
          API.get("/discussions"),
          API.get("/debates"),
          API.get("/blogs"),
        ]);
        setDiscussions(discussionRes.data);
        setDebates(debateRes.data);
        setBlogs(blogRes.data);
      } catch (err) {
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const sortByPopularity = (items) =>
    [...items]
      .sort((a, b) => {
        const popA = (a.comments?.length || 0) + (a.upvotes?.length || 0) + (a.downvotes?.length || 0);
        const popB = (b.comments?.length || 0) + (b.upvotes?.length || 0) + (b.downvotes?.length || 0);
        return popB - popA;
      })
      .slice(0, 9);

  const renderDiscussions = () =>
    sortByPopularity(discussions).map((discussion) => (
      <Link key={discussion._id} to={`/discussionpage/${discussion._id}?section=discussions`} className="group relative rounded-xl overflow-hidden shadow hover:shadow-lg transition">
        <div className="absolute inset-0 bg-gradient-to-t from-blue-500/80 to-transparent opacity-0 group-hover:opacity-100 transition duration-300"></div>
        <div className="p-3 relative z-10 bg-white">
          <h3 className="text-base font-semibold text-gray-800 group-hover:text-white">{discussion.title || "Untitled Discussion"}</h3>
          <p className="text-gray-600 group-hover:text-gray-200 mt-2 text-sm">{discussion.description?.substring(0, 60) || "No description"}...</p>
          <p className="text-xs text-blue-500 group-hover:text-yellow-300 mt-2">{discussion.category || "Uncategorized"}</p>
          <p className="text-xs text-gray-500 group-hover:text-gray-300 mt-1">By: <Link to={`/profile/${discussion.author?.username || "unknown"}`} className="hover:underline">{discussion.author?.username || "Unknown"}</Link></p>
        </div>
      </Link>
    ));

  const renderDebates = () =>
    sortByPopularity(debates).map((debate) => (
      <Link key={debate._id} to={`/debatepage/${debate._id}?section=debates`} className="block p-4 rounded-xl shadow hover:shadow-lg transition bg-white">
        <h3 className="text-gray-800 font-semibold text-sm">{debate.title || "Untitled Debate"}</h3>
        <p className="text-gray-600 text-xs truncate mt-2">{debate.openingArgument?.substring(0, 60) || "No argument"}...</p>
        <div className="flex justify-between text-xs mt-2">
          <span className="text-teal-500">{debate.category || "Uncategorized"}</span>
          <span className="text-gray-500">By: <Link to={`/profile/${debate.author?.username || "unknown"}`} className="hover:underline">{debate.author?.username || "Unknown"}</Link></span>
        </div>
      </Link>
    ));

  const renderBlogs = () =>
    sortByPopularity(blogs).map((blog) => (
      <Link key={blog._id} to={`/blogpage/${blog._id}?section=blogs`} className="group relative rounded-xl overflow-hidden shadow hover:shadow-lg transition">
        <div className="absolute inset-0 bg-gradient-to-t from-blue-500/80 to-transparent opacity-0 group-hover:opacity-100 transition duration-300"></div>
        <div className="p-3 relative z-10 bg-white">
          <h3 className="text-base font-semibold text-gray-800 group-hover:text-white">{blog.title || "Untitled Blog"}</h3>
          <p className="text-gray-600 group-hover:text-gray-200 mt-2 text-sm">{blog.content?.substring(0, 60) || "No content"}...</p>
          <div className="mt-2 flex justify-between items-center">
            <span className="text-xs text-blue-500 group-hover:text-yellow-300">Votes: {(blog.upvotes || 0) - (blog.downvotes || 0)}</span>
            <span className="text-xs text-gray-500 group-hover:text-gray-300">By: <Link to={`/profile/${blog.author?.username || "unknown"}`} className="hover:underline">{blog.author?.username || "Unknown"}</Link></span>
          </div>
        </div>
      </Link>
    ));

  const seeMoreButton = (path) => (
    <Link to={path} className="flex items-center justify-center bg-gradient-to-r from-blue-500 to-teal-500 text-white rounded-xl shadow hover:shadow-lg transition">
      See More
    </Link>
  );

  const renderSection = () => {
    if (loading) return <p className="text-center mt-8">Loading...</p>;
    if (section === "discussions") return renderDiscussions();
    if (section === "debates") return renderDebates();
    if (section === "blogs") return renderBlogs();
  };

  return (
    <div className="px-2 md:px-8 pt-6 md:pt-10">
      {/* Section Toggle */}
      <div className="flex flex-col md:flex-row gap-4 mb-6 md:ml-64">
        {["discussions", "debates", "blogs"].map((sec) => (
          <button
            key={sec}
            onClick={() => setSection(sec)}
            className={`px-4 py-2 rounded-full text-sm md:text-base ${section === sec ? "bg-blue-500 text-white" : "bg-gray-200 text-gray-800"} transition`}
          >
            {sec.charAt(0).toUpperCase() + sec.slice(1)}
          </button>
        ))}
      </div>

      {/* Content Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 md:ml-64">
        {renderSection()}

        {/* Show See More only if enough items */}
        {section === "discussions" && discussions.length > 9 && seeMoreButton("/discussions")}
        {section === "debates" && debates.length > 9 && seeMoreButton("/debates")}
        {section === "blogs" && blogs.length > 9 && seeMoreButton("/blogs")}
      </div>
    </div>
  );
};

export default Home;
