import React, { useState, useEffect, useMemo } from "react";
import { Link } from "react-router-dom";
import { useDebounce } from "use-debounce";
import API from "../api/"; // Adjust your API import path
import { Card, CardContent } from "@/components/ui/card";

const Debate = () => {
  const [debates, setDebates] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [debouncedSearchQuery] = useDebounce(searchQuery, 300); // 300ms delay
  const [loading, setLoading] = useState(true);

  const fetchDebates = async () => {
    try {
      const { data } = await API.get("/debates");
      setDebates(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Error fetching debates:", error);
      setDebates([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchDebates();

    // Polling every 5 seconds
    const intervalId = setInterval(async () => {
      try {
        const { data } = await API.get("/debates");
        const newDebates = Array.isArray(data) ? data : [];
        // Update only if new data is different
        if (JSON.stringify(newDebates) !== JSON.stringify(debates)) {
          setDebates(newDebates);
        }
      } catch (error) {
        console.error("Polling error:", error);
      }
    }, 5000);

    return () => clearInterval(intervalId);
  }, [debates]);

  // Optimized Filtering Logic
  const filteredDebates = useMemo(() => {
    if (!debouncedSearchQuery.trim()) return debates;

    const lowerQuery = debouncedSearchQuery.toLowerCase();
    return debates.filter((debate) => {
      const title = debate.title?.toLowerCase() || "";
      const argument = debate.openingArgument?.toLowerCase() || "";
      const category = debate.category?.toLowerCase() || "";
      const author = debate.author?.username?.toLowerCase() || "";

      return (
        title.includes(lowerQuery) ||
        argument.includes(lowerQuery) ||
        category.includes(lowerQuery) ||
        author.includes(lowerQuery)
      );
    });
  }, [debouncedSearchQuery, debates]);

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-6">
        <input
          type="text"
          placeholder="Search debates..."
          className="border border-gray-300 rounded-lg px-4 py-2 w-full focus:outline-none focus:ring-2 focus:ring-blue-500"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
        />
      </div>

      {loading ? (
        <p className="text-center text-gray-600">Loading debates...</p>
      ) : filteredDebates.length === 0 ? (
        <p className="text-center text-gray-600">No debates found.</p>
      ) : (
        <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
          {filteredDebates.map((debate) => (
            <Card
              key={debate._id}
              className="rounded-2xl shadow-md hover:shadow-lg transition-shadow duration-300"
            >
              <CardContent className="p-4">
                <Link to={`/debate/${debate._id}`}>
                  <h2 className="text-xl font-semibold mb-2 hover:underline">
                    {debate.title}
                  </h2>
                  <p className="text-gray-700 mb-2">{debate.openingArgument}</p>
                  <div className="flex justify-between text-sm text-gray-500">
                    <span>Category: {debate.category}</span>
                    <span>By: {debate.author?.username}</span>
                  </div>
                </Link>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
};

export default Debate;
