import React, { useEffect, useState, useRef } from "react";
import { NavLink, Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import {
  HomeIcon,
  ChatAlt2Icon,
  BellIcon,
} from "@heroicons/react/outline";
import API from "../api/axiosInstance";

const Navbar = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [notifications, setNotifications] = useState([]);
  const [isNotificationOpen, setIsNotificationOpen] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);

  const notificationRef = useRef(null);

  // Fetch user profile and notifications
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      API.get("/users/profile/me", {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => setUsername(res.data.username))
        .catch((err) => {
          console.error("Error fetching user profile:", err);
          setUsername("");
        });

      API.get("/notifications", {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => {
          setNotifications(res.data);
          setUnreadCount(res.data.filter((notif) => !notif.read).length);
        })
        .catch((err) => console.error("Error fetching notifications:", err));
    }
  }, []);

  // Prevent scroll propagation on notification scroll boundary
  useEffect(() => {
    const handleNotificationScroll = (e) => {
      const element = notificationRef.current;
      if (!element) return;

      const { scrollTop, scrollHeight, clientHeight } = element;
      const delta = e.deltaY;
      const atTop = scrollTop === 0;
      const atBottom = scrollTop + clientHeight >= scrollHeight - 1;

      if ((atTop && delta < 0) || (atBottom && delta > 0)) {
        e.preventDefault();
        e.stopPropagation();
      }
    };

    const handleTouchScroll = (e) => {
      const element = notificationRef.current;
      if (!element) return;
      const { scrollTop, scrollHeight, clientHeight } = element;
      const atTop = scrollTop === 0;
      const atBottom = scrollTop + clientHeight >= scrollHeight - 1;

      if (atTop || atBottom) {
        e.preventDefault();
        e.stopPropagation();
      }
    };

    const notificationElement = notificationRef.current;
    if (notificationElement && isNotificationOpen) {
      notificationElement.addEventListener("wheel", handleNotificationScroll, { passive: false });
      notificationElement.addEventListener("touchmove", handleTouchScroll, { passive: false });
    }

    return () => {
      if (notificationElement) {
        notificationElement.removeEventListener("wheel", handleNotificationScroll);
        notificationElement.removeEventListener("touchmove", handleTouchScroll);
      }
    };
  }, [isNotificationOpen]);

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (notificationRef.current && !notificationRef.current.contains(event.target)) {
        setIsNotificationOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const toggleNotifications = () => {
    setIsNotificationOpen((prev) => !prev);
    if (!isNotificationOpen && unreadCount > 0) {
      setUnreadCount(0);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    setUsername("");
    navigate("/login");
  };

  return (
    <nav className="fixed top-0 left-0 h-full w-64 bg-white shadow-lg flex flex-col">
      <div className="p-4">
        <Link to="/" className="text-2xl font-bold text-blue-600">Debatify</Link>
      </div>

      <div className="flex-1 space-y-4 p-4">
        <NavLink to="/" className="flex items-center p-2 rounded-lg hover:bg-gray-100">
          <HomeIcon className="h-6 w-6" />
          <span>Home</span>
        </NavLink>

        <NavLink to="/debate" className="flex items-center p-2 rounded-lg hover:bg-gray-100">
          <ChatAlt2Icon className="h-6 w-6" />
          <span>Debates</span>
        </NavLink>

        <div className="relative" ref={notificationRef}>
          <motion.button onClick={toggleNotifications} className="flex items-center p-2 rounded-lg hover:bg-gray-100">
            <BellIcon className="h-6 w-6" />
            <span>Notifications</span>
            {unreadCount > 0 && (
              <span className="ml-2 bg-red-500 text-white text-xs rounded-full px-2 py-1">{unreadCount}</span>
            )}
          </motion.button>

          {isNotificationOpen && (
            <motion.div
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              className="absolute left-10 top-12 w-80 bg-white shadow-lg rounded-lg max-h-64 overflow-y-auto border border-gray-200"
            >
              {notifications.length > 0 ? (
                notifications.map((notif) => (
                  <div key={notif._id} className="px-4 py-3 border-b border-gray-200 cursor-pointer hover:bg-gray-100">
                    <p className="text-sm">{notif.message}</p>
                    <p className="text-xs text-gray-500">{new Date(notif.createdAt).toLocaleString()}</p>
                  </div>
                ))
              ) : (
                <p className="px-4 py-3 text-gray-500 text-sm">No notifications yet.</p>
              )}
            </motion.div>
          )}
        </div>
      </div>

      <div className="p-4">
        {username ? (
          <motion.button onClick={handleLogout} className="w-full p-2 rounded-lg hover:bg-gray-200">Logout</motion.button>
        ) : (
          <Link to="/login" className="w-full p-2 rounded-lg hover:bg-gray-200">Login / Register</Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
