// components/MobileFooter.jsx
import { Link } from "react-router-dom";
import { Home, MessageSquare, BookOpen, Users, User } from "lucide-react";

const MobileFooter = () => {
  return (
    <div className="fixed bottom-0 w-full bg-white border-t shadow-md md:hidden z-50">
      <div className="flex justify-between items-center px-6 py-3">
        <Link to="/" className="flex flex-col items-center text-gray-600 hover:text-blue-600">
          <Home size={24} />
          <span className="text-xs mt-1">Home</span>
        </Link>
        <Link to="/discussions" className="flex flex-col items-center text-gray-600 hover:text-blue-600">
          <MessageSquare size={24} />
          <span className="text-xs mt-1">Discussions</span>
        </Link>
        <Link to="/debates" className="flex flex-col items-center text-gray-600 hover:text-blue-600">
          <Users size={24} />
          <span className="text-xs mt-1">Debates</span>
        </Link>
        <Link to="/blogs" className="flex flex-col items-center text-gray-600 hover:text-blue-600">
          <BookOpen size={24} />
          <span className="text-xs mt-1">Blogs</span>
        </Link>
        <Link to="/profile" className="flex flex-col items-center text-gray-600 hover:text-blue-600">
          <User size={24} />
          <span className="text-xs mt-1">Profile</span>
        </Link>
      </div>
    </div>
  );
};

export default MobileFooter;
