// CreateBlog.js (or wherever you handle blog creation)
import React, { useState } from "react";
import API from "../api/axiosInstance";

const CreateBlog = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [files, setFiles] = useState([]);
  const [isPrivate, setIsPrivate] = useState(false);
  const [passcode, setPasscode] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    setFiles(Array.from(e.target.files)); // Convert FileList to array
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    formData.append("isPrivate", isPrivate);
    if (isPrivate) formData.append("passcode", passcode);

    // Append multiple files with the field name "files" (must match backend)
    files.forEach((file) => {
      formData.append("files", file);
    });

    try {
      const token = localStorage.getItem("token");
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 10000); // 10 second timeout

      const response = await API.post("/blogs", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: `Bearer ${token}`,
        },
        signal: controller.signal,
      });

      clearTimeout(timeoutId);
      // Handle successful post (e.g., redirect or clear form)
      setTitle("");
      setContent("");
      setFiles([]);
      setIsPrivate(false);
      setPasscode("");
    } catch (err) {
      if (err.name === "AbortError") {
        setError("Request timed out. Please try again with smaller files or check your connection.");
      } else {
        setError(err.response?.data?.message || "Failed to create blog post");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="mb-4">
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Blog Title"
            className="w-full p-2 border rounded"
            required
          />
        </div>
        <div className="mb-4">
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="Blog Content"
            className="w-full p-2 border rounded"
            rows="6"
            required
          />
        </div>
        <div className="mb-4">
          <input
            type="file"
            multiple
            onChange={handleFileChange}
            className="w-full p-2 border rounded"
          />
        </div>
        <div className="mb-4">
          <label className="flex items-center">
            <input
              type="checkbox"
              checked={isPrivate}
              onChange={(e) => setIsPrivate(e.target.checked)}
              className="mr-2"
            />
            Private Blog
          </label>
        </div>
        {isPrivate && (
          <div className="mb-4">
            <input
              type="text"
              value={passcode}
              onChange={(e) => setPasscode(e.target.value)}
              placeholder="Passcode"
              className="w-full p-2 border rounded"
              required={isPrivate}
            />
          </div>
        )}
        {error && <div className="mb-4 p-2 bg-red-100 text-red-700 rounded">{error}</div>}
        <button
          type="submit"
          disabled={loading}
          className={`w-full p-2 rounded ${loading ? "bg-gray-400" : "bg-blue-600 hover:bg-blue-700"} text-white`}
        >
          {loading ? "Posting..." : "Create Blog"}
        </button>
      </form>
    </div>
  );
};

export default CreateBlog;
