import React, { useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance";

const Auth = () => {
  const [form, setForm] = useState({ username: "", email: "", password: "" });
  const [otp, setOtp] = useState("");
  const [isLogin, setIsLogin] = useState(true);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false); // Line 11
  const [isOtpSent, setIsOtpSent] = useState(false);
  const [isOtpMode, setIsOtpMode] = useState(false);
  const [emailForOtp, setEmailForOtp] = useState("");
  const navigate = useNavigate();

  const handleChange = useCallback((e) => {
    setForm((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  }, []);

  const handleSubmit = useCallback(async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const endpoint = isLogin ? "/auth/login" : "/auth/signup";
      const { data } = await API.post(endpoint, form);
      if (!isLogin) {
        setIsOtpSent(true);
      } else {
        localStorage.setItem("token", data.token);
        localStorage.setItem("name", data.username || "User");
        window.dispatchEvent(new Event("storageUpdate"));
        setSuccess(true); // Set success to true
        setTimeout(() => navigate("/"), 1500);
      }
    } catch (error) {
      console.error("Error in authentication:", error.response?.data || error.message);
      alert(error.response?.data?.message || "Authentication failed!");
    } finally {
      setLoading(false);
    }
  }, [form, isLogin, navigate]);

  const handleVerifyOtp = async () => {
    setLoading(true);
    try {
      await API.post("/auth/verify-email", { email: form.email, otp });
      setSuccess(true); // Set success to true
      setTimeout(() => navigate("/"), 1500);
    } catch (error) {
      alert(error.response?.data?.message || "OTP Verification Failed");
    } finally {
      setLoading(false);
    }
  };

  const handleForgotPassword = async (email) => {
    if (!email) return;
    setLoading(true);
    try {
      await API.post("/auth/request-password-reset", { email });
      setEmailForOtp(email);
      setIsOtpMode(true);
    } catch (err) {
      alert(err.response?.data?.message || "Failed to send OTP");
    } finally {
      setLoading(false);
    }
  };

  const handleResetPassword = async () => {
    setLoading(true);
    try {
      await API.post("/auth/reset-password", { email: emailForOtp, otp, newPassword: form.password });
      setSuccess(true); // Set success to true
      setTimeout(() => navigate("/"), 1500);
    } catch (err) {
      alert(err.response?.data?.message || "Password Reset Failed");
    } finally {
      setLoading(false);
    }
  };

  const toggleMode = useCallback(() => {
    setIsLogin((prev) => !prev);
    setForm({ username: "", email: "", password: "" });
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100 flex items-center justify-center p-4">
      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="bg-white rounded-2xl shadow-lg p-6 w-full max-w-sm sm:max-w-md md:ml-64"
      >
        {success ? (
          <div className="text-green-500 text-center">
            <p>Success! Redirecting...</p>
          </div>
        ) : isOtpSent ? (
          <>
            <h2 className="text-xl font-bold text-gray-800">Verify OTP</h2>
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              className="w-full px-3 py-2 border rounded-xl mt-4"
            />
            <button
              onClick={handleVerifyOtp}
              className="mt-4 w-full bg-blue-500 text-white py-2 rounded-xl"
              disabled={loading}
            >
              {loading ? "Verifying..." : "Verify"}
            </button>
          </>
        ) : isOtpMode ? (
          <>
            <h2 className="text-xl font-bold text-gray-800">Reset Password</h2>
            <input
              type="text"
              placeholder="Enter OTP"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              className="w-full px-3 py-2 border rounded-xl mt-4"
            />
            <input
              type="password"
              placeholder="New Password"
              name="password"
              value={form.password}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-xl mt-4"
            />
            <button
              onClick={handleResetPassword}
              className="mt-4 w-full bg-green-500 text-white py-2 rounded-xl"
              disabled={loading}
            >
              {loading ? "Resetting..." : "Reset Password"}
            </button>
          </>
        ) : (
          <>
            <h2 className="text-xl sm:text-2xl font-extrabold text-gray-800 mb-4">
              {isLogin ? "Welcome Back" : "Create Account"}
              <span className="text-yellow-400">!</span>
            </h2>
            <form onSubmit={handleSubmit} className="space-y-4">
              {!isLogin && (
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Username</label>
                  <input
                    type="text"
                    name="username"
                    placeholder="Choose a username"
                    value={form.username}
                    onChange={handleChange}
                    className="w-full px-3 py-2 border rounded-xl"
                    required
                  />
                </div>
              )}
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                <input
                  type="email"
                  name="email"
                  placeholder="Enter your email"
                  value={form.email}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border rounded-xl"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Password</label>
                <input
                  type="password"
                  name="password"
                  placeholder="Enter your password"
                  value={form.password}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border rounded-xl"
                  required
                />
              </div>
              <button
                type="submit"
                disabled={loading}
                className="w-full bg-gradient-to-r from-blue-500 to-purple-600 text-white py-2 rounded-xl"
              >
                {loading ? "Processing..." : isLogin ? "Login" : "Register"}
              </button>
            </form>
            <p className="text-center text-yellow-500 cursor-pointer mt-4" onClick={toggleMode}>
              {isLogin ? "Don't have an account? Register" : "Already have an account? Login"}
            </p>
            <p className="text-center text-blue-500 cursor-pointer mt-2">
              <span
                onClick={() => {
                  const email = prompt("Enter your registered email:");
                  handleForgotPassword(email);
                }}
              >
                Forgot Password?
              </span>
            </p>
          </>
        )}
      </motion.div>
    </div>
  );
};

export default Auth;
