import React, { useState, useEffect } from 'react';
import { Link, NavLink, useNavigate } from 'react-router-dom';
import axios from 'axios';

const Navbar = () => {
  const [isMobileNavOpen, setIsMobileNavOpen] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Check authentication status
    const token = localStorage.getItem('token');
    setIsAuthenticated(!!token);

    // Fetch notifications
    const fetchNotifications = async () => {
      try {
        const res = await axios.get('/api/notifications');
        setNotifications(res.data);
      } catch (err) {
        console.error('Failed to fetch notifications:', err);
      }
    };

    fetchNotifications();
  }, []);

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      localStorage.removeItem('token');
      setIsAuthenticated(false);
      navigate('/login');
    }
  };

  return (
    <nav className={`bg-white shadow-md px-6 py-4 fixed w-full z-50 transition-all duration-300 ${isMobileNavOpen ? 'h-auto' : 'h-16'}`}>
      <div className="flex items-center justify-between">
        {/* Logo */}
        <Link to="/" className="text-2xl font-bold text-gray-800">
          Debatify
        </Link>

        {/* Desktop Navigation */}
        <div className="hidden md:flex space-x-6 items-center">
          <NavLink to="/home" className={({ isActive }) => isActive ? 'text-blue-500 font-semibold' : 'hover:text-blue-500'}>
            Home
          </NavLink>
          <NavLink to="/discussions" className={({ isActive }) => isActive ? 'text-blue-500 font-semibold' : 'hover:text-blue-500'}>
            Discussions
          </NavLink>
          <NavLink to="/debates" className={({ isActive }) => isActive ? 'text-blue-500 font-semibold' : 'hover:text-blue-500'}>
            Debates
          </NavLink>
          <NavLink to="/blogs" className={({ isActive }) => isActive ? 'text-blue-500 font-semibold' : 'hover:text-blue-500'}>
            Blogs
          </NavLink>
          <NavLink to="/chat" className={({ isActive }) => isActive ? 'text-blue-500 font-semibold' : 'hover:text-blue-500'}>
            Chat
          </NavLink>
          <NavLink to="/notifications" className="relative">
            <span className="hover:text-blue-500">Notifications</span>
            {notifications?.length > 0 && (
              <span className="absolute -top-2 -right-3 bg-red-500 text-white text-xs rounded-full px-2 py-0.5">
                {notifications.length}
              </span>
            )}
          </NavLink>
          {isAuthenticated ? (
            <>
              <NavLink to="/profile" className={({ isActive }) => isActive ? 'text-blue-500 font-semibold' : 'hover:text-blue-500'}>
                Profile
              </NavLink>
              <button onClick={handleLogout} className="hover:text-blue-500">
                Logout
              </button>
            </>
          ) : (
            <>
              <NavLink to="/login" className={({ isActive }) => isActive ? 'text-blue-500 font-semibold' : 'hover:text-blue-500'}>
                Login
              </NavLink>
              <NavLink to="/register" className={({ isActive }) => isActive ? 'text-blue-500 font-semibold' : 'hover:text-blue-500'}>
                Register
              </NavLink>
            </>
          )}
        </div>

        {/* Mobile Hamburger */}
        <div className="md:hidden">
          <button
            onClick={() => setIsMobileNavOpen(!isMobileNavOpen)}
            className="text-gray-800 focus:outline-none"
          >
            {isMobileNavOpen ? (
              <svg className="h-6 w-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            ) : (
              <svg className="h-6 w-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              </svg>
            )}
          </button>
        </div>
      </div>

      {/* Mobile Navigation */}
      {isMobileNavOpen && (
        <div className="flex flex-col mt-4 space-y-4 md:hidden">
          <NavLink to="/home" onClick={() => setIsMobileNavOpen(false)} className="hover:text-blue-500">Home</NavLink>
          <NavLink to="/discussions" onClick={() => setIsMobileNavOpen(false)} className="hover:text-blue-500">Discussions</NavLink>
          <NavLink to="/debates" onClick={() => setIsMobileNavOpen(false)} className="hover:text-blue-500">Debates</NavLink>
          <NavLink to="/blogs" onClick={() => setIsMobileNavOpen(false)} className="hover:text-blue-500">Blogs</NavLink>
          <NavLink to="/chat" onClick={() => setIsMobileNavOpen(false)} className="hover:text-blue-500">Chat</NavLink>
          <NavLink to="/notifications" onClick={() => setIsMobileNavOpen(false)} className="relative hover:text-blue-500">
            Notifications
            {notifications?.length > 0 && (
              <span className="ml-2 bg-red-500 text-white text-xs rounded-full px-2 py-0.5">
                {notifications.length}
              </span>
            )}
          </NavLink>
          {isAuthenticated ? (
            <>
              <NavLink to="/profile" onClick={() => setIsMobileNavOpen(false)} className="hover:text-blue-500">Profile</NavLink>
              <button onClick={handleLogout} className="hover:text-blue-500">Logout</button>
            </>
          ) : (
            <>
              <NavLink to="/login" onClick={() => setIsMobileNavOpen(false)} className="hover:text-blue-500">Login</NavLink>
              <NavLink to="/register" onClick={() => setIsMobileNavOpen(false)} className="hover:text-blue-500">Register</NavLink>
            </>
          )}
        </div>
      )}
    </nav>
  );
};

export default Navbar;
