import React, { useEffect, useState, useRef, useCallback } from "react";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance";
import { PencilIcon, ArrowLeftIcon, ArrowRightIcon, SearchIcon, LockClosedIcon } from "@heroicons/react/outline";

const Blog = () => {
  const navigate = useNavigate();
  const [allBlogs, setAllBlogs] = useState([]);
  const [followingBlogs, setFollowingBlogs] = useState([]);
  const [filteredAllBlogs, setFilteredAllBlogs] = useState([]);
  const [filteredFollowingBlogs, setFilteredFollowingBlogs] = useState([]);
  const [loadingAll, setLoadingAll] = useState(true);
  const [loadingFollowing, setLoadingFollowing] = useState(true);
  const [sortBy, setSortBy] = useState("latest");
  const [currentPageAll, setCurrentPageAll] = useState(0);
  const [currentPageFollowing, setCurrentPageFollowing] = useState(0);
  const [searchQuery, setSearchQuery] = useState("");
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const dropdownRef = useRef(null);
  const allSectionRef = useRef(null);
  const followingSectionRef = useRef(null);
  const blogsPerPage = 9;

  const fetchAllBlogs = useCallback(async () => {
    try {
      setLoadingAll(true);
      const { data } = await API.get("/blogs");
      setAllBlogs(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Error fetching all blogs:", error);
      setAllBlogs([]);
    } finally {
      setLoadingAll(false);
    }
  }, []);

  const fetchFollowingBlogs = useCallback(async () => {
    try {
      setLoadingFollowing(true);
      const { data } = await API.get("/blogs/following");
      setFollowingBlogs(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error("Error fetching following blogs:", error);
      setFollowingBlogs([]);
    } finally {
      setLoadingFollowing(false);
    }
  }, []);

  useEffect(() => {
    fetchAllBlogs();
    fetchFollowingBlogs();
  }, [fetchAllBlogs, fetchFollowingBlogs]);

  useEffect(() => {
    const intervalId = setInterval(async () => {
      try {
        const { data: allData } = await API.get("/blogs");
        const { data: followingData } = await API.get("/blogs/following");
        const newAllBlogs = Array.isArray(allData) ? allData : [];
        const newFollowingBlogs = Array.isArray(followingData) ? followingData : [];

        const existingAllIds = new Set(allBlogs.map((d) => d._id));
        const existingFollowingIds = new Set(followingBlogs.map((d) => d._id));

        const allBlogsToAdd = newAllBlogs.filter((d) => !existingAllIds.has(d._id));
        const followingBlogsToAdd = newFollowingBlogs.filter((d) => !existingFollowingIds.has(d._id));

        if (allBlogsToAdd.length > 0) {
          setAllBlogs((prev) => [...prev, ...allBlogsToAdd]);
        }
        if (followingBlogsToAdd.length > 0) {
          setFollowingBlogs((prev) => [...prev, ...followingBlogsToAdd]);
        }
      } catch (error) {
        console.error("Error polling blogs:", error);
      }
    }, 5000);
    return () => clearInterval(intervalId);
  }, [allBlogs, followingBlogs]);

  const filterBlogs = useCallback(() => {
    if (searchQuery.trim() === "") {
      setFilteredAllBlogs(allBlogs);
      setFilteredFollowingBlogs(followingBlogs);
      setIsDropdownOpen(false);
      setCurrentPageAll(0);
      setCurrentPageFollowing(0);
      return;
    }

    const lowerQuery = searchQuery.toLowerCase();
    const filter = (blogs) =>
      blogs.filter((blog) => {
        const title = blog.title || "";
        const content = blog.content || "";
        const username = blog.author?.username || "";
        return (
          title.toLowerCase().includes(lowerQuery) ||
          content.toLowerCase().includes(lowerQuery) ||
          username.toLowerCase().includes(lowerQuery)
        );
      });

    setFilteredAllBlogs(filter(allBlogs));
    setFilteredFollowingBlogs(filter(followingBlogs));
    setIsDropdownOpen(true);
    setCurrentPageAll(0);
    setCurrentPageFollowing(0);
  }, [searchQuery, allBlogs, followingBlogs]);

  useEffect(() => {
    filterBlogs();
  }, [filterBlogs]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside, { passive: true });
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const sortBlogs = (blogs) =>
    [...blogs].sort((a, b) => {
      if (sortBy === "latest") return new Date(b.createdAt) - new Date(a.createdAt);
      if (sortBy === "oldest") return new Date(a.createdAt) - new Date(b.createdAt);
      if (sortBy === "popularity") {
        const popularityA = (a.upvotes || 0) + (a.downvotes || 0);
        const popularityB = (b.upvotes || 0) + (b.downvotes || 0);
        return popularityB - popularityA;
      }
      return 0;
    });

  const sortedAllBlogs = sortBlogs(filteredAllBlogs);
  const sortedFollowingBlogs = sortBlogs(filteredFollowingBlogs);

  const totalPagesAll = Math.ceil(sortedAllBlogs.length / blogsPerPage);
  const totalPagesFollowing = Math.ceil(sortedFollowingBlogs.length / blogsPerPage);
  const paginatedAllBlogs = sortedAllBlogs.slice(currentPageAll * blogsPerPage, (currentPageAll + 1) * blogsPerPage);
  const paginatedFollowingBlogs = sortedFollowingBlogs.slice(currentPageFollowing * blogsPerPage, (currentPageFollowing + 1) * blogsPerPage);

  const handlePrevPageAll = () => currentPageAll > 0 && setCurrentPageAll((prev) => prev - 1);
  const handleNextPageAll = () => currentPageAll < totalPagesAll - 1 && setCurrentPageAll((prev) => prev + 1);
  const handlePrevPageFollowing = () => currentPageFollowing > 0 && setCurrentPageFollowing((prev) => prev - 1);
  const handleNextPageFollowing = () => currentPageFollowing < totalPagesFollowing - 1 && setCurrentPageFollowing((prev) => prev + 1);

  const handleSearchSubmit = useCallback((e) => {
    e.preventDefault();
    if (filteredAllBlogs.length > 0) {
      navigate(`/blogs/${filteredAllBlogs[0]._id}`);
      setSearchQuery("");
      setIsDropdownOpen(false);
    }
  }, [filteredAllBlogs, navigate]);

  const scrollToSection = (ref) => {
    ref.current.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 via-blue-100 to-green-100">
      <motion.div
        initial={{ opacity: 0, y: 30 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="bg-gradient-to-r from-blue-600 to-green-600 text-white py-12 px-4 md:py-16 md:px-5 text-center shadow-lg relative md:ml-64"
      >
        <div className="absolute top-4 right-4 md:right-5" ref={dropdownRef}>
          <form onSubmit={handleSearchSubmit} className="flex items-center">
            <motion.div whileHover={{ scale: 1.02 }} transition={{ duration: 0.3 }}>
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="Search blogs..."
                className="px-3 py-2 md:px-4 md:py-2 pr-10 w-52 md:w-64 bg-white/20 backdrop-blur-md text-white placeholder-gray-200 rounded-xl border border-white/30 focus:outline-none focus:ring-2 focus:ring-yellow-300 text-sm md:text-base"
              />
              <button
                type="submit"
                className="absolute right-3 top-1/2 transform -translate-y-1/2 text-yellow-300 hover:text-yellow-200"
              >
                <SearchIcon className="h-4 w-4 md:h-5 md:w-5" />
              </button>
            </motion.div>
          </form>
          {isDropdownOpen && (
            <motion.div
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.2 }}
              className="absolute top-12 right-0 w-52 md:w-64 bg-white rounded-xl shadow-lg max-h-80 overflow-y-auto z-40"
            >
              {filteredAllBlogs.map((blog) => (
                <Link
                  key={blog._id}
                  to={`/blogs/${blog._id}`}
                  onClick={() => {
                    setSearchQuery("");
                    setIsDropdownOpen(false);
                  }}
                  className="block px-4 py-2 md:py-3 border-b border-gray-200 hover:bg-gradient-to-r hover:from-blue-500/20 hover:to-green-500/20"
                >
                  <h3 className="text-gray-800 font-semibold text-sm md:text-base">
                    {blog.title || "Untitled Blog"}
                  </h3>
                  <p className="text-gray-600 text-xs md:text-sm truncate">
                    {blog.content ? `${blog.content.substring(0, 30)}...` : "No content"}
                  </p>
                  <div className="text-xs md:text-sm mt-1 text-gray-500">
                    By: {blog.author?.username || "Unknown"}{" "}
                    {blog.isPrivate && <LockClosedIcon className="h-3 w-3 md:h-4 md:w-4 inline text-red-500" />}
                  </div>
                </Link>
              ))}
            </motion.div>
          )}
        </div>
        <motion.div
          animate={{ scale: [1, 1.05, 1], opacity: [0.8, 1, 0.8] }}
          transition={{ duration: 2, repeat: Infinity, ease: "easeInOut" }}
          className="inline-block"
        >
          <PencilIcon className="h-12 w-12 md:h-16 md:w-16 mx-auto mb-4 text-yellow-300" />
        </motion.div>
        <h1 className="text-4xl md:text-6xl font-extrabold tracking-tight mb-4">
          <span className="text-yellow-300">Blog</span> Hub
        </h1>
        <p className="text-base md:text-xl max-w-xl mx-auto opacity-90">
          Share your thoughts, inspire others, and explore a world of ideas.
        </p>
        <div className="mt-4 md:mt-6 space-x-4">
          <button
            onClick={() => scrollToSection(allSectionRef)}
            className="inline-block px-6 py-2 md:px-8 md:py-3 bg-yellow-400 text-blue-900 font-semibold rounded-full shadow-lg hover:bg-yellow-500 text-sm md:text-base"
          >
            All
          </button>
          <Link
            to="/createblog"
