import React, { useState, useRef } from "react";
import API from "../api/axiosInstance";
import { useNavigate } from "react-router-dom";

const CreateBlog = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [files, setFiles] = useState([]); // Array to hold selected files
  const [isPrivate, setIsPrivate] = useState(false);
  const [passcode, setPasscode] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const fileInputRef = useRef(null); // Ref to trigger file input click

  const handleAddFile = () => {
    fileInputRef.current.click(); // Trigger file input click
  };

  const handleFileChange = (e) => {
    const newFiles = Array.from(e.target.files);
    setFiles((prevFiles) => [...prevFiles, ...newFiles]); // Append new files to existing ones
    e.target.value = null; // Reset input so same file can be re-selected
    // console.log("Current files:", files.map(f => ({ name: f.name, size: f.size })));
  };

  const handleRemoveFile = (indexToRemove) => {
    setFiles((prevFiles) => prevFiles.filter((_, index) => index !== indexToRemove));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    formData.append("isPrivate", isPrivate.toString());
    if (isPrivate) formData.append("passcode", passcode);

    files.forEach((file, index) => {
      formData.append("files", file);
      // console.log(`Appending file ${index + 1}: ${file.name}`);
    });

    for (let pair of formData.entries()) {
      // console.log(`${pair[0]}:`, pair[1]);
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("Please log in to create a blog post");
      }

      const controller = new AbortController();
      const timeoutId = setTimeout(() => {
        controller.abort();
        // console.log("Request timed out after 15s");
      }, 15000);

      const response = await API.post("/blogs", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: `Bearer ${token}`,
        },
        signal: controller.signal,
      });

      clearTimeout(timeoutId);
      console.log("Blog created successfully:", response.data);

      setTitle("");
      setContent("");
      setFiles([]);
      setIsPrivate(false);
      setPasscode("");
      navigate("/blog");
    } catch (err) {
      console.error("Error in blog creation:", err);
      if (err.name === "AbortError") {
        setError("Request timed out. Try smaller files or check your connection.");
      } else if (err.response) {
        setError(err.response.data.message || "Failed to create blog post");
      } else {
        setError(err.message || "An unexpected error occurred");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Create New Blog</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="mb-4">
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Blog Title"
            className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        <div className="mb-4">
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="Blog Content"
            className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            rows="6"
            required
          />
        </div>
        <div className="mb-4">
          {/* Hidden file input */}
          <input
            type="file"
            ref={fileInputRef}
            onChange={handleFileChange}
            className="hidden"
            accept="image/*,video/*,audio/*,.pdf"
          />
          <button
            type="button"
            onClick={handleAddFile}
            className="px-4 py-2 bg-blue-50 text-blue-700 rounded border border-blue-200 hover:bg-blue-100 transition-colors"
          >
            Add File
          </button>
          {files.length > 0 && (
            <ul className="mt-2 space-y-2">
              {files.map((file, index) => (
                <li key={index} className="flex items-center justify-between p-2 bg-gray-100 rounded">
                  <span className="text-sm text-gray-700 truncate max-w-xs">{file.name}</span>
                  <button
                    type="button"
                    onClick={() => handleRemoveFile(index)}
                    className="text-red-500 hover:text-red-700 font-semibold"
                  >
                    Remove
                  </button>
                </li>
              ))}
            </ul>
          )}
        </div>
        <div className="mb-4">
          <label className="flex items-center">
            <input
              type="checkbox"
              checked={isPrivate}
              onChange={(e) => setIsPrivate(e.target.checked)}
              className="mr-2 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700">Private Blog</span>
          </label>
        </div>
        {isPrivate && (
          <div className="mb-4">
            <input
              type="text"
              value={passcode}
              onChange={(e) => setPasscode(e.target.value)}
              placeholder="Passcode"
              className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
              required={isPrivate}
            />
          </div>
        )}
        {error && (
          <div className="mb-4 p-3 bg-red-100 text-red-700 rounded border border-red-300">
            {error}
          </div>
        )}
        <button
          type="submit"
          disabled={loading}
          className={`w-full p-2 rounded font-semibold text-white transition-colors ${
            loading ? "bg-gray-400 cursor-not-allowed" : "bg-blue-600 hover:bg-blue-700"
          }`}
        >
          {loading ? "Posting..." : "Create Blog"}
        </button>
      </form>
    </div>
  );
};

export default CreateBlog;
