import React, { useEffect, useState } from "react";
import API from "../api/axiosInstance";

const Home = () => {
  const [combinedData, setCombinedData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAllContent = async () => {
      try {
        setLoading(true);
        const [discussionsRes, debatesRes, blogsRes] = await Promise.all([
          API.get("/discussions"),
          API.get("/debates"),
          API.get("/blogs"),
        ]);

        const discussions = Array.isArray(discussionsRes.data) ? discussionsRes.data : [];
        const debates = Array.isArray(debatesRes.data) ? debatesRes.data : [];
        const blogs = Array.isArray(blogsRes.data) ? blogsRes.data : [];

        // Tag each item so you know where it comes from
        const taggedDiscussions = discussions.map(item => ({ ...item, type: "discussion" }));
        const taggedDebates = debates.map(item => ({ ...item, type: "debate" }));
        const taggedBlogs = blogs.map(item => ({ ...item, type: "blog" }));

        // Combine and sort by createdAt (latest first)
        const combined = [...taggedDiscussions, ...taggedDebates, ...taggedBlogs].sort(
          (a, b) => new Date(b.createdAt) - new Date(a.createdAt)
        );

        setCombinedData(combined);
      } catch (error) {
        console.error("Error fetching content:", error);
        setCombinedData([]);
      } finally {
        setLoading(false);
      }
    };

    fetchAllContent();
  }, []);

  if (loading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6">Latest Activity</h1>
      {combinedData.length === 0 ? (
        <p className="text-center text-gray-500">No content available.</p>
      ) : (
        combinedData.map((item) => (
          <div
            key={item._id}
            className="bg-white shadow-md rounded-2xl p-4 mb-6 border hover:shadow-lg transition-all"
          >
            <p className="text-sm text-gray-500 capitalize mb-2">{item.type}</p>
            <h2 className="text-xl font-semibold">{item.title || item.topic}</h2>
            <p className="mt-2 text-gray-700">
              {item.content?.slice(0, 150) ||
                item.description?.slice(0, 150) ||
                "No preview available"}{" "}
              ...
            </p>
            <div className="mt-4 text-gray-400 text-sm">
              Posted on {new Date(item.createdAt).toLocaleDateString()}
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default Home;
