// src/components/Navbar.js
import React, { useEffect, useState, useRef, useCallback } from "react";
import { NavLink, Link, useNavigate, useLocation } from "react-router-dom";
import { motion } from "framer-motion";
import {
  HomeIcon,
  ChatAlt2Icon,
  PencilIcon,
  UserIcon,
  SearchIcon,
  BellIcon,
  MenuIcon,
  XIcon,
} from "@heroicons/react/outline";
import API from "../api/axiosInstance";

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [username, setUsername] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [isNotificationOpen, setIsNotificationOpen] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [isNavbarOpen, setIsNavbarOpen] = useState(false);
  const searchRef = useRef(null);
  const notificationRef = useRef(null);
  const navbarRef = useRef(null);
  const lastNotificationIdRef = useRef(null);

  // Format notification messages
  const formatNotificationMessage = useCallback((notif) => {
    const actor = notif.user?.username || "Someone";
    const targetTitle = notif.target?.title || "Untitled";
    const targetType = notif.target?.type || "post";
    switch (notif.type) {
      case "follow":
        return `${actor} followed you`;
      case "unfollow":
        return `${actor} unfollowed you`;
      case "upvote":
        return `${actor} upvoted your ${targetType} "${targetTitle}"`;
      case "downvote":
        return `${actor} downvoted your ${targetType} "${targetTitle}"`;
      case "comment":
        return `${actor} commented "${notif.comment?.text || "something"}" on your ${targetType} "${targetTitle}"`;
      case "comment_like":
        return `${actor} liked your comment at ${targetType} "${targetTitle}"`;
      default:
        return notif.message || "New notification";
    }
  }, []);

  // Determine redirect path for notifications
  const getRedirectPath = useCallback(
    (notif) => {
      switch (notif.type) {
        case "follow":
        case "unfollow":
          return `/profile/${username}`;
        case "upvote":
        case "downvote":
          if (notif.target?.type === "debate") return `/debatepage/${notif.target?.id}`;
          if (notif.target?.type === "discussion") return `/discussionpage/${notif.target?.id}`;
          if (notif.target?.type === "blog") return `/blogpage/${notif.target?.id}`;
          return "/";
        case "comment":
        case "comment_like":
          if (notif.target?.type === "debate") return `/debatepage/${notif.target?.id}`;
          if (notif.target?.type === "discussion") return `/discussionpage/${notif.target?.id}`;
          return "/";
        default:
          return "/";
      }
    },
    [username]
  );

  // Fetch notifications from API
  const fetchNotifications = useCallback(async () => {
    const token = localStorage.getItem("token");
    if (!token) return;
    try {
      const res = await API.get("/notifications", {
        headers: { Authorization: `Bearer ${token}` },
      });
      const formattedNotifications = res.data.map((notif) => ({
        ...notif,
        message: formatNotificationMessage(notif),
        redirectTo: getRedirectPath(notif),
      }));
      formattedNotifications.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
      const latestId = formattedNotifications[0]?._id;
      if (lastNotificationIdRef.current !== latestId) {
        setNotifications(formattedNotifications);
        setUnreadCount(formattedNotifications.filter((n) => !n.read).length);
        lastNotificationIdRef.current = latestId;
      }
    } catch (err) {
      console.error("Error fetching notifications:", err);
    }
  }, [formatNotificationMessage, getRedirectPath]);

  // Initial setup for user profile and notifications
  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      API.get("/users/profile/me", {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => setUsername(res.data.username))
        .catch((err) => {
          console.error("Error fetching user profile:", err);
          setUsername("");
        });
      fetchNotifications();
      const intervalId = setInterval(() => {
        fetchNotifications();
      }, 1000);
      return () => clearInterval(intervalId);
    }
  }, [fetchNotifications]);

  // Prevent scrolling on navbar
  useEffect(() => {
    const preventScroll = (e) => {
      if (notificationRef.current && notificationRef.current.contains(e.target)) {
        return;
      }
      e.preventDefault();
      e.stopPropagation();
    };
    const navbarElement = navbarRef.current;
    if (navbarElement) {
      navbarElement.addEventListener("wheel", preventScroll, { passive: false });
      navbarElement.addEventListener("touchmove", preventScroll, { passive: false });
    }
    return () => {
      if (navbarElement) {
        navbarElement.removeEventListener("wheel", preventScroll);
        navbarElement.removeEventListener("touchmove", preventScroll);
      }
    };
  }, []);

  // Close dropdowns when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setIsSearchOpen(false);
      }
      if (
        notificationRef.current &&
        !notificationRef.current.contains(event.target)
      ) {
        setIsNotificationOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  // Handle window resize for mobile detection
  useEffect(() => {
    const handleResize = () => {
      const mobile = window.innerWidth < 768;
      setIsMobile(mobile);
      if (!mobile) {
        setIsNavbarOpen(false); // Reset navbar state on desktop
      }
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Close navbar on route change in mobile view
  useEffect(() => {
    if (isMobile) {
      setIsNavbarOpen(false);
    }
  }, [location, isMobile]);

  // Handle search functionality
  const handleSearch = async (query) => {
    setSearchQuery(query);
    if (query.trim() === "") {
      setSearchResults([]);
      setIsSearchOpen(false);
      return;
    }
    try {
      const { data } = await API.get(`/users/search?query=${query}`);
      setSearchResults(data);
      setIsSearchOpen(data.length > 0);
    } catch (error) {
      console.error("Error searching users:", error);
      setSearchResults([]);
    }
  };

  // Mark notifications as read
  const markNotificationsAsRead = async () => {
    const token = localStorage.getItem("token");
    try {
      await API.put(
        "/notifications/mark-read",
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setNotifications((prev) =>
        prev.map((notif) => ({ ...notif, read: true }))
      );
      setUnreadCount(0);
    } catch (error) {
      console.error("Error marking notifications as read:", error);
    }
  };

  // Toggle notifications dropdown
  const toggleNotifications = () => {
    setIsNotificationOpen((prev) => !prev);
    if (!isNotificationOpen && unreadCount > 0) {
      markNotificationsAsRead();
    }
  };

  // Handle logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    setUsername("");
    navigate("/login");
  };

  // Group notifications by time
  const getNotificationGroup = (date) => {
    const today = new Date();
    const notificationDate = new Date(date);
    const diffTime = today - notificationDate;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    if (diffDays <= 1) return "Today";
    if (diffDays <= 2) return "Yesterday";
    if (diffDays <= 7) return "This week";
    if (diffDays <= 30) return "This month";
    return "Older";
  };

  const groupedNotifications = notifications.reduce((acc, notif) => {
    const group = getNotificationGroup(notif.createdAt);
    if (!acc[group]) acc[group] = [];
    acc[group].push(notif);
    return acc;
  }, {});

  const groupOrder = ["Today", "Yesterday", "This week", "This month", "Older"];

  return (
    <>
      {/* Hamburger menu button for mobile */}
      {isMobile && (
        <button
          onClick={() => setIsNavbarOpen(true)}
          className="fixed top-4 left-4 z-50 p-2 rounded-md bg-gray-100 hover:bg-gray-200"
          aria-label="Open navigation menu"
        >
          <MenuIcon className="h-6 w-6" />
        </button>
      )}
      {/* Navbar */}
      <nav
        ref={navbarRef}
        className={`fixed top-0 left-0 h-full bg-white text-black flex flex-col shadow-lg z-50 ${
          isMobile ? (isNavbarOpen ? "w-full" : "hidden") : "w-64"
        }`}
      >
        {/* Close button for mobile */}
        {isMobile && (
          <button
            onClick={() => setIsNavbarOpen(false)}
            className="absolute top-4 right-4 p-2 rounded-md bg-gray-100 hover:bg-gray-200"
            aria-label="Close navigation menu"
          >
            <XIcon className="h-6 w-6" />
          </button>
        )}
        {/* Navbar header */}
        <div className="p-4">
          <Link to="/" className="text-2xl font-bold text-blue-600">
            Debatify
          </Link>
        </div>
        {/* Navigation links */}
        <div className="flex-1 space-y-4 p-4">
          <NavLink
            to="/"
            className={({ isActive }) =>
              `flex items-center space-x-2 p-2 rounded-lg ${
                isActive
                  ? "bg-gray-200 text-blue-600"
                  : "text-black hover:bg-gray-100"
              } transition-colors duration-200`
            }
          >
            <HomeIcon className="h-6 w-6" />
            <span className="text-lg font-medium">Home</span>
          </NavLink>
          <NavLink
            to="/debate"
            className={({ isActive }) =>
              `flex items-center space-x-2 p-2 rounded-lg ${
                isActive
                  ? "bg-gray-200 text-blue-600"
                  : "text-black hover:bg-gray-100"
              } transition-colors duration-200`
            }
          >
            <ChatAlt2Icon className="h-6 w-6" />
            <span className="text-lg font-medium">Debates</span>
          </NavLink>
          <NavLink
            to="/blog"
            className={({ isActive }) =>
              `flex items-center space-x-2 p-2 rounded-lg ${
                isActive
                  ? "bg-gray-200 text-blue-600"
                  : "text-black hover:bg-gray-100"
              } transition-colors duration-200`
            }
          >
            <PencilIcon className="h-6 w-6" />
            <span className="text-lg font-medium">Blogs</span>
          </NavLink>
          {/* Notifications */}
          {username && (
            <div className="relative" ref={notificationRef}>
              <motion.button
                onClick={toggleNotifications}
                className="flex items-center space-x-2 p-2 rounded-lg text-black hover:bg-gray-100 transition-colors duration-200 w-full"
                animate={unreadCount > 0 ? { x: [0, 5, -5, 0] } : {}}
                transition={
                  unreadCount > 0
                    ? { duration: 0.5, repeat: Infinity, ease: "easeInOut" }
                    : {}
                }
              >
                <BellIcon className="h-6 w-6" />
                <span className="text-lg font-medium">Notifications</span>
                {unreadCount > 0 && (
                  <span className="ml-2 bg-red-500 text-white text-xs rounded-full px-2 py-1">
                    {unreadCount}
                  </span>
                )}
              </motion.button>
              {isNotificationOpen && (
                <motion.div
                  initial={{ opacity: 0, x: -10 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: -10 }}
                  transition={{ duration: 0.2 }}
                  className="absolute left-8 bottom-[-20vh] transform -translate-y-1/2 ml-2 w-96 bg-white rounded-lg shadow-lg max-h-[50vh] overflow-y-auto z-50"
                >
                  {notifications.length > 0 ? (
                    groupOrder.map((group) =>
                      groupedNotifications[group] ? (
                        <div key={group}>
                          <h3 className="px-4 py-2 text-sm font-semibold text-gray-600 sticky top-0 bg-white">
                            {group}
                          </h3>
                          {groupedNotifications[group].map((notif) => (
                            <div
                              key={notif._id}
                              className={`px-4 py-3 border-b border-gray-200 ${
                                !notif.read ? "bg-gray-50" : ""
                              } hover:bg-gray-100`}
                            >
                              <p className="text-sm text-gray-800">
                                {notif.message}
                              </p>
                              <p className="text-xs text-gray-500">
                                {new Date(notif.createdAt).toLocaleString()}
                              </p>
                            </div>
                          ))}
                        </div>
                      ) : null
                    )
                  ) : (
                    <p className="px-4 py-3 text-gray-500 text-sm">
                      No notifications yet.
                    </p>
                  )}
                </motion.div>
              )}
            </div>
          )}
          {/* Search bar */}
          <div className="relative" ref={searchRef}>
            <motion.div
              whileHover={{ scale: 1.02 }}
              transition={{ duration: 0.3 }}
              className="flex items-center space-x-2 p-2 rounded-lg bg-gray-100 text-black transition-colors duration-200"
            >
              <SearchIcon className="h-6 w-6 text-blue-600" />
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => handleSearch(e.target.value)}
                placeholder=" Search users..."
                className="bg-transparent focus:outline-none w-full text-black placeholder-gray-500 text-lg font-medium"
                aria-label="Search users by username"
              />
            </motion.div>
            {isSearchOpen && searchResults.length > 0 && (
              <motion.div
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -10 }}
                transition={{ duration: 0.2 }}
                className="absolute top-12 left-0 w-full bg-white rounded-lg shadow-lg max-h-60 overflow-y-auto z-50 border border-gray-200"
              >
                {searchResults.map((user) => (
                  <Link
                    key={user._id}
                    to={`/profile/${user.username}`}
                    onClick={() => {
                      setSearchQuery("");
                      setIsSearchOpen(false);
                    }}
                    className="block px-4 py-2 hover:bg-gradient-to-r hover:from-blue-500/20 hover:to-purple-500/20 transition-colors duration-200 text-black border-b border-gray-200 last:border-b-0"
                  >
                    <h3 className="text-gray-800 font-semibold">
                      {user.username}
                    </h3>
                    <p className="text-gray-600 text-sm truncate">
                      {user.bio
                        ? user.bio.substring(0, 30) + "..."
                        : "No bio available"}
                    </p>
                    <div className="flex justify-between text-sm mt-1">
                      <span className="text-blue-500">
                        Role: {user.role || "User"}
                      </span>
                      <span className="text-gray-500">
                        Joined:{" "}
                        {user.createdAt
                          ? new Date(user.createdAt).toLocaleDateString()
                          : "Unknown"}
                      </span>
                    </div>
                  </Link>
                ))}
              </motion.div>
            )}
          </div>
          {/* Profile link */}
          {username && (
            <NavLink
              to={`/profile/${username}`}
              className={({ isActive }) =>
                `flex items-center space-x-2 p-2 rounded-lg ${
                  isActive
                    ? "bg-gray-200 text-blue-600"
                    : "text-black hover:bg-gray-100"
                } transition-colors duration-200`
              }
            >
              <UserIcon className="h-6 w-6" />
              <span className="text-lg font-medium">Profile</span>
            </NavLink>
          )}
        </div>
        {/* Logout/Login button */}
        <div className="p-4">
          {localStorage.getItem("token") ? (
            <motion.button
              whileHover={{ scale: 1.05 }}
              onClick={handleLogout}
              className="w-full text-left p-2 rounded-lg text-black hover:bg-gray-200 transition-colors duration-200"
            >
              Logout
            </motion.button>
          ) : (
            <Link
              to="/login"
              className="w-full text-left p-2 rounded-lg text-black hover:bg-gray-200 transition-colors duration-200"
            >
              Login / Register
            </Link>
          )}
        </div>
      </nav>
    </>
  );
};

export default Navbar;
