import React, { useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance";
import { LockClosedIcon, UserIcon } from "@heroicons/react/outline";

const Auth = () => {
  const [form, setForm] = useState({ username: "", email: "", password: "" });
  const [isLogin, setIsLogin] = useState(true);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const navigate = useNavigate();

  const handleChange = useCallback((e) => {
    setForm((prev) => ({ ...prev, [e.target.name]: e.target.value }));
  }, []);

  const handleSubmit = useCallback(async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const endpoint = isLogin ? "/auth/login" : "/auth/signup";
      const { data } = await API.post(endpoint, form);
      localStorage.setItem("token", data.token);
      localStorage.setItem("name", data.name || "User");
      window.dispatchEvent(new Event("storageUpdate"));
      setSuccess(true);
      setTimeout(() => navigate("/"), 1500);
    } catch (error) {
      console.error("Error in authentication:", error.response?.data || error.message);
      alert(error.response?.data?.message || "Authentication failed!");
    } finally {
      setLoading(false);
    }
  }, [form, isLogin, navigate]);

  const toggleMode = useCallback(() => {
    setIsLogin((prev) => !prev);
    setForm({ username: "", email: "", password: "" });
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100 flex items-center justify-center p-4">
      <motion.div
        initial={{ opacity: 0, y: 50 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.8, ease: "easeOut" }}
        className="bg-white rounded-2xl shadow-lg p-6 w-full max-w-sm sm:max-w-md md:ml-64"
      >
        <motion.div
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ duration: 0.5 }}
          className="text-center"
        >
          <h2 className="text-xl sm:text-2xl font-extrabold text-gray-800 mb-4">
            {isLogin ? "Welcome Back" : "Create Account"}
            <span className="text-yellow-400">!</span>
          </h2>
          {isLogin ? (
            <LockClosedIcon className="h-8 w-8 sm:h-10 sm:w-10 text-blue-500 mx-auto mb-4 animate-pulse" />
          ) : (
            <UserIcon className="h-8 w-8 sm:h-10 sm:w-10 text-green-500 mx-auto mb-4 animate-pulse" />
          )}
        </motion.div>

        {success ? (
          <motion.div
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.5 }}
            className="text-center"
          >
            <LockClosedIcon className="h-12 w-12 sm:h-16 sm:w-16 text-green-500 mx-auto mb-4 animate-pulse" />
            <p className="text-base sm:text-lg font-semibold text-green-600">
              {isLogin ? "Logged In!" : "Account Created!"}
            </p>
            <p className="text-gray-500 mt-2 text-sm sm:text-base">Redirecting to home...</p>
          </motion.div>
        ) : (
          <form onSubmit={handleSubmit} className="space-y-4">
            {!isLogin && (
              <motion.div
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: 0.1, duration: 0.5 }}
              >
                <label htmlFor="username" className="block text-sm font-medium text-gray-700 mb-1">
                  Username
                </label>
                <input
                  type="text"
                  id="username"
                  name="username"
                  placeholder="Choose a username"
                  value={form.username}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent text-sm sm:text-base"
                  required
                />
              </motion.div>
            )}
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: isLogin ? 0.1 : 0.2, duration: 0.5 }}
            >
              <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
                Email
              </label>
              <input
                type="email"
                id="email"
                name="email"
                placeholder="Enter your email"
                value={form.email}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent text-sm sm:text-base"
                required
              />
            </motion.div>
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: isLogin ? 0.2 : 0.3, duration: 0.5 }}
            >
              <label htmlFor="password" className="block text-sm font-medium text-gray-700 mb-1">
                Password
              </label>
              <input
                type="password"
                id="password"
                name="password"
                placeholder="Enter your password"
                value={form.password}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent text-sm sm:text-base"
                required
              />
            </motion.div>
            <motion.button
              type="submit"
              disabled={loading}
              whileHover={{ scale: loading ? 1 : 1.05 }}
              whileTap={{ scale: loading ? 1 : 0.95 }}
              transition={{ duration: 0.3 }}
              className={`w-full bg-gradient-to-r from-${
                isLogin ? "blue-500" : "green-500"
              } to-${isLogin ? "purple-600" : "emerald-600"} text-white py-2 rounded-xl shadow-md ${
                loading ? "opacity-70 cursor-not-allowed" : "hover:from-" + (isLogin ? "blue-600" : "green-600") + " hover:to-" + (isLogin ? "purple-700" : "emerald-700")
              }`}
            >
              {loading ? (
                <span className="flex items-center justify-center">
                  <svg
                    className="animate-spin h-5 w-5 mr-2 text-white"
                    xmlns="http://www.w3.org/2000/svg"
                    fill="none"
                    viewBox="0 0 24 24"
                  >
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
                    <path
                      className="opacity-75"
                      fill="currentColor"
                      d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                    />
                  </svg>
                  {isLogin ? "Logging In..." : "Registering..."}
                </span>
              ) : isLogin ? "Login" : "Register"}
            </motion.button>
            <motion.p
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 0.4, duration: 0.5 }}
              className="text-center text-yellow-500 cursor-pointer hover:text-yellow-400 text-sm sm:text-base"
              onClick={toggleMode}
            >
              {isLogin ? "Don't have an account? Register" : "Already have an account? Login"}
            </motion.p>
          </form>
        )}
      </motion.div>
    </div>
  );
};

export default Auth;
