import React, { useEffect, useState } from "react"; // Added useState and useEffect
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { ChatAlt2Icon, UserIcon } from "@heroicons/react/outline"; // Added UserIcon
import API from "../api/axiosInstance"; // Import API to fetch username if needed

const Navbar = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState(""); // State to hold username

  // Fetch username from localStorage or API
  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    if (storedUsername) {
      setUsername(storedUsername);
    } else {
      // If username isn‚Äôt in localStorage, fetch it using the userId
      const token = localStorage.getItem("token");
      const userId = localStorage.getItem("userId");
      if (token && userId) {
        API.get("/users/profile/me", { // Assuming an endpoint like this exists
          headers: { Authorization: `Bearer ${token}` },
        })
          .then((res) => {
            setUsername(res.data.username);
            localStorage.setItem("username", res.data.username); // Cache it
          })
          .catch((err) => console.error("Error fetching username:", err));
      }
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    localStorage.removeItem("username"); // Clear username if stored
    navigate("/login");
  };

  const handleProfileClick = () => {
    if (username) {
      navigate(`/profile/${username}`);
    }
  };

  return (
    <nav className="fixed top-0 left-0 w-full bg-gradient-to-r from-blue-600 via-purple-600 to-pink-600 text-white p-4 shadow-lg z-50">
      <div className="container mx-auto flex justify-between items-center">
        <div className="flex items-center space-x-10">
          <motion.div
            whileHover={{ scale: 1.01 }}
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6, ease: "easeOut" }}
          >
            <Link
              to="/"
              className="text-2xl font-extrabold text-yellow-300 hover:text-yellow-250 transition-colors duration-300"
            >
              Homeüè†!
            </Link>
          </motion.div>
          <motion.div
            whileHover={{ scale: 1.01 }}
            whileTap={{ scale: 0.95 }}
            transition={{ duration: 0.3 }}
          >
            <Link
              to="/debate"
              className="flex items-center text-2xl font-extrabold text-yellow-300 hover:text-yellow-200 transition-colors duration-300"
            >
              Debate-Hub!üéôÔ∏è
              <ChatAlt2Icon className="h-8 w-8 text-teal-300 hover:text-teal-200 transition-colors duration-300" />
            </Link>
          </motion.div>
        </div>

        <div className="flex space-x-4 items-center">
          {localStorage.getItem("token") ? (
            <>
              {/* Profile Button */}
              <motion.div whileHover={{ scale: 1.05 }} transition={{ duration: 0.3 }}>
                <button
                  onClick={handleProfileClick}
                  className="flex items-center px-4 py-2 bg-gradient-to-r from-teal-500 to-cyan-600 text-white rounded-xl shadow-md hover:from-teal-600 hover:to-cyan-700 transition-all duration-300 transform hover:shadow-lg"
                >
                  <UserIcon className="h-6 w-6 mr-2" />
                  <span className="text-lg font-bold">{username ? username[0].toUpperCase() : "U"}</span>
                </button>
              </motion.div>

              {/* Logout Button */}
              <motion.div whileHover={{ scale: 1.05 }} transition={{ duration: 0.3 }}>
                <button
                  onClick={handleLogout}
                  className="px-5 py-2 bg-gradient-to-r from-red-500 to-rose-600 text-white rounded-xl shadow-md hover:from-red-600 hover:to-rose-700 transition-all duration-300 transform hover:shadow-lg"
                >
                  Logout
                </button>
              </motion.div>
            </>
          ) : (
            <motion.div whileHover={{ scale: 1.05 }} transition={{ duration: 0.1 }}>
              <Link
                to="/login"
                className="px-5 py-2 bg-gradient-to-r from-blue-500 to-indigo-600 text-white rounded-xl shadow-md hover:from-blue-600 hover:to-indigo-700 transition-all duration-300 transform hover:shadow-lg"
              >
                Login / Register
              </Link>
            </motion.div>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
