import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import API from "../api/axiosInstance";
import { motion } from "framer-motion";
import { ThumbUpIcon, ThumbDownIcon, ChatIcon, HeartIcon } from "@heroicons/react/solid";

const DebatePage = () => {
  const { id } = useParams();
  const [debate, setDebate] = useState(null);
  const [comment, setComment] = useState("");
  const [stance, setStance] = useState("");
  const [userStanceLocked, setUserStanceLocked] = useState(null);
  const [comments, setComments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [voting, setVoting] = useState(false);
  const [commenting, setCommenting] = useState(false);
  const [error, setError] = useState(null);
  const [sortBy, setSortBy] = useState("latest");

  // Initial fetch effect
  useEffect(() => {
    const fetchDebate = async () => {
      try {
        const { data } = await API.get(`/debates/${id}`);
        setDebate(data);
        setComments(data.comments || []);
        const token = localStorage.getItem("token");
        if (token) {
          const userId = JSON.parse(atob(token.split(".")[1])).userId;
          const userComments = data.comments.filter((c) => c.user._id === userId);
          if (userComments.length > 0) {
            setUserStanceLocked(userComments[0].stance);
          }
        }
      } catch (error) {
        console.error("Error fetching debate:", error);
        setError("Failed to load debate.");
      }
    };

    setLoading(true);
    fetchDebate().finally(() => setLoading(false));
  }, [id]);

  // Polling effect
  useEffect(() => {
    const intervalId = setInterval(async () => {
      try {
        const { data } = await API.get(`/debates/${id}`);
        const newComments = data.comments || [];
        const existingCommentIds = new Set(comments.map((c) => c._id));
        const commentsToAdd = newComments.filter((c) => !existingCommentIds.has(c._id));

        if (commentsToAdd.length > 0) {
          setComments((prevComments) => [...prevComments, ...commentsToAdd]);
        }
      } catch (error) {
        console.error("Error polling comments:", error);
      }
    }, 5000);

    return () => clearInterval(intervalId);
  }, [id, comments]); // Include comments as a dependency

  const handleVote = async (voteType) => {
    try {
      setVoting(true);
      setError(null);
      const token = localStorage.getItem("token");
      if (!token) {
        setError("You must be logged in to vote.");
        return;
      }
      const endpoint = voteType === "upvote" ? `/debates/${id}/upvote` : `/debates/${id}/downvote`;
      const { data } = await API.post(endpoint, {}, { headers: { Authorization: `Bearer ${token}` } });

      setDebate((prevDebate) => ({
        ...prevDebate,
        upvotes: data.upvotes || 0,
        downvotes: data.downvotes || 0,
        upvotedBy: data.upvotedBy || prevDebate.upvotedBy,
        downvotedBy: data.downvotedBy || prevDebate.downvotedBy,
      }));
    } catch (error) {
      console.error("Error voting:", error);
      setError(error.response?.data?.message || "Failed to vote.");
    } finally {
      setVoting(false);
    }
  };

  const handleComment = async () => {
    try {
      setCommenting(true);
      setError(null);
      const token = localStorage.getItem("token");
      if (!token) {
        setError("You must be logged in to comment.");
        return;
      }
      if (!comment.trim()) {
        setError("Comment cannot be empty.");
        return;
      }
      if (!stance && !userStanceLocked) {
        setError("Please select your stance.");
        return;
      }

      const finalStance = userStanceLocked || stance;
      const { data } = await API.post(
        `/debates/${id}/comment`,
        { text: comment, stance: finalStance },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const newComment = data.comments[data.comments.length - 1];
      setComments((prevComments) => [...prevComments, newComment]);
      setComment("");
      if (!userStanceLocked) setUserStanceLocked(finalStance);
    } catch (error) {
      console.error("Error commenting:", error);
      setError(error.response?.data?.message || "Failed to comment.");
    } finally {
      setCommenting(false);
    }
  };

  const handleCommentLike = async (commentId) => {
    try {
      setError(null);
      const token = localStorage.getItem("token");
      if (!token) {
        setError("You must be logged in to like a comment.");
        return;
      }
      const { data } = await API.post(
        `/debates/${id}/comment/${commentId}/like`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setComments((prevComments) =>
        prevComments.map((c) =>
          c._id === commentId ? { ...c, likes: data.likes, likedBy: data.likedBy } : c
        )
      );
    } catch (error) {
      console.error("Error liking comment:", error);
      setError(error.response?.data?.message || "Failed to like comment.");
    }
  };

  const sortedComments = [...comments].sort((a, b) => {
    if (sortBy === "mostLiked") {
      return (b.likes || 0) - (a.likes || 0);
    }
    return new Date(b.createdAt) - new Date(a.createdAt);
  });

  if (loading) return <p className="text-center mt-5 text-gray-600">Loading debate...</p>;
  if (!debate) return <p className="text-center mt-5 text-red-500">Debate not found.</p>;

  return (
    <div className="min-h-screen bg-gradient-to-r from-teal-50 to-blue-50 p-5 ml-64">
      {error && <div className="mb-4 p-3 bg-red-100 text-red-700 rounded-md text-center">{error}</div>}

      <div className="max-w-3xl mx-auto bg-white shadow-lg rounded-lg p-5">
        <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.5 }}>
          <h2 className="text-4xl font-bold text-gray-800 mb-2">{debate.title}</h2>
          <p className="text-gray-600 mb-4">{debate.openingArgument}</p>
          <p className="text-sm text-teal-600 mb-4">
            Category: <span className="font-semibold">{debate.category}</span> | By:{" "}
            <Link to={`/profile/${debate.author?.username || 'unknown'}`} className="font-semibold hover:underline">
              {debate.author?.username || "Unknown"}
            </Link> | Created:{" "}
            <span className="font-semibold">{debate.createdAt ? new Date(debate.createdAt).toLocaleString() : "Unknown"}</span>
          </p>

          <div className="flex space-x-4 mb-6">
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => handleVote("upvote")}
              disabled={voting}
              className={`flex items-center px-4 py-2 rounded-lg shadow-md transition-colors duration-200 ${
                voting ? "bg-teal-300 cursor-not-allowed" : "bg-teal-500 hover:bg-teal-600"
              } text-white`}
            >
              <ThumbUpIcon className="h-5 w-5 mr-2" />
              Upvote ({debate.upvotes || 0})
            </motion.button>
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => handleVote("downvote")}
              disabled={voting}
              className={`flex items-center px-4 py-2 rounded-lg shadow-md transition-colors duration-200 ${
                voting ? "bg-orange-300 cursor-not-allowed" : "bg-orange-500 hover:bg-orange-600"
              } text-white`}
            >
              <ThumbDownIcon className="h-5 w-5 mr-2" />
              Downvote ({debate.downvotes || 0})
            </motion.button>
          </div>

          {!userStanceLocked && (
            <div className="mb-4">
              <label className="block text-sm font-medium text-gray-700 mb-1">Your Stance:</label>
              <div className="flex space-x-4">
                <button
                  onClick={() => setStance("with")}
                  className={`px-4 py-2 rounded-lg ${stance === "with" ? "bg-green-500 text-white" : "bg-gray-200"}`}
                >
                  With
                </button>
                <button
                  onClick={() => setStance("against")}
                  className={`px-4 py-2 rounded-lg ${stance === "against" ? "bg-red-500 text-white" : "bg-gray-200"}`}
                >
                  Against
                </button>
              </div>
            </div>
          )}
          {userStanceLocked && (
            <p className="text-sm text-gray-600 mb-4">
              Your stance is locked as: <span className="font-semibold">{userStanceLocked}</span>
            </p>
          )}

          <textarea
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            className="w-full p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-500 mb-2"
            placeholder="Write a comment..."
            rows="3"
          />
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={handleComment}
            disabled={commenting}
            className={`w-full flex items-center justify-center px-4 py-2 rounded-lg shadow-md transition-colors duration-200 ${
              commenting ? "bg-teal-300 cursor-not-allowed" : "bg-teal-500 hover:bg-teal-600"
            } text-white`}
          >
            <ChatIcon className="h-5 w-5 mr-2" />
            {commenting ? "Posting..." : "Post Comment"}
          </motion.button>
        </motion.div>
      </div>

      <div className="max-w-3xl mx-auto mt-6">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-2xl font-bold text-gray-800">Comments</h3>
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="px-3 py-1 border rounded-lg bg-white text-gray-700 focus:outline-none focus:ring-2 focus:ring-teal-500"
          >
            <option value="latest">Latest</option>
            <option value="mostLiked">Popular</option>
          </select>
        </div>

        <div className="max-h-[calc(100vh-400px)] overflow-y-auto border rounded-lg p-4 bg-white shadow-md">
          {sortedComments.length > 0 ? (
            sortedComments.map((c, index) => (
              <motion.div
                key={c._id || index}
                initial={{ opacity: 0, y: 10 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.3 }}
                className={`border-b py-3 ${c.stance === "with" ? "bg-green-50" : "bg-red-50"}`}
              >
                <p className="text-gray-700">{c.text}</p>
                <div className="flex justify-between items-center mt-1">
                  <p className="text-sm text-gray-500">
                    By:{" "}
                    <Link to={`/profile/${c.user?.username || 'unknown'}`} className="hover:underline">
                      {c.user?.username || "Unknown"}
                    </Link> | {new Date(c.createdAt).toLocaleString()} | Stance: {c.stance}
                  </p>
                  <motion.button
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                    onClick={() => handleCommentLike(c._id)}
                    className="flex items-center px-2 py-1 rounded-lg bg-orange-500 hover:bg-orange-600 text-white text-sm shadow-md transition-colors duration-200"
                  >
                    <HeartIcon className="h-4 w-4 mr-1" />
                    ({c.likes || 0})
                  </motion.button>
                </div>
              </motion.div>
            ))
          ) : (
            <p className="text-gray-500 text-center">No comments yet. Be the first to comment!</p>
          )}
        </div>
      </div>

      <div className="max-w-3xl mx-auto text-center mt-8">
        <Link to="/debate-hub" className="text-teal-600 hover:underline font-semibold">
          ← Back to Debate Hub
        </Link>
      </div>
    </div>
  );
};

export default DebatePage;
