import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import {
  HomeIcon,
  DocumentTextIcon,
  ChatAlt2Icon,
  UserIcon,
  PlusCircleIcon,
  MenuIcon,
  XIcon,
} from "@heroicons/react/outline";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();

  const toggleNavbar = () => {
    setIsOpen(!isOpen);
  };

  const navItems = [
    { name: "Home", path: "/", icon: <HomeIcon className="h-6 w-6" /> },
    { name: "Blog", path: "/blog", icon: <DocumentTextIcon className="h-6 w-6" /> },
    { name: "Debate", path: "/debate", icon: <ChatAlt2Icon className="h-6 w-6" /> },
    { name: "Discussion", path: "/", icon: <ChatAlt2Icon className="h-6 w-6" /> },
    { name: "Profile", path: "/profile", icon: <UserIcon className="h-6 w-6" /> },
  ];

  return (
    <>
      {/* Mobile Toggle Button */}
      <div className="md:hidden fixed top-4 left-4 z-50">
        <button onClick={toggleNavbar} className="p-2 rounded-full bg-white shadow-md">
          {isOpen ? (
            <XIcon className="h-6 w-6 text-gray-800" />
          ) : (
            <MenuIcon className="h-6 w-6 text-gray-800" />
          )}
        </button>
      </div>

      {/* Navbar */}
      <motion.div
        initial={{ x: "-100%" }}
        animate={{ x: isOpen ? 0 : "-100%" }}
        transition={{ duration: 0.3, ease: "easeInOut" }}
        className={`fixed top-0 left-0 h-full w-64 bg-white shadow-lg z-40 md:static md:flex md:flex-col md:w-64 md:translate-x-0 ${
          isOpen ? "flex" : "hidden md:flex"
        } flex-col justify-between p-4 md:p-6`}
      >
        {/* Navbar Items */}
        <div className="space-y-4">
          <div className="flex items-center space-x-2 mb-6">
            <h1 className="text-2xl font-bold text-gray-800">AppName</h1>
          </div>
          {navItems.map((item) => (
            <Link
              key={item.name}
              to={item.path}
              onClick={() => setIsOpen(false)} // Close navbar on mobile after clicking
              className="flex items-center space-x-3 p-2 rounded-lg hover:bg-gray-100 transition-colors duration-200"
            >
              <span className="text-gray-600">{item.icon}</span>
              <span className="text-gray-800 font-medium text-base">{item.name}</span>
            </Link>
          ))}
        </div>

        {/* Post Button */}
        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={() => {
            navigate("/create-post");
            setIsOpen(false);
          }}
          className="flex items-center justify-center space-x-2 bg-gradient-to-r from-blue-500 to-purple-600 text-white py-2 px-4 rounded-full shadow-md hover:from-blue-600 hover:to-purple-700 transition-all duration-300"
        >
          <PlusCircleIcon className="h-6 w-6" />
          <span className="text-base font-medium">Post</span>
        </motion.button>
      </motion.div>

      {/* Overlay for Mobile */}
      {isOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-50 z-30 md:hidden"
          onClick={toggleNavbar}
        />
      )}
    </>
  );
};

export default Navbar;
