import React, { useEffect, useState, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import API from "../api/axiosInstance";
import { UserIcon, ChatIcon, UserAddIcon, UserRemoveIcon } from "@heroicons/react/outline";

// Backend base URL (adjust based on your environment)
const BACKEND_BASE_URL = "http://localhost:5000"; // Change to your backend URL in production

function getToken() {
  const token = localStorage.getItem("token");
  console.log("Retrieved token:", token);
  return token;
}

function getUserId() {
  const userId = localStorage.getItem("userId");
  console.log("Retrieved userId:", userId);
  return userId;
}

const ProfilePage = () => {
  const { username } = useParams();
  const navigate = useNavigate();

  const [profileUser, setProfileUser] = useState(null);
  const [createdDebates, setCreatedDebates] = useState([]);
  const [createdDiscussions, setCreatedDiscussions] = useState([]);
  const [error, setError] = useState("");
  const [isFollowing, setIsFollowing] = useState(false);
  const [isFollower, setIsFollower] = useState(false);
  const [activeTab, setActiveTab] = useState("debate");
  const [showFollowers, setShowFollowers] = useState(false);
  const [showFollowing, setShowFollowing] = useState(false);

  const currentUserId = getUserId();
  const token = getToken();

  const fetchProfile = useCallback(async () => {
    try {
      const res = await API.get(`/users/profile/${username}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log("Fetched profile data:", res.data);
      setProfileUser(res.data);
      const followers = Array.isArray(res.data.followers) ? res.data.followers : [];
      const followings = Array.isArray(res.data.followings) ? res.data.followings : [];
      setIsFollowing(followers.some((f) => String(f._id) === String(currentUserId)));
      setIsFollower(followings.some((f) => String(f._id) === String(currentUserId)));
    } catch (err) {
      setError(err.response?.data?.message || "Error fetching profile");
      console.error("Full error:", err.response || err);
    }
  }, [username, token, currentUserId]);

  const fetchCreatedDebates = useCallback(async () => {
    try {
      const res = await API.get(`/users/profile/${username}/debates`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log("Fetched created debates:", res.data);
      setCreatedDebates(res.data);
    } catch (err) {
      setError(err.response?.data?.message || "Error fetching created debates");
      console.error("Full error:", err.response || err);
    }
  }, [username, token]);

  const fetchCreatedDiscussions = useCallback(async () => {
    try {
      const res = await API.get(`/users/profile/${username}/discussions`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      console.log("Fetched created discussions:", res.data);
      setCreatedDiscussions(res.data);
    } catch (err) {
      setError(err.response?.data?.message || "Error fetching created discussions");
      console.error("Full error:", err.response || err);
    }
  }, [username, token]);

  useEffect(() => {
    fetchProfile();
    fetchCreatedDebates();
    fetchCreatedDiscussions();
  }, [fetchProfile, fetchCreatedDebates, fetchCreatedDiscussions]);

  const handleSelectAction = (e) => {
    const action = e.target.value;
    if (action === "logout") {
      localStorage.removeItem("token");
      localStorage.removeItem("userId");
      navigate("/login");
    } else if (action === "changeBio") {
      alert("Change bio functionality to be implemented");
    } else if (action === "changeProfilePicture") {
      alert("Change profile picture functionality to be implemented");
    }
  };

  const handleShowFollowers = () => {
    if (!token) {
      alert("Please log in to view followers!");
      navigate("/login");
      return;
    }
    setShowFollowers(true);
  };

  const handleShowFollowing = () => {
    if (!token) {
      alert("Please log in to view following!");
      navigate("/login");
      return;
    }
    setShowFollowing(true);
  };

  const handleFollow = async () => {
    if (!token) {
      alert("You must be logged in to follow or unfollow!");
      return navigate("/login");
    }
    try {
      if (isFollowing) {
        await API.post(`/users/profile/${username}/unfollow`, {}, { headers: { Authorization: `Bearer ${token}` } });
      } else {
        await API.post(`/users/profile/${username}/follow`, {}, { headers: { Authorization: `Bearer ${token}` } });
      }
      fetchProfile();
    } catch (err) {
      setError(err.response?.data?.message || "Error following/unfollowing");
      console.error(err);
    }
  };

  const handleChat = () => {
    if (!token) {
      alert("You must be logged in to chat!");
      return navigate("/login");
    }
    alert("Open chat window here!");
  };

  const handleDebateClick = (debateId) => {
    navigate(`/debatepage/${debateId}`);
  };

  const handleDiscussionClick = (discussionId) => {
    navigate(`/discussionpage/${discussionId}`);
  };

  const handleBlogClick = (blogId) => {
    navigate(`/blogpage/${blogId}`);
  };

  if (error) {
    return <div className="text-center text-red-500 text-lg mt-10">Error: {error}</div>;
  }

  if (!profileUser) {
    return <div className="text-center text-gray-600 mt-10">Loading profile...</div>;
  }

  const isOwnProfile = currentUserId && profileUser._id && String(currentUserId) === String(profileUser._id);
  console.log("isOwnProfile:", isOwnProfile, "currentUserId:", currentUserId, "profileUser._id:", profileUser._id);

  const totalPosts = (createdDebates.length || 0) + (createdDiscussions.length || 0) + (profileUser.blogs?.length || 0);
  const followButtonText = isFollowing ? "Unfollow" : isFollower ? "Follow Back" : "Follow";

  return (
    <div className="min-h-screen bg-black text-white">
      <div className="relative p-5">
        {isOwnProfile && (
          <div className="absolute top-4 right-4">
            <select
              onChange={handleSelectAction}
              className="bg-gray-800 text-white px-3 py-2 rounded-lg focus:outline-none"
              defaultValue=""
            >
              <option value="" disabled>Select an action</option>
              <option value="logout">Logout</option>
              <option value="changeBio">Change Bio</option>
              <option value="changeProfilePicture">Change Profile Picture</option>
            </select>
          </div>
        )}

        <div className="flex justify-center">
          <img
            src={profileUser.profilePicture ? `${BACKEND_BASE_URL}${profileUser.profilePicture}` : `${BACKEND_BASE_URL}/images/default-avatar.png`}
            alt="profile"
            className="w-24 h-24 rounded-full border-2 border-white"
            onError={(e) => {
              console.error("Profile picture failed to load:", profileUser.profilePicture);
              e.target.src = `${BACKEND_BASE_URL}/images/default-avatar.png`; // Fallback to default image
            }}
          />
        </div>

        <h1 className="text-center text-2xl font-bold mt-4 underline">{profileUser.username}</h1>

        <div className="flex justify-center gap-8 mt-4">
          <div className="text-center">
            <p className="text-lg">{totalPosts}</p>
            <p className="text-sm">Posts</p>
          </div>
          <div className="text-center cursor-pointer" onClick={handleShowFollowers}>
            <p className="text-lg">{profileUser.followers?.length || 0}</p>
            <p className="text-sm">Followers</p>
          </div>
          <div className="text-center cursor-pointer" onClick={handleShowFollowing}>
            <p className="text-lg">{profileUser.followings?.length || 0}</p>
            <p className="text-sm">Following</p>
          </div>
        </div>

        <div className="text-center mt-4">
          {profileUser.name && <p className="font-bold">{profileUser.name}</p>}
          <p className="text-sm">{profileUser.bio || "No bio yet."}</p>
        </div>

        <div className="flex justify-center mt-4 gap-4">
          {isOwnProfile ? (
            <button className="bg-gray-700 text-white px-4 py-2 rounded-full flex items-center">
              <span className="text-2xl mr-2">+</span> New
            </button>
          ) : (
            <>
              <button
                onClick={handleFollow}
                className={`flex items-center px-4 py-2 rounded-full text-white ${
                  isFollowing ? "bg-gray-600 hover:bg-gray-700" : "bg-blue-600 hover:bg-blue-700"
                } transition-all duration-300`}
              >
                {isFollowing ? <UserRemoveIcon className="h-5 w-5 mr-2" /> : <UserAddIcon className="h-5 w-5 mr-2" />}
                {followButtonText}
              </button>
              <button
                onClick={handleChat}
                className="flex items-center px-4 py-2 bg-green-600 text-white rounded-full hover:bg-green-700 transition-all duration-300"
              >
                <ChatIcon className="h-5 w-5 mr-2" />
                Chat
              </button>
            </>
          )}
        </div>
      </div>

      <div className="flex justify-center gap-8 mt-8 border-b border-gray-700">
        <button
          onClick={() => setActiveTab("debate")}
          className={`pb-2 flex flex-col items-center ${activeTab === "debate" ? "border-b-2 border-white" : ""}`}
        >
          <UserIcon className="h-6 w-6" />
          <span>DEBATES</span>
        </button>
        <button
          onClick={() => setActiveTab("discussion")}
          className={`pb-2 flex flex-col items-center ${activeTab === "discussion" ? "border-b-2 border-white" : ""}`}
        >
          <ChatIcon className="h-6 w-6" />
          <span>DISCUSSIONS</span>
        </button>
        <button
          onClick={() => setActiveTab("blogs")}
          className={`pb-2 flex flex-col items-center ${activeTab === "blogs" ? "border-b-2 border-white" : ""}`}
        >
          <UserIcon className="h-6 w-6" />
          <span>BLOGS</span>
        </button>
      </div>

      <div className="grid grid-cols-3 gap-1 mt-4">
        {activeTab === "debate" &&
          (createdDebates.length > 0 ? (
            createdDebates.map((deb, index) => (
              <div
                key={deb._id || index}
                className="bg-gray-800 p-4 cursor-pointer hover:bg-gray-700 transition-all duration-200"
                onClick={() => handleDebateClick(deb._id)}
              >
                <h3 className="text-lg font-bold">{deb.title}</h3>
                <p className="text-sm">{deb.openingArgument?.substring(0, 50)}...</p>
              </div>
            ))
          ) : (
            <p className="text-center col-span-3">No debates created yet.</p>
          ))}
        {activeTab === "discussion" &&
          (createdDiscussions.length > 0 ? (
            createdDiscussions.map((disc, index) => (
              <div
                key={disc._id || index}
                className="bg-gray-800 p-4 cursor-pointer hover:bg-gray-700 transition-all duration-200"
                onClick={() => handleDiscussionClick(disc._id)}
              >
                <h3 className="text-lg font-bold">{disc.title}</h3>
                <p className="text-sm">{disc.description?.substring(0, 50)}...</p>
              </div>
            ))
          ) : (
            <p className="text-center col-span-3">No discussions created yet.</p>
          ))}
        {activeTab === "blogs" &&
          (profileUser.blogs?.length > 0 ? (
            profileUser.blogs.map((blog, index) => (
              <div
                key={blog._id || index}
                className="bg-gray-800 p-4 cursor-pointer hover:bg-gray-700 transition-all duration-200"
                onClick={() => handleBlogClick(blog._id)}
              >
                <h3 className="text-lg font-bold">{blog.title}</h3>
                <p className="text-sm">{blog.content?.substring(0, 50)}...</p>
              </div>
            ))
          ) : (
            <p className="text-center col-span-3">No blogs yet.</p>
          ))}
      </div>

      {showFollowers && (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center">
          <div className="bg-gray-900 text-white p-6 rounded-xl shadow-lg max-w-md w-full max-h-[80vh] overflow-y-auto">
            <h2 className="text-2xl font-bold mb-4 text-center">Followers</h2>
            <ul className="space-y-4">
              {profileUser.followers.map((follower) => (
                <li key={follower._id} className="flex items-center space-x-3 p-3 bg-gray-800 rounded-lg">
                  <img
                    src={follower.profilePicture ? `${BACKEND_BASE_URL}${follower.profilePicture}` : `${BACKEND_BASE_URL}/images/default-avatar.png`}
                    alt={follower.username}
                    className="w-10 h-10 rounded-full border-2 border-gray-600"
                    onError={(e) => {
                      console.error("Follower picture failed to load:", follower.profilePicture);
                      e.target.src = `${BACKEND_BASE_URL}/images/default-avatar.png`; // Fallback to default image
                    }}
                  />
                  <span className="text-lg">{follower.username}</span>
                </li>
              ))}
            </ul>
            <button
              onClick={() => setShowFollowers(false)}
              className="mt-6 w-full bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-all duration-300"
            >
              Close
            </button>
          </div>
        </div>
      )}

      {showFollowing && (
        <div className="fixed inset-0 bg-black bg-opacity-75 flex justify-center items-center">
          <div className="bg-gray-900 text-white p-6 rounded-xl shadow-lg max-w-md w-full max-h-[80vh] overflow-y-auto">
            <h2 className="text-2xl font-bold mb-4 text-center">Following</h2>
            <ul className="space-y-4">
              {profileUser.followings.map((following) => (
                <li key={following._id} className="flex items-center space-x-3 p-3 bg-gray-800 rounded-lg">
                  <img
                    src={following.profilePicture ? `${BACKEND_BASE_URL}${following.profilePicture}` : `${BACKEND_BASE_URL}/images/default-avatar.png`}
                    alt={following.username}
                    className="w-10 h-10 rounded-full border-2 border-gray-600"
                    onError={(e) => {
                      console.error("Following picture failed to load:", following.profilePicture);
                      e.target.src = `${BACKEND_BASE_URL}/images/default-avatar.png`; // Fallback to default image
                    }}
                  />
                  <span className="text-lg">{following.username}</span>
                </li>
              ))}
            </ul>
            <button
              onClick={() => setShowFollowing(false)}
              className="mt-6 w-full bg-red-600 text-white px-4 py-2 rounded-lg hover:bg-red-700 transition-all duration-300"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProfilePage;
