import React, { useEffect, useState } from "react";
import { NavLink, Link, useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import { HomeIcon, ChatAlt2Icon, PencilIcon, UserIcon, SearchIcon } from "@heroicons/react/outline";
import API from "../api/axiosInstance";

const Navbar = () => {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      API.get("/users/profile/me", {
        headers: { Authorization: `Bearer ${token}` },
      })
        .then((res) => {
          setUsername(res.data.username);
        })
        .catch((err) => {
          console.error("Error fetching user profile:", err);
          setUsername("");
        });
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    setUsername("");
    navigate("/login");
  };

  return (
    <nav className="fixed top-0 left-0 h-full w-64 bg-white text-black flex flex-col shadow-lg z-50">
      <div className="p-4 flex-1">
        {/* Home */}
        <NavLink
          to="/"
          className={({ isActive }) =>
            `flex items-center space-x-2 p-2 rounded-lg ${
              isActive ? "bg-gray-200 text-blue-600" : "text-black hover:bg-gray-100"
            } transition-colors duration-200`
          }
        >
          <HomeIcon className="h-6 w-6" />
          <span className="text-lg font-medium">Home</span>
        </NavLink>

        {/* Debates */}
        <NavLink
          to="/debate"
          className={({ isActive }) =>
            `flex items-center space-x-2 p-2 rounded-lg ${
              isActive ? "bg-gray-200 text-blue-600" : "text-black hover:bg-gray-100"
            } transition-colors duration-200`
          }
        >
          <ChatAlt2Icon className="h-6 w-6" />
          <span className="text-lg font-medium">Debates</span>
        </NavLink>

        {/* Blogs */}
        <NavLink
          to="/blog"
          className={({ isActive }) =>
            `flex items-center space-x-2 p-2 rounded-lg ${
              isActive ? "bg-gray-200 text-blue-600" : "text-black hover:bg-gray-100"
            } transition-colors duration-200`
          }
        >
          <PencilIcon className="h-6 w-6" />
          <span className="text-lg font-medium">Blogs</span>
        </NavLink>

        {/* Profile (only if logged in and username exists) */}
        {username && (
          <NavLink
            to={`/profile/${username}`}
            className={({ isActive }) =>
              `flex items-center space-x-2 p-2 rounded-lg ${
                isActive ? "bg-gray-200 text-blue-600" : "text-black hover:bg-gray-100"
              } transition-colors duration-200`
            }
          >
            <UserIcon className="h-6 w-6" />
            <span className="text-lg font-medium">Profile</span>
          </NavLink>
        )}

        {/* Search */}
        <Link
          to="/search"
          className="flex items-center space-x-2 p-2 rounded-lg text-black hover:bg-gray-100 transition-colors duration-200"
        >
          <SearchIcon className="h-6 w-6" />
          <span className="text-lg font-medium">Search</span>
        </Link>
      </div>

      {/* Logout/Login */}
      <div className="p-4">
        {localStorage.getItem("token") ? (
          <motion.button
            whileHover={{ scale: 1.05 }}
            onClick={handleLogout}
            className="w-full text-left p-2 rounded-lg text-black hover:bg-gray-200 transition-colors duration-200"
          >
            Logout
          </motion.button>
        ) : (
          <Link
            to="/login"
            className="w-full text-left p-2 rounded-lg text-black hover:bg-gray-200 transition-colors duration-200"
          >
            Login / Register
          </Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
