import React, { useEffect, useState } from "react";
import axios from "axios";

const Home = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAllData = async () => {
      try {
        const [discussionsRes, debatesRes, blogsRes] = await Promise.all([
          axios.get("/api/discussions"),
          axios.get("/api/debates"),
          axios.get("/api/blogs"),
        ]);

        // Combine and sort by createdAt (if available)
        const combined = [
          ...discussionsRes.data.map((item) => ({ ...item, type: "discussion" })),
          ...debatesRes.data.map((item) => ({ ...item, type: "debate" })),
          ...blogsRes.data.map((item) => ({ ...item, type: "blog" })),
        ].sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));

        setData(combined);
      } catch (err) {
        console.error("Error fetching data:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchAllData();
  }, []);

  if (loading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-3xl font-bold mb-6">Latest Activity</h1>
      {data.length === 0 ? (
        <p className="text-center text-gray-500">No content available.</p>
      ) : (
        data.map((item) => (
          <div
            key={item._id}
            className="bg-white shadow-md rounded-2xl p-4 mb-6 border hover:shadow-lg transition-all"
          >
            <p className="text-sm text-gray-500 capitalize mb-2">
              {item.type}
            </p>
            <h2 className="text-xl font-semibold">{item.title || item.topic}</h2>
            <p className="mt-2 text-gray-700">
              {item.content?.slice(0, 150) || item.description?.slice(0, 150)}...
            </p>
            <div className="mt-4 text-gray-400 text-sm">
              Posted on {new Date(item.createdAt).toLocaleDateString()}
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default Home;
