import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import API from "../api/axiosInstance";

const Home = () => {
  const [discussions, setDiscussions] = useState([]);
  const [debates, setDebates] = useState([]);
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeSection, setActiveSection] = useState("discussions");

  useEffect(() => {
    const fetchAllContent = async () => {
      try {
        setLoading(true);
        const [discussionsRes, debatesRes, blogsRes] = await Promise.all([
          API.get("/discussions"),
          API.get("/debates"),
          API.get("/blogs"),
        ]);
        setDiscussions(Array.isArray(discussionsRes.data) ? discussionsRes.data : []);
        setDebates(Array.isArray(debatesRes.data) ? debatesRes.data : []);
        setBlogs(Array.isArray(blogsRes.data) ? blogsRes.data : []);
      } catch (error) {
        console.error("Error fetching content:", error);
        setDiscussions([]);
        setDebates([]);
        setBlogs([]);
      } finally {
        setLoading(false);
      }
    };

    fetchAllContent();
  }, []);

  const sortByPopularity = (items) => {
    return [...items].sort((a, b) => {
      const popularityA = (a.comments?.length || 0) + (a.upvotes?.length || 0) + (a.downvotes?.length || 0);
      const popularityB = (b.comments?.length || 0) + (b.upvotes?.length || 0) + (b.downvotes?.length || 0);
      return popularityB - popularityA;
    });
  };

  if (loading) {
    return <div className="flex justify-center items-center h-screen">Loading...</div>;
  }

  return (
    <div className="md:ml-64 p-6 flex flex-col gap-10">
      {/* Navigation Buttons */}
      <div className="flex justify-around bg-white shadow-md rounded-2xl p-4">
        <button
          onClick={() => setActiveSection("discussions")}
          className={`text-lg font-semibold ${activeSection === "discussions" ? "text-blue-600 underline" : "text-gray-600"}`}
        >
          Discussions
        </button>
        <button
          onClick={() => setActiveSection("debates")}
          className={`text-lg font-semibold ${activeSection === "debates" ? "text-blue-600 underline" : "text-gray-600"}`}
        >
          Debates
        </button>
        <button
          onClick={() => setActiveSection("blogs")}
          className={`text-lg font-semibold ${activeSection === "blogs" ? "text-blue-600 underline" : "text-gray-600"}`}
        >
          Blogs
        </button>
      </div>

      {/* Render Popular Section */}
      {activeSection === "discussions" && (
        <section>
          <h2 className="text-3xl font-bold mb-6">Top Discussions</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-3 gap-6">
            {sortByPopularity(discussions).slice(0, 5).map((discussion) => (
              <div key={discussion._id} className="bg-white shadow-md rounded-2xl p-4 border hover:shadow-lg transition-all">
                <h3 className="text-xl font-semibold">{discussion.title}</h3>
                <p className="mt-2 text-gray-700">{discussion.content?.slice(0, 150)}...</p>
                <p className="mt-2 text-gray-500 text-sm">
                  Posted on {new Date(discussion.createdAt).toLocaleDateString()}
                </p>
              </div>
            ))}

            {/* See More Button */}
            <Link
              to="/discussions"
              className="bg-blue-100 hover:bg-blue-200 text-blue-700 font-semibold rounded-2xl flex justify-center items-center p-6 transition-all"
            >
              See More Discussions →
            </Link>
          </div>
        </section>
      )}

      {activeSection === "debates" && (
        <section>
          <h2 className="text-3xl font-bold mb-6">Top Debates</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-3 gap-6">
            {sortByPopularity(debates).slice(0, 5).map((debate) => (
              <div key={debate._id} className="bg-white shadow-md rounded-2xl p-4 border hover:shadow-lg transition-all">
                <h3 className="text-xl font-semibold">{debate.topic}</h3>
                <p className="mt-2 text-gray-700">{debate.description?.slice(0, 150)}...</p>
                <p className="mt-2 text-gray-500 text-sm">
                  Posted on {new Date(debate.createdAt).toLocaleDateString()}
                </p>
              </div>
            ))}
            <Link
              to="/debates"
              className="bg-blue-100 hover:bg-blue-200 text-blue-700 font-semibold rounded-2xl flex justify-center items-center p-6 transition-all"
            >
              See More Debates →
            </Link>
          </div>
        </section>
      )}

      {activeSection === "blogs" && (
        <section>
          <h2 className="text-3xl font-bold mb-6">Top Blogs</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-3 gap-6">
            {sortByPopularity(blogs).slice(0, 5).map((blog) => (
              <div key={blog._id} className="bg-white shadow-md rounded-2xl p-4 border hover:shadow-lg transition-all">
                <h3 className="text-xl font-semibold">{blog.title}</h3>
                <p className="mt-2 text-gray-700">{blog.content?.slice(0, 150)}...</p>
                <p className="mt-2 text-gray-500 text-sm">
                  Posted on {new Date(blog.createdAt).toLocaleDateString()}
                </p>
              </div>
            ))}
            <Link
              to="/blog"
              className="bg-blue-100 hover:bg-blue-200 text-blue-700 font-semibold rounded-2xl flex justify-center items-center p-6 transition-all"
            >
              See More Blogs →
            </Link>
          </div>
        </section>
      )}
    </div>
  );
};

export default Home;
