import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import API from "../api/axiosInstance";
import { motion } from "framer-motion";

const Bookmarks = () => {
  const [bookmarks, setBookmarks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchBookmarks = async () => {
      try {
        setLoading(true);
        const token = localStorage.getItem("token");
        if (!token) {
          setError("Please log in to view bookmarks");
          return;
        }
        const { data } = await API.get("/bookmarks", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setBookmarks(data);
      } catch (error) {
        setError("Failed to load bookmarks");
        console.error("Error fetching bookmarks:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchBookmarks();
  }, []);

  const handleNavigate = (item) => {
    if (item.type === "blog") navigate(`/blogpage/${item._id}`);
    else if (item.type === "debate") navigate(`/debatepage/${item._id}`);
    else if (item.type === "discussion") navigate(`/discussionpage/${item._id}`);
  };

  if (loading) return <p className="text-center mt-10 text-gray-600">Loading bookmarks...</p>;
  if (error) return <p className="text-center mt-10 text-red-500">{error}</p>;

  return (
    <div className="min-h-screen bg-gray-50 md:ml-64 py-8 px-4">
      <h1 className="text-4xl font-bold mb-8 text-center text-gray-800">Bookmarks</h1>

      {bookmarks.length === 0 ? (
        <p className="text-gray-600 text-center">You haven't bookmarked any posts yet.</p>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
          {bookmarks.map((item) => (
            <motion.div
              key={item._id}
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              whileHover={{ scale: 1.03 }}
              className="bg-white shadow-xl rounded-2xl p-6 cursor-pointer hover:shadow-2xl transition-all duration-300"
              onClick={() => handleNavigate(item)}
            >
              <h2 className="text-2xl font-semibold text-blue-600 mb-2">{item.title}</h2>
              <p className="text-gray-700">By: {item.author?.username || "Unknown"}</p>
              <p className="text-sm text-gray-500 mt-2">Type: <span className="capitalize">{item.type}</span></p>
              <p className="text-sm text-gray-500">Views: {item.views || 0}</p>
            </motion.div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Bookmarks;
