import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import API from "../api/axiosInstance";
import { motion } from "framer-motion";

const Bookmarks = () => {
  const [bookmarks, setBookmarks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBookmarks = async () => {
      try {
        setLoading(true);
        const token = localStorage.getItem("token");
        if (!token) {
          setError("Please log in to view bookmarks");
          return;
        }
        const { data } = await API.get("/bookmarks", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setBookmarks(data);
      } catch (error) {
        setError("Failed to load bookmarks");
        console.error("Error fetching bookmarks:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchBookmarks();
  }, []);

  // Get proper route based on type
  const getRoute = (item) => {
    if (item.type === "blog") return `/blogpage/${item._id}`;
    if (item.type === "debate") return `/debatepage/${item._id}`;
    if (item.type === "discussion") return `/discussionpage/${item._id}`;
    return "#";
  };

  if (loading) return <p className="text-center mt-10 text-gray-600">Loading bookmarks...</p>;
  if (error) return <p className="text-center mt-10 text-red-500">{error}</p>;

  return (
    <div className="min-h-screen bg-gray-50 md:ml-64 py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <h1 className="text-4xl font-bold mb-8 text-gray-800 text-center">Bookmarks</h1>

        {bookmarks.length === 0 ? (
          <p className="text-gray-600 text-center">You haven't bookmarked anything yet.</p>
        ) : (
          <div className="grid gap-6 sm:grid-cols-2 lg:grid-cols-3">
            {bookmarks.map((item) => (
              <motion.div
                key={item._id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="bg-white rounded-2xl shadow-md p-6 hover:shadow-xl transition-shadow"
              >
                <Link to={getRoute(item)} className="block hover:underline">
                  <h2 className="text-xl font-semibold text-blue-700 mb-2">{item.title}</h2>
                  <p className="text-gray-600 mb-1">By {item.author?.username || "Unknown"}</p>
                  <p className="text-gray-500 text-sm">Type: <span className="capitalize">{item.type}</span></p>
                  <p className="text-gray-500 text-sm mt-1">Views: {item.views || 0}</p>
                  <p className="text-gray-500 text-sm">Bookmarked by {item.bookmarkedBy?.length || 0} users</p>
                </Link>
              </motion.div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Bookmarks;
