import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useSelector } from 'react-redux';
import axios from 'axios';

const Navbar = () => {
  const [search, setSearch] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const user = useSelector((state) => state.user);
  const navigate = useNavigate();

  const handleSearch = async (e) => {
    const query = e.target.value;
    setSearch(query);
    if (query.trim() === '') {
      setSuggestions([]);
      return;
    }
    try {
      const res = await axios.get(`/api/users/search?query=${query}`);
      setSuggestions(res.data);
    } catch (err) {
      console.log(err);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';
  };

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <Link to="/" className="logo">Debatify</Link>
      </div>

      <div className="navbar-center">
        <input
          type="text"
          placeholder="Search users..."
          value={search}
          onChange={handleSearch}
          className="search-input"
        />
        {suggestions.length > 0 && (
          <ul className="suggestions">
            {suggestions.map((user) => (
              <li key={user._id} onClick={() => navigate(`/profile/${user._id}`)}>
                {user.username}
              </li>
            ))}
          </ul>
        )}
      </div>

      <div className="navbar-right">
        <Link to={`/profile/${user._id}`} className="nav-item">Profile</Link>
        {/* âœ… FIXED: Inbox Route Navigates to /inbox */}
        <Link to="/inbox" className="nav-item">Inbox</Link>
        {/* Notification bell as you had */}
        <div className="notification-bell">
          <i className="fas fa-bell"></i>
          <span className="notification-count">3</span>
        </div>
        <button onClick={handleLogout} className="logout-btn">Logout</button>
      </div>
    </nav>
  );
};

export default Navbar;
