// src/components/Navbar.js
import React, { useEffect, useState, useRef, useCallback } from "react";
import { NavLink, Link, useNavigate, useLocation } from "react-router-dom";
import { motion } from "framer-motion";
import {
  HomeIcon,
  ChatAlt2Icon,
  PencilIcon,
  UserIcon,
  SearchIcon,
  BellIcon,
  MenuIcon,
  XIcon,
  BookmarkIcon,
  ChatIcon
} from "@heroicons/react/outline";
import API from "../api/axiosInstance";

const Navbar = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [username, setUsername] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [isNotificationOpen, setIsNotificationOpen] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);
  const [isNavbarOpen, setIsNavbarOpen] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(!!localStorage.getItem("token"));
  const searchRef = useRef(null);
  const notificationRef = useRef(null);
  const navbarRef = useRef(null);
  const lastNotificationIdRef = useRef(null);
  const hasVibrated = useRef(false);

  // Button animation variants
  const buttonVariants = {
    hidden: { opacity: 0, y: -20 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { 
        type: "spring",
        stiffness: 300,
        damping: 20,
        duration: 0.3
      }
    }
  };

  // Format notification messages
  const formatNotificationMessage = useCallback((notif) => {
    const actor = notif.user?.username || "Someone";
    const targetTitle = notif.target?.title || "Untitled";
    const targetType = notif.target?.type || "post";
    switch (notif.type) {
      case "follow": return `${actor} followed you`;
      case "unfollow": return `${actor} unfollowed you`;
      case "upvote": return `${actor} upvoted your ${targetType} "${targetTitle}"`;
      case "downvote": return `${actor} downvoted your ${targetType} "${targetTitle}"`;
      case "comment": return `${actor} commented "${notif.comment?.text || "something"}" on your ${targetType} "${targetTitle}"`;
      case "comment_like": return `${actor} liked your comment at ${targetType} "${targetTitle}"`;
      default: return notif.message || "New notification";
    }
  }, []);

  const getRedirectPath = useCallback((notif) => {
    switch (notif.type) {
      case "follow":
      case "unfollow": return `/profile/${username}`;
      case "upvote":
      case "downvote":
        if (notif.target?.type === "debate") return `/debatepage/${notif.target?.id}`;
        if (notif.target?.type === "discussion") return `/discussionpage/${notif.target?.id}`;
        if (notif.target?.type === "blog") return `/blogpage/${notif.target?.id}`;
        return "/";
      case "comment":
      case "comment_like":
        if (notif.target?.type === "debate") return `/debatepage/${notif.target?.id}`;
        if (notif.target?.type === "discussion") return `/discussionpage/${notif.target?.id}`;
        return "/";
      default: return "/";
    }
  }, [username]);

  const fetchNotifications = useCallback(async () => {
    const token = localStorage.getItem("token");
    if (!token) return;
    try {
      const res = await API.get("/notifications", { headers: { Authorization: `Bearer ${token}` } });
      const formattedNotifications = res.data.map((notif) => ({
        ...notif,
        message: formatNotificationMessage(notif),
        redirectTo: getRedirectPath(notif),
      }));
      formattedNotifications.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
      const latestId = formattedNotifications[0]?._id;
      if (lastNotificationIdRef.current !== latestId) {
        setNotifications(formattedNotifications);
        setUnreadCount(formattedNotifications.filter((n) => !n.read).length);
        lastNotificationIdRef.current = latestId;
      }
    } catch (err) {
      console.error("Error fetching notifications:", err);
    }
  }, [formatNotificationMessage, getRedirectPath]);

  // Check authentication status with polling
  useEffect(() => {
    const checkAuthStatus = () => {
      const token = localStorage.getItem("token");
      const newAuthStatus = !!token;
      if (newAuthStatus !== isAuthenticated) {
        setIsAuthenticated(newAuthStatus);
        if (token) {
          API.get("/users/profile/me", { headers: { Authorization: `Bearer ${token}` } })
            .then((res) => setUsername(res.data.username))
            .catch((err) => {
              console.error("Error fetching user profile:", err);
              setUsername("");
              setIsAuthenticated(false);
            });
        } else {
          setUsername("");
        }
      }
    };

    // Initial check
    checkAuthStatus();

    // Set up polling every 1 second
    const authPollInterval = setInterval(checkAuthStatus, 1000);

    // Set up notifications if authenticated
    if (isAuthenticated) {
      fetchNotifications();
      const notificationInterval = setInterval(() => fetchNotifications(), 1000);
      return () => {
        clearInterval(authPollInterval);
        clearInterval(notificationInterval);
      };
    }

    return () => clearInterval(authPollInterval);
  }, [fetchNotifications, isAuthenticated]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) setIsSearchOpen(false);
      if (notificationRef.current && !notificationRef.current.contains(event.target)) setIsNotificationOpen(false);
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  useEffect(() => {
    const handleResize = () => {
      const mobile = window.innerWidth < 768;
      setIsMobile(mobile);
      if (!mobile) setIsNavbarOpen(false);
    };
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  useEffect(() => {
    if (isMobile) setIsNavbarOpen(false);
  }, [location, isMobile]);

  const handleSearch = async (query) => {
    setSearchQuery(query);
    if (query.trim() === "") {
      setSearchResults([]);
      setIsSearchOpen(false);
      return;
    }
    try {
      const { data } = await API.get(`/users/search?query=${query}`);
      setSearchResults(data);
      setIsSearchOpen(data.length > 0);
    } catch (error) {
      console.error("Error searching users:", error);
      setSearchResults([]);
    }
  };

  const markNotificationsAsRead = async () => {
    const token = localStorage.getItem("token");
    try {
      await API.put("/notifications/mark-read", {}, { headers: { Authorization: `Bearer ${token}` } });
      setNotifications((prev) => prev.map((notif) => ({ ...notif, read: true })));
      setUnreadCount(0);
    } catch (error) {
      console.error("Error marking notifications as read:", error);
    }
  };

  const toggleNotifications = () => {
    setIsNotificationOpen((prev) => !prev);
    if (!isNotificationOpen && unreadCount > 0 && !hasVibrated.current) {
      hasVibrated.current = true;
      markNotificationsAsRead();
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    setUsername("");
    setIsAuthenticated(false);
    navigate("/login");
  };

  const getNotificationGroup = (date) => {
    const today = new Date();
    const notificationDate = new Date(date);
    const diffTime = today - notificationDate;
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    if (diffDays <= 1) return "Today";
    if (diffDays <= 2) return "Yesterday";
    if (diffDays <= 7) return "This week";
    if (diffDays <= 30) return "This month";
    return "Older";
  };

  const groupedNotifications = notifications.reduce((acc, notif) => {
    const group = getNotificationGroup(notif.createdAt);
    if (!acc[group]) acc[group] = [];
    acc[group].push(notif);
    return acc;
  }, {});

  const groupOrder = ["Today", "Yesterday", "This week", "This month", "Older"];

  return (
    <>
      {isMobile && (
        <button
          onClick={() => setIsNavbarOpen(true)}
          className="fixed top-4 left-4 z-50 p-2 rounded-md bg-gray-100 hover:bg-gray-200"
          aria-label="Open navigation menu"
        >
          <MenuIcon className="h-6 w-6" />
        </button>
      )}
      <nav
        ref={navbarRef}
        className={`fixed top-0 left-0 h-full bg-white text-black flex flex-col shadow-lg z-50 transition-all duration-300 ${
          isMobile ? (isNavbarOpen ? "w-full" : "hidden") : "w-64"
        } ${isMobile ? "mb-16" : ""}`}
      >
        {isMobile && (
          <button
            onClick={() => setIsNavbarOpen(false)}
            className="absolute top-4 right-4 p-2 rounded-md bg-gray-100 hover:bg-gray-200"
            aria-label="Close navigation menu"
          >
            <XIcon className="h-6 w-6" />
          </button>
        )}
        <div className="p-4">
          <Link to="/" className="text-2xl font-bold text-blue-600">
            Debatify
          </Link>
        </div>
        <div className="flex-1 space-y-4 p-4">
          {/* Moved Login/Register button up here */}
          {!isAuthenticated && (
            <motion.div variants={buttonVariants} initial="hidden" animate="visible">
              <Link
                to="/login"
                className="w-full text-left p-2 rounded-lg text-black hover:bg-gray-200 transition-colors duration-200"
              >
                Login / Register
              </Link>
            </motion.div>
          )}

          <div className="relative" ref={searchRef}>
            <motion.div
              whileHover={{ scale: 1.02 }}
              transition={{ duration: 0.3 }}
              className="flex items-center space-x-2 p-2 rounded-lg bg-gray-100 text-black transition-colors duration-200"
            >
              <SearchIcon className="h-6 w-6 text-blue-600" />
              <input
                type="text"
                value={searchQuery}
                onChange={(e) => handleSearch(e.target.value)}
                placeholder=" Search users..."
                className="bg-transparent focus:outline-none w-full text-black placeholder-gray-500 text-lg font-medium"
                aria-label="Search users by username"
              />
            </motion.div>
            {isSearchOpen && searchResults.length > 0 && (
              <motion.div
                initial={{ opacity: 0, y: -10 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -10 }}
                transition={{ duration: 0.2 }}
                className="absolute top-12 left-0 w-full bg-white rounded-lg shadow-lg max-h-60 overflow-y-auto z-50 border border-gray-200"
              >
                {searchResults.map((user) => (
                  <Link
                    key={user._id}
                    to={`/profile/${user.username}`}
                    onClick={() => {
                      setSearchQuery("");
                      setIsSearchOpen(false);
                    }}
                    className="block px-4 py-2 hover:bg-gradient-to-r hover:from-blue-500/20 hover:to-purple-500/20 transition-colors duration-200 text-black border-b border-gray-200 last:border-b-0"
                  >
                    <h3 className="text-gray-800 font-semibold">{user.username}</h3>
                    <p className="text-gray-600 text-sm truncate">
                      {user.bio ? user.bio.substring(0, 30) + "..." : "No bio available"}
                    </p>
                    <div className="flex justify-between text-sm mt-1">
                      <span className="text-blue-500">Role: {user.role || "User"}</span>
                      <span className="text-gray-500">
                        Joined: {user.createdAt ? new Date(user.createdAt).toLocaleDateString() : "Unknown"}
                      </span>
                    </div>
                  </Link>
                ))}
              </motion.div>
            )}
          </div>

          <motion.div variants={buttonVariants} initial="hidden" animate="visible">
            <NavLink
              to="/"
              className={({ isActive }) =>
                `flex items-center space-x-2 p-2 rounded-lg ${
                  isActive ? "bg-gray-200 text-blue-600" : "text-black hover:bg-gray-100"
                } transition-colors duration-200`
              }
            >
              <HomeIcon className="h-6 w-6" />
              <span className="text-lg font-medium">Home</span>
            </NavLink>
          </motion.div>

          <motion.div variants={buttonVariants} initial="hidden" animate="visible">
            <NavLink
              to="/debate"
              className={({ isActive }) =>
                `flex items-center space-x-2 p-2 rounded-lg ${
                  isActive ? "bg-gray-200 text-blue-600" : "text-black hover:bg-gray-100"
                } transition-colors duration-200`
              }
            >
              <ChatAlt2Icon className="h-6 w-6" />
              <span className="text-lg font-medium">Debates</span>
            </NavLink>
          </motion.div>

          <motion.div variants={buttonVariants} initial="hidden" animate="visible">
            <NavLink
              to="/blog"
              className={({ isActive }) =>
                `flex items-center space-x-2 p-2 rounded-lg ${
                  isActive ? "bg-gray-200 text-blue-600" : "text-black hover:bg-gray-100"
                } transition-colors duration-200`
              }
            >
              <PencilIcon className="h-6 w-6" />
              <span className="text-lg font-medium">Blogs</span>
            </NavLink>
          </motion.div>

          <motion.div variants={buttonVariants} initial="hidden" animate="visible">
            <NavLink
              to="/discussion"
              className={({ isActive }) =>
                `flex items-center space-x-2 p-2 rounded-lg ${
                  isActive ? "bg-gray-200 text-blue-600" : "text-black hover:bg-gray-100"
                } transition-colors duration-200`
              }
            >
              <ChatIcon className="h-6 w-6" />
              <span className="text-lg font-medium">Discussion</span>
            </NavLink>
          </motion.div>

          {isAuthenticated && (
            <motion.div variants={buttonVariants} initial="hidden" animate="visible">
              <NavLink
                to="/bookmarks"
                className={({ isActive }) =>
                  `flex items-center space-x-2 p-2 rounded-lg ${
                    isActive ? "bg-gray-200 text-blue-600" : "text-black hover:bg-gray-100"
                  } transition-colors duration-200`
                }
              >
                <BookmarkIcon className="h-6 w-6" />
                <span className="text-lg font-medium">Bookmarks</span>
              </NavLink>
            </motion.div>
          )}

          {isAuthenticated && (
            <motion.div variants={buttonVariants} initial="hidden" animate="visible">
              <div className="relative" ref={notificationRef}>
                <motion.button
                  onClick={toggleNotifications}
                  className="flex items-center space-x-2 p-2 rounded-lg text-black hover:bg-gray-100 transition-colors duration-200 w-full"
                  animate={unreadCount > 0 && !hasVibrated.current ? { x: [0, 5, -5, 0] } : {}}
                  transition={unreadCount > 0 && !hasVibrated.current ? { duration: 0.5, ease: "easeInOut" } : {}}
                >
                  <BellIcon className="h-6 w-6" />
                  <span className="text-lg font-medium">Notifications</span>
                  {unreadCount > 0 && (
                    <span className="ml-2 bg-red-500 text-white text-xs rounded-full px-2 py-1">
                      {unreadCount}
                    </span>
                  )}
                </motion.button>
                {isNotificationOpen && (
                  <motion.div
                    initial={{ opacity: 0, x: -10 }}
                    animate={{ opacity: 1, x: 0 }}
                    exit={{ opacity: 0, x: -10 }}
                    transition={{ duration: 0.2 }}
                    className="absolute left-20 bottom-[-25vh] transform -translate-y-1/2 ml-2 w-lvw bg-white rounded-lg shadow-lg max-h-[80vh] overflow-y-auto z-50"
                  >
                    {notifications.length > 0 ? (
                      groupOrder.map((group) =>
                        groupedNotifications[group] ? (
                          <div key={group}>
                            <h3 className="px-4 py-2 text-sm font-semibold text-gray-600 sticky top-0 bg-white">
                              {group}
                            </h3>
                            {groupedNotifications[group].map((notif) => (
                              <div
                                key={notif._id}
                                className={`px-4 py-3 border-b border-gray-200 ${
                                  !notif.read ? "bg-gray-50" : ""
                                } hover:bg-gray-100`}
                              >
                                <p className="text-sm text-gray-800">{notif.message}</p>
                                <p className="text-xs text-gray-500">
                                  {new Date(notif.createdAt).toLocaleString()}
                                </p>
                              </div>
                            ))}
                          </div>
                        ) : null
                      )
                    ) : (
                      <p className="px-4 py-3 text-gray-500 text-sm">No notifications yet.</p>
                    )}
                  </motion.div>
                )}
              </div>
            </motion.div>
          )}

          {isAuthenticated && (
            <motion.div variants={buttonVariants} initial="hidden" animate="visible">
              <NavLink
                to={`/profile/${username}`}
                className={({ isActive }) =>
                  `flex items-center space-x-2 p-2 rounded-lg ${
                    isActive ? "bg-gray-200 text-blue-600" : "text-black hover:bg-gray-100"
                  } transition-colors duration-200`
                }
              >
                <UserIcon className="h-6 w-6" />
                <span className="text-lg font-medium">Profile</span>
              </NavLink>
            </motion.div>
          )}
        </div>
        {/* Logout button remains at the bottom when authenticated */}
        {isAuthenticated && (
          <div className="p-4">
            <motion.button
              whileHover={{ scale: 1.05 }}
              onClick={handleLogout}
              className="w-full text-left p-2 rounded-lg text-black hover:bg-gray-200 transition-colors duration-200"
              variants={buttonVariants}
              initial="hidden"
              animate="visible"
            >
              Logout
            </motion.button>
          </div>
        )}
      </nav>
    </>
  );
};

export default Navbar;
