import React, { useState } from "react";
import axios from "axios";

const CreateBlog = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [files, setFiles] = useState([]);
  const [isPrivate, setIsPrivate] = useState(false);
  const [passcode, setPasscode] = useState("");

  const handleFileChange = (e) => {
    setFiles([...e.target.files]);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    formData.append("isPrivate", isPrivate);
    if (isPrivate) formData.append("passcode", passcode);
    files.forEach((file) => formData.append("files", file));

    try {
      const res = await axios.post("/api/blogs", formData, {
        headers: { "Content-Type": "multipart/form-data" },
        withCredentials: true, // Send cookie for auth
      });
      console.log("Blog created:", res.data);
      // Redirect or show success
    } catch (err) {
      console.error("Error creating blog:", err.response?.data?.message);
    }
  };

  return (
    <div className="max-w-3xl mx-auto p-6 bg-white rounded-2xl shadow-lg mt-10">
      <h2 className="text-3xl font-semibold mb-6 text-center">Create a New Blog</h2>
      <form onSubmit={handleSubmit} className="space-y-5">
        <div>
          <label className="block text-gray-700 font-medium mb-2">Title</label>
          <input
            type="text"
            className="w-full border border-gray-300 rounded-xl p-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
        </div>

        <div>
          <label className="block text-gray-700 font-medium mb-2">Content</label>
          <textarea
            rows="6"
            className="w-full border border-gray-300 rounded-xl p-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            required
          ></textarea>
        </div>

        <div>
          <label className="block text-gray-700 font-medium mb-2">Upload Files (Images, Videos, Audio, PDFs)</label>
          <input
            type="file"
            accept=".jpeg,.jpg,.png,.mp4,.webm,.mp3,.wav,.ogg,.pdf"
            multiple
            className="w-full p-2 border border-gray-300 rounded-xl"
            onChange={handleFileChange}
          />
        </div>

        <div className="flex items-center space-x-4">
          <label className="text-gray-700 font-medium">Make Private?</label>
          <input
            type="checkbox"
            checked={isPrivate}
            onChange={() => setIsPrivate(!isPrivate)}
            className="h-5 w-5 text-blue-600"
          />
        </div>

        {isPrivate && (
          <div>
            <label className="block text-gray-700 font-medium mb-2">Passcode for Private Blog</label>
            <input
              type="text"
              className="w-full border border-gray-300 rounded-xl p-3 focus:outline-none focus:ring-2 focus:ring-blue-500"
              value={passcode}
              onChange={(e) => setPasscode(e.target.value)}
              required={isPrivate}
            />
          </div>
        )}

        <button
          type="submit"
          className="w-full bg-blue-600 text-white py-3 rounded-xl font-semibold hover:bg-blue-700 transition"
        >
          Publish Blog
        </button>
      </form>
    </div>
  );
};

export default CreateBlog;
