import { Link } from "react-router-dom";

const DebateCard = ({ debate }) => {
  return (
    <div
      key={debate._id}
      className="bg-white rounded-2xl shadow-md hover:shadow-lg transition-shadow duration-300"
    >
      <div className="p-4">
        <Link to={`/debate/${debate._id}`}>
          <h2 className="text-xl font-semibold mb-2 hover:underline">
            {debate.title}
          </h2>
          <p className="text-gray-700 mb-2">{debate.openingArgument}</p>
          <div className="flex justify-between text-sm text-gray-500">
            <span>Category: {debate.category}</span>
            <span>By: {debate.author?.username}</span>
          </div>
        </Link>
      </div>
    </div>
  );
};

export default DebateCard;
