// src/components/Inbox.js
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import API from "../api/axiosInstance";

const Inbox = () => {
  const [conversations, setConversations] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
      return;
    }

    const fetchInbox = async () => {
      try {
        const res = await API.get("/inbox", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setConversations(res.data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching inbox:", err);
        setLoading(false);
      }
    };

    fetchInbox();
  }, [navigate]);

  if (loading) return <div className="p-4">Loading...</div>;

  return (
    <div className="max-w-4xl mx-auto p-4 mt-16">
      <h1 className="text-2xl font-bold mb-4">Inbox</h1>
      {conversations.length === 0 ? (
        <p className="text-gray-500">No conversations yet.</p>
      ) : (
        <ul className="space-y-4">
          {conversations.map((chat) => {
            const otherUser =
              chat.sender._id === localStorage.getItem("userId")
                ? chat.receiver
                : chat.sender;
            return (
              <li
                key={chat._id}
                className="p-4 bg-white rounded-lg shadow hover:bg-gray-100 transition"
              >
                <Link
                  to={`/chat/${otherUser._id}`}
                  className="flex items-center space-x-4"
                >
                  <div className="flex-1">
                    <p className="font-semibold">{otherUser.username}</p>
                    <p className="text-gray-600 truncate">
                      {chat.text || (chat.image ? "Sent an image" : "No message")}
                    </p>
                    <p className="text-xs text-gray-500">
                      {new Date(chat.timestamp).toLocaleString()}
                    </p>
                  </div>
                  {!chat.seen && chat.receiver._id === localStorage.getItem("userId") && (
                    <span className="bg-blue-500 text-white text-xs px-2 py-1 rounded">
                      New
                    </span>
                  )}
                </Link>
              </li>
            );
          })}
        </ul>
      )}
    </div>
  );
};

export default Inbox;
