import React, { useState } from "react";
import API from "../api/axiosInstance";
import { useNavigate } from "react-router-dom"; // Added for redirect after success

const CreateBlog = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [files, setFiles] = useState([]);
  const [isPrivate, setIsPrivate] = useState(false);
  const [passcode, setPasscode] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate(); // For redirecting after success

  const handleFileChange = (e) => {
    const selectedFiles = Array.from(e.target.files);
    setFiles(selectedFiles);
    console.log("Selected files:", selectedFiles.map(f => ({ name: f.name, size: f.size })));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    const formData = new FormData();
    formData.append("title", title);
    formData.append("content", content);
    formData.append("isPrivate", isPrivate.toString()); // Ensure boolean is sent as string
    if (isPrivate) formData.append("passcode", passcode);

    // Append files with the correct field name "files"
    files.forEach((file, index) => {
      formData.append("files", file);
      console.log(`Appending file ${index + 1}: ${file.name}`);
    });

    // Debug FormData contents (note: FormData can't be directly logged in some browsers)
    for (let pair of formData.entries()) {
      console.log(`${pair[0]}:`, pair[1]);
    }

    try {
      const token = localStorage.getItem("token");
      if (!token) {
        throw new Error("Please log in to create a blog post");
      }

      const controller = new AbortController();
      const timeoutId = setTimeout(() => {
        controller.abort();
        console.log("Request timed out after 15s");
      }, 15000); // Increased to 15s for larger files

      const response = await API.post("/blogs", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
          Authorization: `Bearer ${token}`,
        },
        signal: controller.signal,
      });

      clearTimeout(timeoutId);
      console.log("Blog created successfully:", response.data);

      // Reset form and redirect
      setTitle("");
      setContent("");
      setFiles([]);
      setIsPrivate(false);
      setPasscode("");
      navigate("/blog"); // Redirect to blog list or wherever you want
    } catch (err) {
      console.error("Error in blog creation:", err);
      if (err.name === "AbortError") {
        setError("Request timed out. Try smaller files or check your connection.");
      } else if (err.response) {
        setError(err.response.data.message || "Failed to create blog post");
        console.log("Server response:", err.response.data);
      } else {
        setError(err.message || "An unexpected error occurred");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Create New Blog</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <div className="mb-4">
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Blog Title"
            className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            required
          />
        </div>
        <div className="mb-4">
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="Blog Content"
            className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
            rows="6"
            required
          />
        </div>
        <div className="mb-4">
          <input
            type="file"
            multiple
            onChange={handleFileChange}
            className="w-full p-2 border rounded file:mr-4 file:py-2 file:px-4 file:rounded file:border-0 file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
            accept="image/*,video/*,audio/*,.pdf" // Restrict file types
          />
          {files.length > 0 && (
            <div className="mt-2 text-sm text-gray-600">
              Selected files: {files.map(f => f.name).join(", ")}
            </div>
          )}
        </div>
        <div className="mb-4">
          <label className="flex items-center">
            <input
              type="checkbox"
              checked={isPrivate}
              onChange={(e) => setIsPrivate(e.target.checked)}
              className="mr-2 rounded border-gray-300 text-blue-600 focus:ring-blue-500"
            />
            <span className="text-gray-700">Private Blog</span>
          </label>
        </div>
        {isPrivate && (
          <div className="mb-4">
            <input
              type="text"
              value={passcode}
              onChange={(e) => setPasscode(e.target.value)}
              placeholder="Passcode"
              className="w-full p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
              required={isPrivate}
            />
          </div>
        )}
        {error && (
          <div className="mb-4 p-3 bg-red-100 text-red-700 rounded border border-red-300">
            {error}
          </div>
        )}
        <button
          type="submit"
          disabled={loading}
          className={`w-full p-2 rounded font-semibold text-white transition-colors ${
            loading ? "bg-gray-400 cursor-not-allowed" : "bg-blue-600 hover:bg-blue-700"
          }`}
        >
          {loading ? "Posting..." : "Create Blog"}
        </button>
      </form>
    </div>
  );
};

export default CreateBlog;
