import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";
import API from "../api/axiosInstance";
import { CheckCircleIcon } from "@heroicons/react/outline";

const CreateBlog = () => {
  const [form, setForm] = useState({ title: "", content: "" });
  const [file, setFile] = useState(null); // Ensure setFile is defined here
  const [isPrivate, setIsPrivate] = useState(false);
  const [passcode, setPasscode] = useState("");
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile) {
      if (selectedFile.size > 2 * 1024 * 1024) { // 2MB limit to match backend
        setError("File size must be less than 2MB.");
        setFile(null);
      } else {
        setError(null);
        setFile(selectedFile);
      }
    } else {
      setFile(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const token = localStorage.getItem("token");
      if (!token) throw new Error("You must be logged in to create a blog.");
      if (!form.title.trim() || !form.content.trim()) throw new Error("Title and content are required.");
      if (isPrivate && !passcode.trim()) throw new Error("Passcode is required for private blogs.");

      let config = { headers: { Authorization: `Bearer ${token}` }, timeout: 60000 };
      let data;

      if (file) {
        const formData = new FormData();
        formData.append("title", form.title);
        formData.append("content", form.content);
        formData.append("isPrivate", isPrivate.toString());
        formData.append("passcode", isPrivate ? passcode : "");
        formData.append("file", file);
        console.log("FormData entries:", [...formData]);
        data = formData;
      } else {
        const jsonData = {
          title: form.title,
          content: form.content,
          isPrivate: isPrivate,
          passcode: isPrivate ? passcode : "",
        };
        console.log("JSON data:", jsonData);
        data = jsonData;
        config.headers["Content-Type"] = "application/json";
      }

      const response = await API.post("/blogs", data, config);
      console.log("Blog created:", response.data);
      setSuccess(true);
      setTimeout(() => navigate("/blog"), 1500);
    } catch (error) {
      console.error("Error:", error.response?.data || error.message);
      setError(error.response?.data?.message || error.message || "Failed to create blog.");
      setLoading(false);
      setFile(null); // This should work now since setFile is defined
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 via-blue-100 to-green-100 flex items-center justify-center p-4">
      <div className="ml-64 w-full flex justify-center">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, ease: "easeOut" }}
          className="bg-white rounded-2xl shadow-2xl p-6 w-full max-w-md transform transition-all duration-300 hover:shadow-3xl"
        >
          <motion.div initial={{ scale: 0 }} animate={{ scale: 1 }} transition={{ duration: 0.5 }} className="text-center">
            <h2 className="text-3xl font-extrabold text-gray-800 mb-6">
              Write a <span className="text-yellow-400">Blog</span>
            </h2>
          </motion.div>

          {success ? (
            <motion.div
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5 }}
              className="text-center"
            >
              <CheckCircleIcon className="h-20 w-20 text-green-500 mx-auto mb-4 animate-pulse" />
              <p className="text-xl font-semibold text-green-600">Blog Posted Successfully!</p>
              <p className="text-gray-500 mt-2">Redirecting to Blog Hub...</p>
            </motion.div>
          ) : (
            <form onSubmit={handleSubmit} className="space-y-6">
              {error && <div className="text-red-500 text-sm text-center">{error}</div>}

              <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: 0.1, duration: 0.5 }}>
                <label htmlFor="title" className="block text-sm font-medium text-gray-700 mb-1">
                  Title
                </label>
                <input
                  type="text"
                  id="title"
                  name="title"
                  placeholder="Enter blog title"
                  value={form.title}
                  onChange={handleChange}
                  className="w-full px-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all duration-300"
                  required
                />
              </motion.div>

              <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: 0.2, duration: 0.5 }}>
                <label htmlFor="content" className="block text-sm font-medium text-gray-700 mb-1">
                  Content
                </label>
                <textarea
                  id="content"
                  name="content"
                  placeholder="Write your blog content..."
                  value={form.content}
                  onChange={handleChange}
                  className="w-full px-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all duration-300 h-32 resize-none"
                  required
                />
              </motion.div>

              <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: 0.3, duration: 0.5 }}>
                <label htmlFor="file" className="block text-sm font-medium text-gray-700 mb-1">
                  Attach File (Optional - Image, Video, Audio, PDF - Max 2MB)
                </label>
                <input
                  type="file"
                  id="file"
                  accept=".jpeg,.jpg,.png,.mp4,.webm,.mp3,.wav,.ogg,.pdf"
                  onChange={handleFileChange}
                  className="w-full px-4 py-2 border border-gray-300 rounded-xl text-gray-700 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                />
              </motion.div>

              <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: 0.4, duration: 0.5 }}>
                <label className="flex items-center space-x-2 text-sm font-medium text-gray-700">
                  <input
                    type="checkbox"
                    checked={isPrivate}
                    onChange={(e) => setIsPrivate(e.target.checked)}
                    className="h-5 w-5 text-yellow-400 border-gray-300 rounded"
                  />
                  <span>Keep this blog private</span>
                </label>
              </motion.div>

              {isPrivate && (
                <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: 0.5, duration: 0.5 }}>
                  <label htmlFor="passcode" className="block text-sm font-medium text-gray-700 mb-1">
                    Set a Passcode
                  </label>
                  <input
                    type="password"
                    id="passcode"
                    name="passcode"
                    placeholder="Enter a passcode"
                    value={passcode}
                    onChange={(e) => setPasscode(e.target.value)}
                    className="w-full px-4 py-2 border border-gray-300 rounded-xl focus:ring-2 focus:ring-yellow-400 focus:border-transparent transition-all duration-300"
                    required={isPrivate}
                  />
                </motion.div>
              )}

              <motion.button
                type="submit"
                disabled={loading}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                transition={{ duration: 0.3 }}
                className={`w-full bg-gradient-to-r from-blue-500 to-green-600 text-white p-2 rounded-xl shadow-md hover:from-blue-600 hover:to-green-700 transition-all duration-300 ${
                  loading ? "opacity-70 cursor-not-allowed" : ""
                }`}
              >
                {loading ? (
                  <span className="flex items-center justify-center">
                    <svg
                      className="animate-spin h-5 w-5 mr-2 text-white"
                      xmlns="http://www.w3.org/2000/svg"
                      fill="none"
                      viewBox="0 0 24 24"
                    >
                      <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                      <path
                        className="opacity-75"
                        fill="currentColor"
                        d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                      ></path>
                    </svg>
                    Posting...
                  </span>
                ) : (
                  "Post Blog"
                )}
              </motion.button>
            </form>
          )}
        </motion.div>
      </div>
    </div>
  );
};

export default CreateBlog;
